# define GET_INCOMING \
ivec2 getIncoming(ivec2 coords, out vec4 particle){\
    vec4 pU, pUR, pUL, pR, pL, pDR, pDL, pD;\
    if(exist(pU = gp(coords + ivec2(0, 1))) &&\
    		particleCellDir(pU) == D){\
        particle = pU;\
        return coords + ivec2(0, 1);\
    }else if(exist(pUR = gp(coords + ivec2(1, 1))) &&\
        	particleCellDir(pUR) == DL){\
        particle = pUR;\
        return coords + ivec2(1, 1);\
    }else if(exist(pUL = gp(coords + ivec2(-1, 1))) &&\
        	particleCellDir(pUL) == DR){\
        particle = pUL;\
        return coords + ivec2(-1, 1);\
    }else if(exist(pR = gp(coords + ivec2(1, 0))) &&\
