        return coords + ivec2(-1, -1);\
    }else if(exist(pD = gp(coords + ivec2(0, -1))) &&\
        	particleCellDir(pD) == U){\
        particle = pD;\
        return coords + ivec2(0, -1);\
    } else {\
        particle = vec4(0,0,0,0);\
        return ivec2(-1);\
    }\
}
# define MOVE_PARTICLE \
vec4 moveParticle(vec4 p, ivec2 myCoords, out ivec2 targetCoordsOut){\
	\
    vec4 pNextState = p;\
    \
    /* Simulate movement.*/\
    /* Determine this cell's state*/\
    if(exist(p)){\
    	/* This cell's particle exist.*/\
        /* Check if particle tries to move out of cell.*/\
        vec4 targetP; /* Target particle.*/\
        int thisDir = particleCellDir(p);\
        ivec2 targetCoords = myCoords;\
        if(thisDir == U){\
        	targetCoords += ivec2(0,1);\
        } else if(thisDir == D){\
        	targetCoords += ivec2(0,-1);\
        } else if(thisDir == R){\
        	targetCoords += ivec2(1,0);\
        } else if(thisDir == L){\
        	targetCoords += ivec2(-1,0);\
        } else if(thisDir == UR){\
        	targetCoords += ivec2(1,1);\
        } else if(thisDir == UL){\
        	targetCoords += ivec2(-1,1);\
        } else if(thisDir == DR){\
        	targetCoords += ivec2(1,-1);\
        } else if(thisDir == DL){\
        	targetCoords += ivec2(-1,-1);\
        }\
        if(\
            !exist(gp(targetCoords)) &&\
            myCoords == getIncoming(/*ivec2 coords=*/targetCoords, /*out vec4 particle=*/targetP) &&\
            !((thisDir == D || thisDir == DR || thisDir == DL) && myCoords.y == 1) &&\
            !((thisDir == L || thisDir == UL || thisDir == DL) && myCoords.x == 0) &&\
            !((thisDir == R || thisDir == UR || thisDir == DR) && myCoords.x == int(iResolution.x)-1) &&\
            !((thisDir == U || thisDir == UR || thisDir == UL) && myCoords.y == int(iResolution.y)-1)\
        ){\
            /* This particle is going to the target.*/\
        	destroy(pNextState);\
        	targetCoordsOut = targetCoords;\
        } else {\
            /* Stay the same.*/\
            /* Move by velocity.*/\
            vec2 pos = getPos(pNextState);\
            pos += limSize(pNextState.xy);\
            pos = clamp(pos, 0., 1.-1e-5);\
            pNextState.zw = floor(pNextState.zw) + pos;\
            \
            targetCoordsOut = myCoords;\
        }\
    } else {\
    	/* This cell's particle does not exist.*/\
        /* Look for particle moving into cell.*/\
        ivec2 incomingCoords = getIncoming(/*ivec2 coords=*/myCoords, /*out vec4 particle=*/pNextState);\
        if(incomingCoords.x != -1){\
        	vec2 pos = getPos(pNextState);\
            pos += limSize(pNextState.xy);\
            pos = mod(pos.xy, 1.-1e-3);\
            pNextState.zw = floor(pNextState.zw) + pos;\
            \
            targetCoordsOut = incomingCoords;\
        } else {\
        	targetCoordsOut = myCoords;\
        }\
    }\
    return pNextState;\
}
