            if(doAutoSpawn && myCoords == ivec2(iResolution.x*0.5, iResolution.y*0.95) && hash(vec2(myCoords.x, myCoords.y) + vec2(iTime*0.04)) < 6.e-3){\
            	setClass(pNextState, AIRCRAFT);\
            }\
        }\
    }\
    /* Fire bomb from tank. */\
    ivec2 tankPosOffset = ivec2(0, -4);\
    vec4 tank = texelFetch(iChannel0, myCoords + tankPosOffset, 0);\
    if(\
        iteration == 0 && getClass(tank) == TANK &&\
        tankPos == myCoords + tankPosOffset &&\
        texelFetch(iChannel1, ivec2(KEY_SPACE, 0), 0).x > 0.5 /*&&*/\
        /*ivec2(texelFetch(iChannel0, ivec2(TIME, 0), 0)).x % 8 == 0*/\
    ){\
        setClass(pNextState, BOMB);\
        float tankShootAngle = texelFetch(iChannel0, ivec2(TANK_SHOOT_ANGLE, 0), 0).x;\
        vec2 aimDir = vec2(\
            cos(tankShootAngle),\
            sin(tankShootAngle)\
        );\
        pNextState.xy = tank.xy + aimDir*15.;\
    }\
    \
    /* Fire put out by itself. */\
    if(pClass == FIRE && hash(vec2(myCoords) + vec2(iTime*0.04)) < 0.01){\
 		destroy(pNextState);\
    }\
    /* Explosion put out by itself. */\
    if(pClass == EXPLOSION && hash(vec2(myCoords) + vec2(iTime*0.04)) < 0.02){\
 		setClass(pNextState, FIRE);\
    }\
    /* Condensate water. */\
    if(pClass == WATER_VAPOR && hash(vec2(myCoords) + vec2(iTime*0.04)) < 0.0001){\
        setClass(pNextState, WATER);\
    }\
    /* Water force. */\
    const float waterForce = 0.1 * SPEED;\
    vec4 pD = gp(myCoords + ivec2(0, -1));\
    if(length(pNextState.xy) < 1e1){\
        if(pClass == WATER && exist(pD) && getClass(pD) == WATER){\
            vec4 left = targets[3];\
            vec4 right = targets[4];\
            if(exist(right) && getClass(right) == WATER){\
                if(exist(left) && getClass(left) == WATER){\
                    vec4 leftLeft = gp(myCoords + ivec2(-2,0));\
                    vec4 rightRight = gp(myCoords + ivec2(2,0));\
                    if(exist(rightRight) && getClass(rightRight) == WATER){\
                        if(exist(leftLeft) && getClass(leftLeft) == WATER){\
                            /* Do nothing.*/\
                        }else{\
                            pNextState.x -= waterForce;\
                        }\
                    }else if(exist(leftLeft) && getClass(leftLeft) == WATER){\
                        pNextState.x += waterForce;\
                    }else{\
                        /* Do nothing.*/\
                    }\
                }else{\
                    pNextState.x -= waterForce;\
                }\
            }else if(exist(left) && getClass(left) == WATER){\
                pNextState.x += waterForce;\
            } else{\
                /* Do nothing.*/\
            }\
        }\
    }\
    /*Spawn grass.*/\
    vec4 pU = gp(myCoords + ivec2(0, 1));\
    int pUClass = getClass(pU);\
    vec4 pUU = gp(myCoords + ivec2(0, 2));\
    int pUUClass = getClass(pUU);\
    if(exist(p) && getClass(p) == WATER && !exist(pU) && exist(pD) && (getClass(pD) == SAND || getClass(pD) == DIRT) && hash(vec2(myCoords) + vec2(iTime*0.04)) < 0.5){\
        setClass(pNextState, GRASS);\
    }\
    /*Remove grass.*/\
    vec4 pUUU;\
    int pUUUClass;\
    if(exist(p) && getClass(p) == GRASS &&\
        exist(pU) && (pUClass == SAND || pUClass == WATER) &&\
        exist(pUU) && (pUUClass == SAND || pUUClass == WATER) &&\
        exist((pUUU = gp(myCoords + ivec2(0,3)))) &&\
            ((pUUUClass = getClass(pUUU)) == SAND || pUUUClass == WATER) &&\
        hash(vec2(myCoords.x+1, myCoords.y) + vec2(iTime*0.04)) < 0.01){\
        setClass(pNextState, WATER_VAPOR);\
    }\
    /* Evaporate water by itself. */\
    if(pClass == WATER && pUClass != WATER && pUUClass != WATER && hash(vec2(myCoords.x+1, myCoords.y) + vec2(iTime*0.04)) < 0.0001){\
        setClass(pNextState, WATER_VAPOR);\
    }\
    /* Control enemy aircraft. */\
    if(pClass == AIRCRAFT){\
        /* Accelerate forward. */\
        vec2 dir = vec2(0,1);\
        float l = length(pNextState.xy);\
        if(l > 0.) dir = pNextState.xy/l;\
        pNextState.xy += dir*0.1;\
        /* Slow down to speed of 0.5. */\
        pNextState.xy /= max(l*2., 1.);\
        /*ivec2 toTank = tankPos - myCoords;*/\
        vec2 sideways = vec2(-dir.y, dir.x);\
        ivec2 frontLeft = ivec2(vec2(myCoords) + dir*100. + sideways*30.);\
        ivec2 frontRight = ivec2(vec2(myCoords) + dir*100. - sideways*30.);\
        if(\
            frontLeft.x > int(iResolution.x) || frontLeft.x < 0 || frontLeft.y > int(iResolution.y) || frontLeft.y < 0 ||\
            getClass(gp(frontLeft)) != EMPTY\
        ){\
            pNextState.xy -= sideways*0.05;\
        }else if(\
            frontRight.x > int(iResolution.x) || frontRight.x < 0 || frontRight.y > int(iResolution.y) || frontRight.y < 0 ||\
            getClass(gp(frontRight)) != EMPTY\
        ){\
            pNextState.xy += sideways*0.05;\
        }else if(tankPos.x != -1){\
            vec2 tankDir = vec2(tankPos) - vec2(myCoords);\
            if(dot(tankDir, sideways) > 0.){\
            	pNextState.xy += sideways*0.002;\
            } else {\
            	pNextState.xy -= sideways*0.002;\
            }\
        }\
    }\
    /* Fire lazer/mega bomb from aircraft. */\
    ivec2 aircraftPosOffset = ivec2(0, 4);\
    vec4 aircraft = texelFetch(iChannel0, myCoords + aircraftPosOffset, 0);\
    if(\
        tankPos.x != -1 && iteration == 0 && getClass(aircraft) == AIRCRAFT && hash(vec2(myCoords) + vec2(iTime*0.04)) < 0.0025 &&\
        dot(normalize(vec2(tankPos) - vec2(myCoords+aircraftPosOffset)), normalize(aircraft.xy)) > 0.9\
    ){\
        if(hash(vec2(myCoords+1) + vec2(iTime*0.04)) < 0.1){\
        	setClass(pNextState, MEGA_BOMB);\
            pNextState.xy = aircraft.xy*64.;\
        }else{\
        	setClass(pNextState, LAZER);\
        	pNextState.xy = aircraft.xy;\
        }\
    }\
    /* Lazer. */\
    if(pClass == LAZER){\
       	float l = length(pNextState.xy);\
        if(l == 0.){\
        	pNextState.xy = normalize(vec2(-1,-1));\
            l = 1.;\
        }\
    	pNextState.xy = pNextState.xy/l*10.;\
        if(hash(vec2(myCoords) + vec2(iTime*0.04)) < 0.001){\
        	destroy(pNextState);\
        }\
    }\
    /* Particle-ground.*/\
    if(myCoords.y == 1 && pNextState.y < 0.){\
    	pNextState.y = -0.5*pNextState.y;\
    	pNextState.x *= 0.99;\
    }\
    /* Particle-ceiling.*/\
    if(myCoords.y == int(iResolution.y)-1 && pNextState.y > 0.){\
    	pNextState.y = -0.5*pNextState.y;\
    	pNextState.x *= 0.99;\
    }\
    /* Particle-left wall.*/\
    if(myCoords.x == 0 && pNextState.x < 0.){\
    	pNextState.x = -0.5*pNextState.x;\
    	pNextState.y *= 0.99;\
    }\
    /* Particle-right wall.*/\
    if(myCoords.x == int(iResolution.x)-1 && pNextState.x > 0.){\
    	pNextState.x = -0.5*pNextState.x;\
    	pNextState.y *= 0.99;\
    }\
    \
    /* Make sticky.*/\
	/*vec2 myNextPos = getPos(pNextState);*/\
    /*myNextPos = mix(myNextPos, vec2(0.5,0.5), 0.1);*/\
    /*pNextState.zw = floor(pNextState.zw) + myNextPos;*/\
    \
    /*Concrete force*/\
    bool isSupported = false;\
    /*Break concrete*/\
    if(exist(p) && (pClass == CONCRETE || pClass == CONCRETE_BROKEN)){\
        vec4 below   = gp(myCoords + ivec2( 0,-1));\
        int belowClass = getClass(below);\
        \
        vec4 belowR  = gp(myCoords + ivec2( 1,-1));\
        int belowRClass = getClass(belowR);\
        \
        vec4 belowL  = gp(myCoords + ivec2(-1,-1));\
        int belowLClass = getClass(belowL);\
        \
        vec4 right   = gp(myCoords + ivec2( 1, 0));\
        int rightClass = getClass(right);\
        \
        vec4 left    = gp(myCoords + ivec2(-1, 0));\
        int leftClass = getClass(left);\
        \
        vec4 rightRight   = gp(myCoords + ivec2( 2, 0));\
        int rightRightClass = getClass(rightRight);\
        \
        vec4 leftLeft    = gp(myCoords + ivec2(-2, 0));\
        int leftLeftClass = getClass(leftLeft);\
        \
        vec4 rightRightD = gp(myCoords + ivec2( 2,-1));\
        int rightRightDClass = getClass(rightRightD);\
        \
        vec4 leftLeftD = gp(myCoords + ivec2(-2,-1));\
        int leftLeftDClass = getClass(leftLeftD);\
        \
        vec4 rightRightRight   = gp(myCoords + ivec2( 3, 0));\
        int rightRightRightClass = getClass(rightRightRight);\
        \
        vec4 leftLeftLeft    = gp(myCoords + ivec2(-3, 0));\
        int leftLeftLeftClass = getClass(leftLeftLeft);\
        \
        vec4 rightRightRightD = gp(myCoords + ivec2( 3,-1));\
        int rightRightRightDClass = getClass(rightRightRightD);\
        \
        vec4 leftLeftLeftD = gp(myCoords + ivec2(-3,-1));\
        int leftLeftLeftDClass = getClass(leftLeftLeftD);\
        if(\
            myCoords.y <= 1 ||\
            belowClass==CONCRETE || belowClass==SAND || belowClass==DIRT ||\
            exist(right) && (belowRClass==CONCRETE || belowRClass==SAND || belowRClass==DIRT) ||\
            exist(left) && (belowLClass==CONCRETE || belowLClass==SAND || belowLClass==DIRT) ||\
            exist(right) && exist(rightRight) && (rightRightDClass==CONCRETE || rightRightDClass==SAND || rightRightDClass==DIRT) ||\
            exist(left) && exist(leftLeft) && (leftLeftDClass==CONCRETE || leftLeftDClass==SAND || leftLeftDClass==DIRT) ||\
            exist(right) && exist(rightRight) && exist(rightRightRight) && (rightRightRightDClass==CONCRETE || rightRightRightDClass==SAND || rightRightRightDClass==DIRT) ||\
            exist(left) && exist(leftLeft) && exist(leftLeftLeft) && (leftLeftLeftDClass==CONCRETE || leftLeftLeftDClass==SAND || leftLeftLeftDClass==DIRT)\
        ){\
            isSupported = true;\
            if(pClass == CONCRETE){\
				pNextState.x *= 0.8;\
                pNextState.y *= 0.8;\
                vec2 pos = getPos(pNextState);\
                pNextState = setPos(pNextState, mix(pos, vec2(0.5,0.5), 0.1));\
            }\
        }\
    }\
    if(exist(p) && pClass == CONCRETE && !isSupported){\
        setClass(pNextState, CONCRETE_BROKEN);\
    }\
    if(exist(p) && pClass == CONCRETE_BROKEN && isSupported && hash(vec2(myCoords.x, myCoords.y) + vec2(iTime*0.04)) < 0.005){\
        setClass(pNextState, CONCRETE);\
    }\
    /**/\
    float drag = 0.995;\
