    }\
    \
    /* Drag.*/\
    pNextState.xy *= pow(drag, SPEED);\
    \
    return pNextState;\
}
# define SWAP_ABOVE_AND_CALC_IMPULSES \
vec4 swapAboveAndCalcImpulses(ivec2 myCoords, int iteration){\
    vec4 p = gp(myCoords);\
    ivec2 offset=ivec2(0,0);\
	\
    if(exist(p) && getClass(p) != CONCRETE){\
        vec4 pU = gp(myCoords + ivec2(0, 1));\
        float pPriority = getPriority(p);\
        if(exist(pU) && getClass(pU) != CONCRETE){\
            float puPriority = getPriority(pU);\
            if(pPriority < puPriority){\
                vec4 pUU = gp(myCoords + ivec2(0, 2));\
                if(exist(pUU) && puPriority < getPriority(pUU) && getClass(pUU) != CONCRETE){\
                    /* Do nothing.*/\
                } else{\
                    /* Swap with above.*/\
                    offset= ivec2(0, 1);\
                }\
            }else{\
                /* Swap with below.*/\
                vec4 pD = gp(myCoords + ivec2(0, -1));\
                if(exist(pD) && getPriority(pD) < pPriority && getClass(pD) != CONCRETE){\
                    offset=  ivec2(0, -1);\
                }\
            }\
        }else{\
            /* Swap with below.*/\
            vec4 pD = gp(myCoords + ivec2(0, -1));\
            if(exist(pD) && getPriority(pD) < pPriority && getClass(pD) != CONCRETE){\
                 offset=  ivec2(0, -1);\
            } \
        }\
    }\
        /* Do nothing.*/\
    return calcImpulses(myCoords+offset, iteration);\
}
# define TRACK_TANK \
ivec2 trackTank(ivec2 tankCoords){\
	/* Track tank. */\
    vec4 pC, pU, pUR, pUL, pR, pL, pDR, pDL, pD;\
    if(exist(pC = gp(tankCoords + ivec2( 0, 0))) && getClass(pC) == TANK){\
        return tankCoords + ivec2( 0, 0);\
    }else if(exist(pU = gp(tankCoords + ivec2( 0, 1))) && getClass(pU) == TANK){\
        return tankCoords + ivec2( 0, 1);\
    }else if(exist(pUR = gp(tankCoords + ivec2( 1, 1))) && getClass(pUR) == TANK){\
        return tankCoords + ivec2( 1, 1);\
    }else if(exist(pUL = gp(tankCoords + ivec2(-1, 1))) && getClass(pUL) == TANK){\
        return tankCoords + ivec2(-1, 1);\
    }else if(exist(pR = gp(tankCoords + ivec2( 1, 0))) && getClass(pR) == TANK){\
        return tankCoords + ivec2( 1, 0);\
    }else if(exist(pL = gp(tankCoords + ivec2(-1, 0))) && getClass(pL) == TANK){\
        return tankCoords + ivec2(-1, 0);\
    }else if(exist(pDR = gp(tankCoords + ivec2( 1,-1))) && getClass(pDR) == TANK){\
        return tankCoords + ivec2( 1,-1);\
    }else if(exist(pDL = gp(tankCoords + ivec2(-1,-1))) && getClass(pDL) == TANK){\
        return tankCoords + ivec2(-1,-1);\
    }else if(exist(pD = gp(tankCoords + ivec2( 0,-1))) && getClass(pD) == TANK){\
        return tankCoords + ivec2( 0,-1);\
    } else {\
        return ivec2(-1);\
    }\
}\

bool exist(vec4 p){
	return p.z >= 1.;
}

void destroy(inout vec4 p){
	p.z = 0.;
}

vec2 getPos(vec4 p){
	return mod(p.zw, 1.);
}

vec4 setPos(vec4 p, vec2 newPos){
	return vec4(p.xy, floor(p.zw) + newPos);
}

int getClass(vec4 p){
	return int(p.z)-1;
}

void setClass(inout vec4 p, int cl){
	p.z = mod(p.z, 1.) + float(cl+1);
}

