vec2 limSize(vec2 v){
	float l = dot(v,v);
    if(l > 1.){
    	v /= sqrt(l);
    }
    return v;
}

# define C 0
# define U 1
# define D 2
# define R 3
# define L 4
# define UR 5
# define UL 6
# define DR 7
# define DL 8
// Given the velocity of the particle, what other cell is it trying to reach?
int particleCellDir(vec4 p){
    vec2 pos = getPos(p);
    vec2 nextPos = pos + limSize(p.xy);
    if(nextPos.x >= 1.){
    	// Right.
        if(nextPos.y >= 1.){
        	// Up.
            return UR;
        } else if(nextPos.y < 0.) {
        	// Down.
            return DR;
        } else {
        	// Vertical center.
            return R;
        }
    } else if(nextPos.x < 0.){
    	// Left.
        if(nextPos.y >= 1.){
        	// Up.
            return UL;
        } else if(nextPos.y < 0.) {
        	// Down.
            return DL;
        } else {
        	// Vertical center.
            return L;
        }
    } else {
        // Horizontal center.
    	if(nextPos.y >= 1.){
        	// Up.
            return U;
        } else if(nextPos.y < 0.) {
        	// Down.
            /*if(getClass(p) == WATER){
                if(p.x > 0.){
                	return DR;
                }else{
                	return DL;
                }
            }*/
            return D;
        } else {
        	// Vertical center.
            if(getClass(p) == WATER){
                if(p.x > 0.){
                	return R;
                }else{
                	return L;
                }
            }
            return C;
        }
    }
}

// https://www.shadertoy.com/view/4dS3Wd
float hash(vec2 p) {vec3 p3 = fract(vec3(p.xyx) * 0.13); p3 += dot(p3, p3.yzx + 3.333); return fract((p3.x + p3.y) * p3.z); }

// https://iquilezles.org/articles/distfunctions2d
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

