bool house(in vec2 fragCoord, in vec2 pos){
    float d = length(fragCoord.xy - 0.5 - pos);
    ivec2 diff = ivec2(fragCoord.xy - pos);
    float rr = 3.;
    if(
    diff.x < 30 && diff.x > -30 && diff.y < 10 && diff.y > 0 ||
    sdSegment( vec2(diff), vec2(-35, -7), vec2(1, 5)) < rr+1. ||
    sdSegment( vec2(diff), vec2(35, -7), vec2(-1, 5)) < rr+1. ||
    diff.x <= -30 && diff.x > -39 && diff.y < 79 && diff.y > -int(rr)-2 ||
    diff.x >= 30 && diff.x < 39 && diff.y < 79 && diff.y > -int(rr)-2 ||
    sdSegment( vec2(diff), vec2(-36, 36), vec2(0, 48)) < rr ||
    sdSegment( vec2(diff), vec2(36, 36), vec2(0, 48)) < rr ||
    sdSegment( vec2(diff), vec2(-36, 76), vec2(0, 88)) < rr ||
    sdSegment( vec2(diff), vec2(36, 76), vec2(0, 88)) < rr ||
    sdSegment( vec2(diff), vec2(0, 4), vec2(0, 88)) < rr

    ){
        return true;
    }
    return false;
}

