        ivec2 targetCoords;
        vec4 pNextState = moveParticle(/*vec4 p=*/p, /*ivec2 myCoords=*/ivec2(fragCoord), /*out ivec2 targetCoords*/targetCoords);

        // Input.
        bool keyJ = texelFetch(iChannel1, ivec2(KEY_J, 0), 0).x > 0.5;
        bool keyK = texelFetch(iChannel1, ivec2(KEY_K, 0), 0).x > 0.5;
        float hBoost = 300.*(float(keyK)-float(keyJ));
        float vBoost = 100.*(-float(keyK)-float(keyJ));
