vec2 R;

vec4 t(vec2 p){
    return texture(iChannel1, p);   
}
float height(vec2 p){
    return t(p).x;
}
vec3 Bumpyyyyyyyyyyyyyyyyyyyyyyyyyyyyy(vec2 uv, float strength){
    float h = 0.002;
    vec3 p = t(uv).xyz; 
    float dx = height(uv) - height(uv + vec2(h, .0));
    float dy = height(uv) - height(uv + vec2(0., h));
    return normalize( vec3(0., 0., -1.) + vec3(dx, dy, 0.)*strength);
}


void mainImage( out vec4 f, in vec2 u )
{
    R = iResolution.xy;
    vec2 uv = u / R;
    vec2 cuv =  vec2(u.xy - 0.5*R.xy)/R.y; 

    vec3 lp = vec3(0., 0., -1.0); 
    vec3 sp = vec3(cuv, 0.0); 
    vec3 ro = vec3(0, 0, -1.0); 
    vec3 rd = normalize(sp - ro);
    vec3 ld = lp - sp;

    float dist = max(length(ld), 0.001);
    ld /= dist;
    vec3 objCol = t(uv).xyz;

    vec3 norm = Bumpyyyyyyyyyyyyyyyyyyyyyyyyyyyyy(uv, 12.0);

    float dif = max(dot(norm, ld), 0.);  
    float spec = pow(max(dot( reflect(-ld, norm), -rd), 0.), 16.); 

    float falloff = 1.5/(1. + dist*0.15 + dist*dist*2.15);

    vec3 col = (objCol*(dif + .5) + vec3(0.5, 0.6, 0.7)*spec*1.4)*falloff;

    f = vec4(col, 0.);
}

