vec2 hash22(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

void mainImage( out vec4 f, in vec2 u ){
    R = iResolution.xy;
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;
    vec2 m = vec2(iMouse.xy / R) - 0.5;
    m.x *= R.x / R.y;
    m *= .35;
    vec2 dir = t(u).xy;

    vec2 up = t(u+vec2(.0, 1.)).xy;
    vec2 dwn = t(u+vec2(.0, -1.)).xy;
    vec2 lft = t(u+vec2(-1.0, 0.)).xy;
    vec2 rgt = t(u+vec2(1.0, 0.)).xy;

    vec2 upl = t(u+vec2(-1.0, 1.)).xy;
    vec2 upr = t(u+vec2(1.0, 1.)).xy;
    vec2 dwnl = t(u+vec2(-1.0, -1.)).xy;
    vec2 dwnr = t(u+vec2(1.0, -1.)).xy;

    vec2 sum = (up + dwn + lft + rgt + upl + upr + dwnl + dwnr);

    float div = dot(dir, (sum));
    vec3 r = normalize(cross(vec3(dir,1.0), vec3(sum, 1.0)));


    dir = normalize(sum-div*.09-r.z);
    dir += hash22(u)*.01;

    //dir += .02;

    if(iFrame < 2){
    	dir = vec2(1);
    }

    float d = length(uv - m);
    if(iMouse.z > 0.0){
        vec2 val = exp2(-d*25.)*hash22(u);
        dir += val; 
    }

    //dir=mix(dir, hash22(u), .01);

    f = vec4(dir, 0., 00.);

}

