// Based on : https://www.shadertoy.com/view/XtyGzh
// And iq's version: https://www.shadertoy.com/view/XtyGR1

#define kDepth 7
#define kBranches 3
#define kMaxDepth 2187 // branches ^ depth

//--------------------------------------------------------------------------

mat3 matRotate( float angle ) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3( c, s, 0, -s, c, 0, 0, 0, 1);
}

mat3 matTranslate( float x, float y ) {
    return mat3( 1, 0, 0, 0, 1, 0, -x, -y, 1 );
}

float sdBranch( vec2 p, float w1, float w2, float l ) {
    float h = clamp( p.y/l, 0.0, 1.0 );
	float d = length( p - vec2(0.0,l*h) );
    return d - mix( w1, w2, h );
}

//--------------------------------------------------------------------------

float map( vec2 pos ) {
    const float len = 6.2;
    const float wid = 0.25;
    float lenf = 0.55;
    const float widf = 0.4;
    float wind = iTime * 0.5;

    float d = sdBranch( pos, wid, wid*widf, len );

    int c = 0;
    for( int count=0; count < kMaxDepth; count++ ) {
        int off = kMaxDepth;
    	vec2 pt_n = pos;

        float l = len;
        float w = wid;

      	for( int i=1; i<=kDepth; i++ ) {
            l *= lenf;
            w *= widf;

            off /= kBranches; 
            int dec = c / off;
        	int path = dec - kBranches*(dec/kBranches); //  dec % kBranches

            mat3 mx;

            // Originals (references)

            //if( path == 0 ) {
		  	//	mx = matRotate(0.75 + 0.25*sin(wind-1.0)) * matTranslate( 0.0,0.4*l/lenf);
	    	//} else if( path == 1 ) {
          	//	mx = matRotate(-0.6 + 0.21*sin(wind)) * matTranslate(0.0,0.6*l/lenf);
	    	//} else {
          	//	mx = matRotate(0.23*sin(wind+1.)) * matTranslate(0.0,1.0*l/lenf);
	    	//}

            float t = sin(iTime) - 0.1;
            if( path == 0 ) {
		  		mx = matRotate(0.75 + 0.15*t*sin(wind-1.0)) * matTranslate( 0.0,0.4*l/lenf);
	    	} else if( path == 1 ) {
          		mx = matRotate(-0.6 + 0.11*t*sin(wind)) * matTranslate(0.0,0.6*l/lenf);
	    	} else {
          		mx = matRotate(0.13*t*sin(wind+1.)) * matTranslate(0.0,1.0*l/lenf);
	    	}
            pt_n = (mx * vec3(pt_n,1)).xy;

        	// bounding sphere test
            float y = length( pt_n - vec2(0.0, l) );
       		if( y-1.4*l > 0.0 ) { c += off-1; break; }

            d = min( d, sdBranch( pt_n, w, w*widf, l ) );
     	}

    	c++;
    	if( c > kMaxDepth ) break;
	}

   	return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // coordinate system
    vec2  tree_uv = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
    float px = 2.0 / iResolution.y;

    // frame in screen
    tree_uv = tree_uv * 25.0 + vec2(0.0,6.5);
    px = px * 4.0;

    // compute
    float d = map( tree_uv );

    vec2 ground_uv = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
    vec2 ground_right_top = step(vec2(0.94, 1.265),1.0-ground_uv);
    vec2 ground_left_bottom = step(vec2(-0.06, -0.272),ground_uv);
    float ground = ground_left_bottom.x * ground_left_bottom.y * ground_right_top.x * ground_right_top.y;
    d *= 1. - ground;

  	float fuzziness = 9.0;

    // shape
    vec3 col = vec3( smoothstep( 0.0, fuzziness * px, d ) );

    col = vec3(mix(1., 0.15, col.r));

    fragColor = vec4( col, 1.0 );
}

const float pi = 3.141592;

// lower right quadrant of circle
// 1^2 = (y-1)^2 + (-x)^2
//
//     |
//    /
// .--
//
float arcramp(float x)
{
    return 1. - sqrt(
        -(
            pow(-x,2.)
            - pow(1., 2.)
        )
    );
}

// upper left quadrant of circle
// 1^2 = y^2 + (x-1)^2
//
//   -``
//  /
// |
//
float rarcramp(float x)
{
    return sqrt(
        -(
            pow(x-1., 2.)
            -sqrt(1.)
        )
    );
}

// cos(x) adjusted to be periodic 0 to 1
// y = (cos(x * pi * 2) / 2) + .5
// 
// ``-       -``
//    \     /
//      -..-
//
float bathtub(float x)
{
    return (
        (
            cos(x * pi * 2.)
            / 2.
        ) 
        + .5
    );
}


// value {0...1} mapped to {-1...1}
float s_p0p1_to_n1p1(float value)
{
    return mix(-1., 1., value);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // compute uv
    float max_res_dim = max(iResolution.x, iResolution.y);
    vec2 uvsq_offset = (1.-(iResolution.xy/max_res_dim))/2.;

    vec2 uvsq = (        // special case screen-is-uv-but-square-uv-space
        gl_FragCoord.xy  // width/height
        / max_res_dim    // of max width/height
    ) + uvsq_offset;     // plus half the w/h margin
					     // is centered

    vec2 uv = (          // optionally, use regular uv
        gl_FragCoord.xy
        / iResolution.xy
    );

	vec2 uvc = vec2(0.5,0.5); // center point to draw circle arround

    // color expressions
	// shadertoy example color, to make it shinney
    vec3 feature_color = 0.5 + 0.5*cos((iTime)+uv.xyx+vec3(0,2,4));
	vec3 background_color = vec3(0.10) + (vec3(0.10) * bathtub(iTime / 7.));


    fragColor = vec4(
        mix(
            feature_color, background_color,  // shade circle, or background

        	rarcramp(rarcramp(rarcramp(  // decay gradient to a thin ring
                abs(s_p0p1_to_n1p1(  // circle gradient to two-band circle gradient
          			(distance(uvsq, uvc)*2.) // special case uv circle gradient, 0. center, 1. edge
				))
        	)))
        ),
        1.  // solid color (no alpha)
    );
}

// "Truchet Balls" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

/*
  Balls in and between channels based on hexagonal Truchet tiles 
  (mouseable, click upper-right for new run, lower-right to
  switch between distant viewpoint and panoramic view near random ball)
  In addition to gravity and friction there is a horizontal force that varies
  with time, and other forces that bias the movement and reduce dispersal.
*/

mat3 QtToRMat (vec4 q);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float Minv3 (vec3 p);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);
float Hashfv2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

const int nBall = 64;
vec3 vnObj, ltDir;
vec2 gId, cMid;
float tCur, dstFar, hgSize, cDir, trWidf, trHt;
int nFrame, idBall;
const float pi = 3.14159, sqrt3 = 1.73205;

#define VAR_ZERO min (nFrame, 0)

float SurfHt (vec2 p)
{
  vec3 w;
  vec2 s, r;
  float b;
  p -= cMid;
  r = p / hgSize;
  s = vec2 (0., - cDir);
  b = dot (s - r, s - r);
  w = vec3 (s, b);
  s = 0.5 * vec2 (sqrt3, cDir);
  b = dot (s - r, s - r);
  if (b < w.z) w = vec3 (s, b);
  s = 0.5 * vec2 (- sqrt3, cDir);
  b = dot (s - r, s - r);
  if (b < w.z) w = vec3 (s, b);
  return trHt * smoothstep (0.25 * trWidf, trWidf, abs (sqrt (w.z) - 0.5));
}

float SurfDf (vec3 p)
{
  return 0.8 * (p.y - SurfHt (p.xz));
}

void SetTrConf ()
{
  cMid = HexToPix (gId * hgSize);
  cDir = 2. * step (Hashfv2 (gId), 0.5) - 1.;
}

float SurfRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  gIdP = vec2 (-99.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetTrConf ();
    }
    d = SurfDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 SurfNf (vec3 p)
{
  const vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (SurfHt (p.xz) - vec2 (SurfHt (p.xz + e.xy), SurfHt (p.xz + e.yx)), e.x).xzy);
}

float BallHit (vec3 ro, vec3 rd)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    u = ro - p.xyz;
    rad = 0.45;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w >= 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        vnObj = (u + d * rd) / rad;
        idBall = n;
      }
    }
  }
  return dMin;
}

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 p;
  vec3 rs, u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    u = ro - p.xyz;
    rad = 0.45;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) dMin = d;
    }
  }
  return smoothstep (0., rng, dMin);
}

float BallChqr (int idBall, vec3 vnBall)
{
  vec3 u;
  vec2 a;
  u = vnBall * QtToRMat (Loadv4 (4 * idBall + 2));
  a = mod (floor (8. * vec2 (atan (u.x, u.y), asin (u.z)) / pi), 2.) - 0.5;
  return 0.7 + 0.3 * step (0., sign (a.x) * sign (a.y));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, bgCol;
  float dstObj, dstSurf, sh;
  bgCol = mix (vec3 (0.8, 0.8, 0.6), vec3 (0.7, 0.7, 1.), smoothstep (-0.1, 0.05, rd.y));
  dstObj = BallHit (ro, rd);
  dstSurf = SurfRay (ro, rd);
  if (min (dstObj, dstSurf) < dstFar) {
    if (dstObj < dstSurf) {
      ro += dstObj * rd;
      vn = vnObj;
      col4 = vec4 (HsvToRgb (vec3 (float (idBall) / float (nBall), 0.8, BallChqr (idBall, vn))), 0.2);
    } else if (dstSurf < dstFar) {
      ro += dstSurf * rd;
      col4 = mix (vec4 (0.5, 0.35, 0.25, 0.), vec4 (1., 1., 0.7, 0.), smoothstep (0.94, 0.98, ro.y / trHt));
      vn = VaryNf (2. * ro, SurfNf (ro), max (0.02, 2. - 2. * dstSurf / dstFar));
    }
    sh = 0.5 + 0.5 * BallHitSh (ro + 0.005 * ltDir, ltDir, 5.);
    col = col4.rgb * (0.1 + 0.2 * max (- dot (vn, ltDir), 0.) +
       0.8 * sh * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    col = mix (col, bgCol, smoothstep (0.7, 1., min (dstObj, dstSurf) / dstFar));

  } else col = bgCol;
  return clamp (col, 0., 1.);
}

#define AA    1

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat, mPtr;
  vec3 col, rd, ro, vd, bMid;
  vec2 canvas, uv, uvv, ut;
  float az, el, asp, zmFac, sr, s, mb, bMid2;
  int fBall;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  nFrame = iFrame;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (4 * nBall + 0);
  hgSize = stDat.x;
  trWidf = stDat.y;
  trHt = stDat.z;
  stDat = Loadv4 (4 * nBall + 1);
  mPtr.xyz = stDat.xyz;
  fBall = int (stDat.w);
  ut = vec2 (mPtr.x, abs (mPtr.y)) + 0.05 * vec2 (1. / asp, 1.) - 0.5;
  mb = min (ut.x, ut.y);
  if (mPtr.z > 0. && mb < 0.) {
    az = 2. * pi * mPtr.x;
    el = 0.5 * pi * (mPtr.y + 0.25);
    el = clamp (el, 0.02 * pi, 0.3 * pi);
  } else if (fBall < 0) {
    az -= 0.02 * pi * tCur;
    el = 0.06 * pi;
  } else {
    az = 0.;
    el = 0.;
  }
  ut = uv;
  if (fBall < 0) {
    stDat = Loadv4 (4 * nBall + 2);
    bMid = stDat.xyz;
    bMid2 = stDat.w;
    ro = bMid + min (bMid2, 80.) * vec3 (cos (el) * sin (az + vec2 (0.5 * pi, 0.)), 2. * sin (el)).xzy;
    vd = normalize (bMid - ro);
    s = sqrt (max (1. - vd.y * vd.y, 1e-6));
    vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / s, vec3 (- vd.y * vd.x, 1. - vd.y * vd.y,
       - vd.y * vd.z) / s, vd);
    zmFac = 4.;
  } else {
    ro = Loadv4 (4 * fBall + 0).xyz;
    ro.y += 1.;
    vd = Loadv4 (4 * fBall + 1).xyz;
    vd = normalize (vec3 (vd.x, 0., vd.z));
    vd.xz = Rot2D (vd.xz, - az);
    ro.xz -= 0.3 * vd.xz;
    vuMat = mat3 (vec3 (vd.z, 0., - vd.x), vec3 (0., 1., 0.), vd);
  }
  dstFar = 200.;
  ltDir = normalize (vec3 (0., 1., -1.));
  ltDir.xz = Rot2D (ltDir.xz, 0.04 * pi * tCur);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi);
    rd = (fBall < 0) ? vec3 (uvv, zmFac) : vec3 (0.5 * sin (uvv.x * pi / asp + vec2 (0., 0.5 * pi)), uvv.y).xzy;
    rd = vuMat * normalize (rd);
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (mPtr.z > 0. && min (uv.x - asp, abs (uv.y) - 1.) > -0.1)
     col = mix (col, vec3 (0., 0.3, 1.), 0.3);
  fragColor = vec4 (pow (col, vec3 (0.9)), 1.);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

