const int nBall = 64;
float todCur, nStep, dstFar, hgSize, trWidf, trHt;
int nFrame, fBall;
const float pi = 3.14159, sqrt3 = 1.73205;
const float txRow = 128.;

#define VAR_ZERO min (nFrame, 0)

float SurfHt (vec2 p)
{
  vec3 w;
  vec2 gId, cMid, s, r;
  float cDir, b;
  gId = PixToHex (p / hgSize);
  cMid = HexToPix (gId * hgSize);
  cDir = 2. * step (Hashfv2 (gId), 0.5) - 1.;
  p -= cMid;
  r = p / hgSize;
  s = vec2 (0., - cDir);
  b = dot (s - r, s - r);
  w = vec3 (s, b);
  s = 0.5 * vec2 (sqrt3, cDir);
  b = dot (s - r, s - r);
  if (b < w.z) w = vec3 (s, b);
  s = 0.5 * vec2 (- sqrt3, cDir);
  b = dot (s - r, s - r);
  if (b < w.z) w = vec3 (s, b);
  return trHt * smoothstep (0.25 * trWidf, trWidf, abs (sqrt (w.z) - 0.5));
}

vec3 SurfNf (vec3 p)
{
  const vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (SurfHt (p.xz) - vec2 (SurfHt (p.xz + e.xy), SurfHt (p.xz + e.yx)), e.x).xzy);
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  vec4 p;
  vec3 rmN, rmN1, rmN2, vmN, wmN, dr, dr1, dr2, dv, drw, am, wam;
  float fOvlap, fricN, fricT, fricS, fricSW, fDamp, fPull, fCent, grav, rSep,
     fc, ft, drv, dt, spFreq;
  fOvlap = 500.;
  fricN = 0.5;
  fricS = 0.1;
  fricSW = 0.5;
  fricT = 0.5;
  fPull = 0.8;
  fCent = 0.01;
  spFreq = 0.0002;
  fDamp = 0.4;
  grav = 6.;
  p = Loadv4 (4 * mId);
  rm = p.xyz;
  vm = Loadv4 (4 * mId + 1).xyz;
  qm = Loadv4 (4 * mId + 2);
  wm = Loadv4 (4 * mId + 3).xyz;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    rmN = p.xyz;
    dr = rm - rmN;
    rSep = length (dr);
    if (n != mId && rSep < 1.) {
      fc = fOvlap * (1. / rSep - 1.);
      vmN = Loadv4 (4 * n + 1).xyz;
      wmN = Loadv4 (4 * n + 3).xyz;
      dv = vm - vmN;
      drv = dot (dr, dv) / (rSep * rSep);
      fc = max (fc - fricN * drv, 0.);
      am += fc * dr;
      dv -= drv * dr + cross (0.5 * (wm + wmN), dr);
      ft = min (fricT, fricS * abs (fc) * rSep / max (0.001, length (dv)));
      am -= ft * dv;
      wam += (ft / rSep) * cross (dr, dv);
    }
  }
  dr.xz = 0.5 * SurfNf (rm).xz;
  dr.y = rm.y + 0.5 - SurfHt (rm.xz - dr.xz);
  rSep = length (dr);
  if (rSep < 1.) {
    fc = fOvlap * (1. / rSep - 1.);
    dv = vm;
    drv = dot (dr, dv) / (rSep * rSep);
    fc = max (fc - fricN * drv, 0.);
    am += fc * dr;
    dv -= drv * dr + cross (wm, dr);
    ft = min (fricT, fricSW * abs (fc) * rSep / max (0.001, length (dv)));
    am -= ft * dv;
    wam += (ft / rSep) * cross (dr, dv);
  }
  am.xz += fPull * sin (spFreq * 2. * pi * nStep + vec2 (0.5 * pi, 0.));
  am.z += 1.5 * fPull;
  am.x -= fCent * rm.x;
  am.y -= grav;
  am -= fDamp * vec3 (1., 5., 1.) * vm;
  wam -= 1e-4 * fDamp * wm;
  dt = (fBall < 0) ? 0.02 : 0.005;
  vm += dt * am;
  rm += dt * vm;
  wm += dt * wam / 0.1;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

