float Circle( vec2 uv , vec2 pos , float radius , float blur ) {

    float d = length( uv - pos );
    float c = smoothstep( radius , radius - blur , d );
    return c;
}

// 웃기 함수
float Smily( vec2 uv , vec2 pos , float radius ) {

    float blur = 0.01f;

    // face
    float c= Circle( uv , pos , radius , blur );

    // eye
    float eyeLeft = Circle ( uv , pos + vec2(-0.15f, 0.2f) , 0.1f , 0.005f );
    float eyeRight = Circle ( uv , pos + vec2(0.15f, 0.2f) , 0.1f , 0.005f );

    // mouse
    float mouse = Circle ( uv , pos + vec2(0.0f, 0.0f) , radius * 0.8f , blur );
    mouse -= Circle  ( uv , pos + vec2(0.0f, 0.05f) , radius * 0.8f , blur );
    mouse = clamp( 0. , 1., mouse );

    // 합성.
    c -= eyeLeft;
    c -= eyeRight;
    c -= mouse;

    return c;
}

// 밴드
float Band( float t , float start , float end ,  float blur ) {
    float c;
    c = smoothstep(  start - blur , start + blur , t ); 
    c *= smoothstep ( end + blur , end - blur , t );	// 이거 중요함. 스텝함수의 사용법 볼것
    return c;
}


// 사각형
float Rectangle( vec2 uv , vec2 center , float width , float height ) {

    float c;
    c = Band(  uv.x , center.x - width / 2.0f  , center.x + width / 2.0f , 0.001f );
    c *= Band( uv.y , center.y - height / 2.0f , center.y + height / 2.0f , 0.001f );    
    return c;
}


