// License: CC0 (https://creativecommons.org/publicdomain/zero/1.0/)

/*
How to C2 continuous bicubic interpolation:
- compute C1 continuous partial derivatives by using a specific convolution kernel (Buffer C)
- construct a bicubic patch in each 2x2 neighborhood from the values+derivatives stored at the grid vertices (Image)
- bonus: analytically compute normals during the reconstruction

bottom:
	left:  bicubically sampled grey scale texture
	right: artifact free shading based on analytically computed C1 continuous normals  
top:
	left:  abs(laplacian); useful for debugging the C2 continuity
	right: normals


Related:

- https://www.shadertoy.com/view/wlsBz2 | "C2-interpolating cubic Kernel"   (plot + background info on the kernel used to compute the derivatives)
- https://www.shadertoy.com/view/3tfBzX | "Cubic Reconstruction"            (derivation of the bicubic reconstruction scheme)
- https://www.shadertoy.com/view/wtByDt | "Single Sample Bicubic Sampling"  (approximating the reconstruction with a single sample in 2d)
- https://www.shadertoy.com/view/tdtyzj | "Single Sample Tricubic Sampling" (approximating the reconstruction with a single sample in 3d)

*/

// use this to scale the shown region
#if 1
	#define SCALE 0.125*0.5
#else
	#define SCALE 0.75
#endif


// CUBIC AND BICUBIC RECONSTRUCTION KERNELS ============================================================================================

float kern_v(float x) { return 1.0-x*x*(3.0-2.0*abs(x)); }
float kern_d(float x) { float o = abs(x)-1.0; return x*(o*o); }

float kern_vD1(float x) { return x*(abs(x)*6.0-6.0); }
float kern_dD1(float x) { return (abs(x)-1.0)*(abs(x)*3.0-1.0); }

float kern_vD2(float x) { return abs(x) * 12.0 - 6.0; }
float kern_dD2(float x) { return x * 6.0 + (x > 0.0 ? -4.0 : 4.0); }


vec4 kern(vec2 p)
{
    return vec4(kern_d(p.x) * kern_v(p.y),
                kern_v(p.x) * kern_d(p.y),
                kern_d(p.x) * kern_d(p.y),
                kern_v(p.x) * kern_v(p.y));
}

mat4 kern4x4(vec2 p)
{
    vec2 v   = vec2(kern_v  (p.x), kern_v  (p.y));
    vec2 d   = vec2(kern_d  (p.x), kern_d  (p.y));

    vec2 vD1 = vec2(kern_vD1(p.x), kern_vD1(p.y));
    vec2 dD1 = vec2(kern_dD1(p.x), kern_dD1(p.y));

    mat4 m = mat4
    (
        /*   kernDx       |  kernDy       |  kernDxy        |  kern    */
        vec4(dD1.x * v.y  ,  d.x * vD1.y  ,  dD1.x * vD1.y  ,  d.x * v.y),
        vec4(vD1.x * d.y  ,  v.x * dD1.y  ,  vD1.x * dD1.y  ,  v.x * d.y),
        vec4(dD1.x * d.y  ,  d.x * dD1.y  ,  dD1.x * dD1.y  ,  d.x * d.y),
        vec4(vD1.x * v.y  ,  v.x * vD1.y  ,  vD1.x * vD1.y  ,  v.x * v.y)
    );

    return m;
}

void kern4x4(vec2 p, out mat4 mA, out mat4 mB)
{
    vec2 v   = vec2(kern_v  (p.x), kern_v  (p.y));
    vec2 d   = vec2(kern_d  (p.x), kern_d  (p.y));

    vec2 vD1 = vec2(kern_vD1(p.x), kern_vD1(p.y));
    vec2 dD1 = vec2(kern_dD1(p.x), kern_dD1(p.y));

    vec2 vD2 = vec2(kern_vD2(p.x), kern_vD2(p.y));
    vec2 dD2 = vec2(kern_dD2(p.x), kern_dD2(p.y));

    mA = mat4
    (
        /*   kernDx       |  kernDy       |  kernDxy        |  kern    */
        vec4(dD1.x * v.y  ,  d.x * vD1.y  ,  dD1.x * vD1.y  ,  d.x * v.y),
        vec4(vD1.x * d.y  ,  v.x * dD1.y  ,  vD1.x * dD1.y  ,  v.x * d.y),
        vec4(dD1.x * d.y  ,  d.x * dD1.y  ,  dD1.x * dD1.y  ,  d.x * d.y),
        vec4(vD1.x * v.y  ,  v.x * vD1.y  ,  vD1.x * vD1.y  ,  v.x * v.y)
    );

    mB = mat4
    (
        /*   kernDxx      |  kernDyy      |  kernDxxy       |  kernDxyy    */
        vec4(dD2.x * v.y  ,  d.x * vD2.y  ,  dD2.x * vD1.y  ,  dD1.x * vD2.y),
        vec4(vD2.x * d.y  ,  v.x * dD2.y  ,  vD2.x * dD1.y  ,  vD1.x * dD2.y),
        vec4(dD2.x * d.y  ,  d.x * dD2.y  ,  dD2.x * dD1.y  ,  dD1.x * dD2.y),
        vec4(vD2.x * v.y  ,  v.x * vD2.y  ,  vD2.x * vD1.y  ,  vD1.x * vD2.y)
    );
}


// BICUBIC SAMPLING ROUTINES =============================================================================================================

// this is the most basic version which only evaluates the function value
float SampleBicubic(sampler2D channel, vec2 uv)
{
    uv -= vec2(0.5);

    vec2 uvi = floor(uv);
    vec2 uvf = uv - uvi;

    ivec2 uv0 = ivec2(uvi);

    float r = 0.0;
    for(int j = 0; j < 2; ++j)
    for(int i = 0; i < 2; ++i)
    {
        vec4 c = texelFetch(channel, uv0 + ivec2(i, j), 0);

        vec2 l = uvf;

        if(i != 0) l.x -= 1.0;
        if(j != 0) l.y -= 1.0;

        r += dot(c, kern(l));
    }

	return r;
}

// ... this version also outputs derivatives (used here to compute normals)
vec4 SampleBicubic2(sampler2D channel, vec2 uv)
{
    uv -= vec2(0.5);

    vec2 uvi = floor(uv);
    vec2 uvf = uv - uvi;

    ivec2 uv0 = ivec2(uvi);

    vec4 r = vec4(0.0);
    for(int j = 0; j < 2; ++j)
    for(int i = 0; i < 2; ++i)
    {
        vec4 c = texelFetch(channel, uv0 + ivec2(i, j), 0);

        vec2 l = uvf;

        if(i != 0) l.x -= 1.0;
        if(j != 0) l.y -= 1.0;

        r += kern4x4(l) * c;
    }

    // r = vec4(df/dx, df/dy, ddf/dxy, f)
	return r;
}

// ... this version also outputs higher order derivatives (only used to debug C2 continuity here)
vec4 SampleBicubic3(sampler2D channel, vec2 uv, out vec4 d2)
{
    uv -= vec2(0.5);

    vec2 uvi = floor(uv);
    vec2 uvf = uv - uvi;

    ivec2 uv0 = ivec2(uvi);

    d2 = vec4(0.0);
    vec4 r = vec4(0.0);
    for(int j = 0; j < 2; ++j)
    for(int i = 0; i < 2; ++i)
    {
        vec4 c = texelFetch(channel, uv0 + ivec2(i, j), 0);

        vec2 l = uvf;

        if(i != 0) l.x -= 1.0;
        if(j != 0) l.y -= 1.0;

        mat4 mA, mB;
        kern4x4(l, /*out*/ mA, mB);

        r  += mA * c;
        d2 += mB * c;
    }

    // r  = vec4(  df/dx,   df/dy,  ddf/dxy ,         f)
    // d2 = vec4(ddf/dxx, ddf/dyy, dddf/dxxy, dddf/dxyy)
	return r;
}


// IMAGE ==========================================================================================================================

void mainImage( out vec4 fragColor, in vec2 uv0 )
{    
   #if 0
    //if(uv0.x < 256.0 && uv0.y < 256.0)
    {
    	vec4 col = texelFetch(iChannel0, ivec2(uv0)/1, 0);
        col.xyz = col.xyz * 2.0 + 0.2;
    	fragColor = vec4((clamp01(col.rgb)), 0.0);
        return;
    }
   #endif

    vec3 col;

    vec2 uv = uv0;

   #if 1
    if(uv0.x >= iResolution.x*0.5)
    uv.x -= iResolution.x*0.5;
   #endif

    col = vec3(texture(iChannel0, uv0/iResolution.xy*0.125).r);

    float s = SCALE;

    uv += 2.0/SCALE;
    uv += iMouse.xy;// - iResolution.xy*0.5;

    vec4 d2;
    vec4 h = SampleBicubic3(iChannel0, uv * s, d2);

    float nscale = 4.0;// must be 1 for normals to properly align with height map
    vec3 N = normalize(vec3(-h.xy * nscale, 1.0));

    vec3 V = vec3(0.0, 0.0, 1.0);
    vec3 L = AngToVec(vec2(Pi*0.75, Pi*0.3)).xzy;
    //vec3 L = AngToVec(vec2(Pi*0.75 + sin(iTime)*(Pi*0.3), Pi*0.3)).xzy;
    vec3 H = normalize(L + V);

    float ct = clamp01(dot(N, L));

    if(uv0.x < iResolution.x*0.5)
    {
        if(uv0.y < iResolution.y*0.75)
        {
    		col = vec3(h.w);// == col = vec3(SampleBicubic(iChannel0, uv0 * s));
        }
        else
        {
            col = vec3(abs(d2.x + d2.y));
            //col = vec3(abs(d2.x + d2.y + d2.z + d2.w));
            //col = vec3(abs(d2.z + d2.w));
        }
    }
    else
    {
        if(uv0.y < iResolution.y*0.75)
        {
        	float r = (1.0-Pow2(1.0-h.w));
        	col = vec3(ct*h.w*h.w + 0.25*ct*r*(1.0/(1.0 + r*20.0 * (1.0-clamp01(dot(H, N))))));

        	col = GammaEncode(clamp01(col));
        }
        else
        {
         	col = clamp01(N * 0.5 + 0.5);
        }
    }


    fragColor = vec4(col, 0.0);
}

