// License: CC0 (https://creativecommons.org/publicdomain/zero/1.0/)


#define FILTERING_MODE 0
/*
	0: interpolating cubic kernel (produces C2 continuous signal without altering the sample values)
	1: generalized cubic BSpline (produces C2 continuous signal with smaller kernel but alters the sample values)
	2: quadratic BSpline (produces C1 continuous signal)
	3: central difference (reconstructed signal will not be C2 continuous; overall bad quality)
*/

#define FETCH(uv) (texelFetch(iChannel3, uv + ivec2(40, 64), 0).b)


void mainImage( out vec4 fragColor, in vec2 uv0 )
{    
    ivec2 uv = ivec2(uv0 - 0.5);

    vec4 col = vec4(0.0);

#if FILTERING_MODE == 0
    // ======================================================= INTERPOLATING CUBIC KERNEL =======================================================

    // truncated kernel radii for 1st and 2nd derivatives respectively
    int r1 = 5;
    int r2 = 2;
    // setting r2 to 0 results in suprisingly little shading artifacts (which might mean that in 3d we can throw out all higher order derivatives..)


    col.w = FETCH(uv);

   #if 1
    // precomputed kernel
    float kern[12];
    kern[0] = 0.0         ; kern[1] = -0.803848    ; kern[ 2] = 0.21539       ; kern[ 3] = -0.0577137    ; 
    kern[4] = 0.0154643   ; kern[5] = -0.00414365  ; kern[ 6] = 0.00111029    ; kern[ 7] = -0.000297501  ; 
    kern[8] = 0.0000797152; kern[9] = -0.0000213596; kern[10] = 0.00000572329 ; kern[11] = -0.00000153355;
   #else
    // compute kernel at compile time
    float kern[12];
    {
    	kern[0] = 0.0;

        float sn = -1.0;
    	for(uint i = 1u; i < 12u; ++i, sn = -sn)
        	kern[i] = sn * 3.0 * exp2(-float(i) * log2(2.0 + sqrt(3.0)));
    }
   #endif    

    // d/dx
    for(int i = -r1; i <= r1; ++i)
    {
    	float f = FETCH(uv + ivec2(i, 0));

        float k = kern[abs(i)] * (i > 0 ? -1.0 : 1.0);

        col.x += f * k;
    }

    // d/dy
    for(int i = -r1; i <= r1; ++i)
    {
    	float f = FETCH(uv + ivec2(0, i));

        float k = kern[abs(i)] * (i > 0 ? -1.0 : 1.0);

        col.y += f * k;
    }


    // d^2/dxy
    for(int j = -r2; j <= r2; ++j)
    for(int i = -r2; i <= r2; ++i)
    {
        if(i == 0 || j == 0) continue;

    	float f = FETCH(uv + ivec2(i, j));

        float kx = kern[abs(i)] * (i > 0 ? -1.0 : 1.0);
        float ky = kern[abs(j)] * (j > 0 ? -1.0 : 1.0);

        col.z += f * (kx * ky);
    }

#elif FILTERING_MODE == 1
    // ======================================================= GENERALIZED CUBIC BSPLINE =======================================================

    float kernD0[3];
    float kernD1[3];

    kernD0[0] = 2.0/3.0; kernD0[1] = 1.0/6.0; kernD0[2] = 0.0;
    kernD1[0] =     0.0; kernD1[1] =    -0.5; kernD1[2] = 0.0;

    float sw;// side lobes weight

    sw = 0.0;// cubic BSpline

   #if 1

    sw = 0.25;// similar to 1/3 but less ringing

   #elif 0

    sw = 1.0/3.0;// kernD0[0] == 1

   #elif 0

    sw = 0.186605;// max abs derivative == 1

   #elif 0

    sw = 1.0/6.0;// maximaly flat pass band

   #elif 0

    sw = -0.25;// spectrum falls off to 0 at Nyquist frequency

   #endif

    // add a pair of side lobes:
    kernD0[0] += 1.0 * sw; kernD0[1] += -1.0/3.0 * sw; kernD0[2] += -1.0/6.0 * sw;
    	                   kernD1[1] += -1.0     * sw; kernD1[2] +=  0.5     * sw;

    int r = sw == 0.0 ? 1 : 2;
    for(int j = -r; j <= r; ++j)
    for(int i = -r; i <= r; ++i)
    {
    	float f = FETCH(uv + ivec2(i, j));

        int x = abs(i);
        int y = abs(j);

        float kAx = kernD0[x];
        float kAy = kernD0[y];

        float kBx = kernD1[x] * (i > 0 ? -1.0 : 1.0);
        float kBy = kernD1[y] * (j > 0 ? -1.0 : 1.0);

        col += f * vec4(kBx * kAy, 
                        kAx * kBy, 
                        kBx * kBy,
                        kAx * kAy);
    }

#elif FILTERING_MODE == 2
    // ======================================================= QUADRATIC BSPLINE =======================================================

    float kernD0[2];
    float kernD1[2];

    kernD0[0] = 0.5; kernD0[1] =  0.25;
    kernD1[0] = 0.0; kernD1[1] = -0.5 ;

    int r = 1;
    for(int j = -r; j <= r; ++j)
    for(int i = -r; i <= r; ++i)
    {
    	float f = FETCH(uv + ivec2(i, j));

        int x = abs(i);
        int y = abs(j);

        float kAx = kernD0[x];
        float kAy = kernD0[y];

        float kBx = kernD1[x] * (i > 0 ? -1.0 : 1.0);
        float kBy = kernD1[y] * (j > 0 ? -1.0 : 1.0);

        col += f * vec4(kBx * kAy, 
                        kAx * kBy, 
                        kBx * kBy,
                        kAx * kAy);
    }

#else    
    // ======================================================= CENTRAL DIFFERENCE =======================================================

    col.w = FETCH(uv);

    float kern[2];

    kern[0] =  0.0;
    kern[1] = -0.5;

    for(int j = -1; j <= 1; ++j)
    for(int i = -1; i <= 1; ++i)
    {
    	float f = FETCH(uv + ivec2(i, j));

        float kx = kern[abs(i)] * (i > 0 ? -1.0 : 1.0);
        float ky = kern[abs(j)] * (j > 0 ? -1.0 : 1.0);

        col.xyz += f * vec3(kx * (j == 0 ? 1.0 : 0.0), 
                            ky * (i == 0 ? 1.0 : 0.0), 
                            kx * ky);
    }

#endif    

    // col = vec4(df/dx, df/dy, d^2f/dxy, f)
    fragColor = col;
}

