    pct += drawCircle(vec2(bottomLeft.x + mv.x, topRight.y), radius, edgeFade, fragCoord);
    pct += drawCircle(vec2(topRight.x, topRight.y - mv.y), radius, edgeFade, fragCoord);
    pct += drawCircle(vec2(topRight.x - mv.x, bottomLeft.y), radius, edgeFade, fragCoord);

    // Set color
