// Random Delaunay Triangulation - Tomkh
// https://www.shadertoy.com/view/4sKyRD
//
// Use "parabolic lifting" method to calculate if two triangles are about to flip.
// This is actually more reliable than circumscribed circle method.
// The technique is based on duality between Delaunay Triangulation
// and Convex Hull, where DT is just a boundary of convex hull
// of projected seeds onto paraboloid.
// We project (h1 h2 h3) triangle onto paraboloid
// and return the distance of the origin
// to a plane crossing projected triangle.
float flipDistance(vec2 h1, vec2 h2, vec2 h3)
{
   // Projects triangle on paraboloid.
   vec3 g1 = vec3(h1, dot(h1, h1));
   vec3 g2 = vec3(h2, dot(h2, h2));
   vec3 g3 = vec3(h3, dot(h3, h3));
   // Return signed distance of (g1, g2, g3) plane to the origin.
   //#if FLIP_ANIMATION
    // return dot(g1, normalize(cross(g3-g1, g2-g1)));
   //#else
     // If we don't do animation, we are only interested in a sign,
     // so normalization is unnecessary.
   	 return dot(g1, cross(g3-g1, g2-g1));
   //#endif
}
*/


// Skewing coordinates. "s" contains the X and Y skew factors.
vec2 skewXY(vec2 p, vec2 s){

    return mat2(1, -s.y, -s.x, 1)*p;
}

// Unskewing coordinates. "s" contains the X and Y skew factors.
vec2 unskewXY(vec2 p, vec2 s){

    //float idm = 1. - s.x*s.y;
    //if(idm == 0.) idm += 1e-6;
    //mat2 inM = 1./(idm)*mat2(1, s.y, s.x, 1);
    //return inM*p;
    return inverse(mat2(1, -s.y, -s.x, 1))*p;
}


// A regular extruded block grid.
//
// The idea is very simple: Produce a normal grid full of packed square pylons.
// That is, use the grid cell's center pixel to obtain a height value (read in
// from a height map), then render a pylon at that height.

// Global vertices and local coordinates of the triangle cell.
// It's lazy putting them here, but I'll tidy them up later.
vec2[3] gV;
vec2 gP;


vec4 blocks(vec3 q){


    // Scale.
    #ifdef SKEW_GRID
	const vec2 scale = vec2(1./.8660254, 1)*vec2(1./6.);
    #else
    // Roughly the same area as the above cell, but it can be anything
    // you want, and it doesn't have to be square either.
    const vec2 scale = vec2(.18); 
    #endif

    // Block dimension: Length to height ratio with additional scaling.
	const vec2 dim = scale;
    // A helper vector, but basically, it's the size of the repeat cell.
	const vec2 s = dim*2.;


    #ifdef SKEW_GRID
    // Skewing half way along X, and not skewing in the Y direction.
    const vec2 sk = vec2(.5/.8660254, 0);//;
    #else
    const vec2 sk = vec2(0);
    #endif

    // Distance.
    float d = 1e5;
    // Cell center, local coordinates, center and overall cell ID.
    vec2 p, ip, cntr, id = vec2(0);


    // Four block corner postions.
    const vec2[4] ps4 = vec2[4](vec2(-.5, .5), vec2(.5), vec2(.5, -.5), vec2(-.5)); 


    float triID = 0.; // Triangle ID. Not used in this example, but helpful.


    // Height scale.
    #ifdef FLAT_GRID
    const float hs = 0.;
    #else
    const float hs = .25;
    #endif

    // Initializing the global vertices and local coordinates of the triangle cell.
    gV = vec2[3](vec2(0), vec2(0), vec2(0));
    gP = p;

    for(int i = 0; i<4; i++){

        // Here's how you apply skewing to four neighboring blocks. There's a specific
        // order, which would become apparent if you were to code this from scratch. 
        // You could check, if you wanted, or just take my word for it... On a side note,
        // I make way too many mistakes, so you should never take my word for it. :D

        // Block center.
        cntr = ps4[i]/2. - ps4[0]/2.;

        // Skewed local coordinates.
        p = skewXY(q.xy, sk);

        ip = floor(p/s - cntr) + .5; // Local tile ID.
        p -= (ip + cntr)*s; // New local position.

        // Unskew the local coordinates.
        p = unskewXY(p, sk);

        // Correct positional individual tile ID.
        vec2 idi = ip + cntr;

        // Skewed rectangle vertices. 
        vec2[4] vert = ps4;  

        #ifdef OFFSET_TRIS
        // Offsetting the vertices. Note that accuracy is important here. I had a bug for
        // a while because I was premultiplying by "s," to save some calculations, which meant
        // points were not quite meeting at the joins... I won't bore you with the rest,
        // except to say that it's necessary to keep these numbers simple.
        vert[0] += hash22B((idi + vert[0]/2.))*.25;
   		vert[1] += hash22B((idi + vert[1]/2.))*.25;
        vert[2] += hash22B((idi + vert[2]/2.))*.25; 
        vert[3] += hash22B((idi + vert[3]/2.))*.25;
        #endif

        // Unskewing to enable rendering back in normal space.
        vert[0] = unskewXY(vert[0]*dim, sk);
        vert[1] = unskewXY(vert[1]*dim, sk);
        vert[2] = unskewXY(vert[2]*dim, sk);
        vert[3] = unskewXY(vert[3]*dim, sk); 

        // Unskewing the rectangular cell ID.
	    idi = unskewXY(idi*s, sk); 


        // Mid edge points. Not used here.   
        //vec2[4] mid = vec2[4](mix(vert[0], vert[1], .5), mix(vert[1], vert[2], .5),
                              //mix(vert[2], vert[3], .5), mix(vert[3], vert[0], .5));  



        // Some triangle flipping to ensure a Delaunay triangulation... Further non-Delaunay
        // subdivisions will occur, so it's probably redundant, but it's here for completeness.
        //float f = flipDistance(vert[0] - vert[2], vert[1] - vert[2], vert[3] - vert[2])<0.? 1. : -1.;


        // Partioning the rectangle into two triangles.

        // Triangle one.
        vec2[3] v1 = vec2[3](vert[0], vert[1], vert[2]); 
        //if(f>.5) v1 = vec2[3](vert[0], vert[1], vert[3]); // Delaunay flipping.

        // Incircle position and radius.
        vec3 inC = inCentRad(v1[0], v1[1], v1[2]);

        //float face1 = sdPoly(p, v1); 
        float face1 = sdTriR(p, v1[0], v1[1], v1[2], inC);
        //float face1 = length(p - inC.xy) - inC.z;
        //vec2 offs = (v1[0] + v1[1] + v1[2])/3.;
        vec2 offs = inC.xy;
        vec2 idi1 = idi + offs;
        float h1 = hm(idi1);
        //h1 = floor(h1*15.999)/15.; // Or just, "h1 *= .15," for nondiscreet heights.
        h1 *= hs;
        float face1Ext = opExtrusion(face1 + .0*scale.x, (q.z + h1), h1); 
        //float cyl1 = opExtrusion(length(p - inC.xy) - .015, (q.z + h1 + .015), h1 + .015);

        // Triangle two.
        vec2[3] v2 = vec2[3](vert[0], vert[2], vert[3]);
        //if(f>.5) v2 = vec2[3](vert[1], vert[2], vert[3]);  // Delaunay flipping.


        inC = inCentRad(v2[0], v2[1], v2[2]);
        //float face2 = sdPoly(p, v2);
        float face2 = sdTriR(p, v2[0], v2[1], v2[2], inC);
        //float face2 = length(p - inC.xy) - inC.z;
        //offs = (v2[0] + v2[1] + v2[2])/3.;
        offs = inC.xy;
        vec2 idi2 = idi + offs;
        float h2 = hm(idi2);
        //h2 = floor(h2*15.999)/15.; // Or just, "h1 *= .15," for nondiscreet heights.
        h2 *= hs;
        float face2Ext = opExtrusion(face2 + .0*scale.x, (q.z + h2), h2); 
        //float cyl2 = opExtrusion(length(p - inC.xy) - .015, (q.z + h2 + .015), h2 + .015);

        //face1Ext += max(face1, -.02);
        //face2Ext += max(face2, -.02);
        face1Ext += face1*.2;
        face2Ext += face2*.2;

        //face1Ext = min(face1Ext, cyl1);
        //face2Ext = min(face2Ext, cyl2);

        // Obtain the smallest triangle information.
        vec4 di = face1Ext<face2Ext? vec4(face1Ext, idi1, h1) : vec4(face2Ext, idi2, h2);



        // If applicable, update the overall minimum distance value,
        // ID, and box ID. 
        if(di.x<d){

            d = di.x; // Minimum distance.
            id = di.yz; // Position-based triangle ID.
            triID = di.w; // Triangle ID. Not used here, but useful.

            // Setting the vertices and local coordinates.
            // Hacked in at the last minute.
            gV = v2; if(face1Ext<face2Ext) gV = v1;
            gP = p;

        }

    }

    // Return the distance, position-based ID and triangle ID.
    return vec4(d, id, triID);
}



// Block ID -- It's a bit lazy putting it here, but it works. :)
vec3 gID;

// The extruded blocks.
float map(vec3 p){

    // Floor.
    float fl = -p.z + .0;

    // The extruded blocks.
    vec4 d4 = blocks(p);
    gID = d4.yzw; // Individual block ID.


    //d4.x = -p.z + d4.x*.5;


    // Overall object ID.
    objID = fl<d4.x? 1. : 0.;

    // Combining the floor with the extruded image
    return min(fl, d4.x);

}


// Basic raymarcher.
float trace(in vec3 ro, in vec3 rd){

    // Overall ray distance and scene distance.
    float t = 0., d;

    for(int i = 0; i<80; i++){

        d = map(ro + rd*t);
        // Note the "t*b + a" addition. Basically, we're putting less emphasis on accuracy, as
        // "t" increases. It's a cheap trick that works in most situations... Not all, though.
        if(abs(d)<.001*(1. + t*.125) || t>FAR) break; // Alternative: 0.001*max(t*.25, 1.), etc.

        // Hacky, but not all distance fields play nice. :)
        t += i<40? d*.5 : d*.75; 
        //t += d*.7; 
    }

    return min(t, FAR);
}


// Standard normal function. It's not as fast as the tetrahedral calculation, but more symmetrical.
vec3 getNormal(in vec3 p, float t) {

    const vec2 e = vec2(.001, 0);


    //vec3 n = normalize(vec3(map(p + e.xyy) - map(p - e.xyy),
    //map(p + e.yxy) - map(p - e.yxy),	map(p + e.yyx) - map(p - e.yyx)));

    // This mess is an attempt to speed up compiler time by forcing a break...
    // Not sure if it works or not.
    float sgn = 1.;
    vec3 n = vec3(0);
    float mp[6];
    vec3[3] e6 = vec3[3](e.xyy, e.yxy, e.yyx);
    for(int i = min(iFrame, 0); i<6; i++){
		mp[i] = map(p + sgn*e6[i/2]);
        sgn = -sgn;
        if(sgn>2.) break;
    }

    return normalize(vec3(mp[0] - mp[1], mp[2] - mp[3], mp[4] - mp[5]));
}


// Cheap shadows are hard. In fact, I'd almost say, shadowing particular scenes with limited 
// iterations is impossible... However, I'd be very grateful if someone could prove me wrong. :)
float softShadow(vec3 ro, vec3 lp, vec3 n, float k){

    // More would be nicer. More is always nicer, but not really affordable... Not on my slow test machine, anyway.
    const int maxIterationsShad = 24; 

    ro += n*.0015;
    vec3 rd = lp - ro; // Unnormalized direction ray.


    float shade = 1.;
    float t = 0.;//.0015; // Coincides with the hit condition in the "trace" function.  
    float end = max(length(rd), 0.0001);
    //float stepDist = end/float(maxIterationsShad);
    rd /= end;

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. Obviously, the lowest 
    // number to give a decent shadow is the best one to choose. 
    for (int i = 0; i<maxIterationsShad; i++){

        float d = map(ro + rd*t);
        shade = min(shade, k*d/t);
        //shade = min(shade, smoothstep(0., 1., k*h/dist)); // Subtle difference. Thanks to IQ for this tidbit.
        // So many options here, and none are perfect: dist += min(h, .2), dist += clamp(h, .01, stepDist), etc.
        t += clamp(d, .01, .25); 


        // Early exits from accumulative distance function calls tend to be a good thing.
        if (d<0. || t>end) break; 
    }

    // Sometimes, I'll add a constant to the final shade value, which lightens the shadow a bit --
    // It's a preference thing. Really dark shadows look too brutal to me. Sometimes, I'll add 
    // AO also just for kicks. :)
    return max(shade, 0.); 
}


// I keep a collection of occlusion routines... OK, that sounded really nerdy. :)
// Anyway, I like this one. I'm assuming it's based on IQ's original.
float calcAO(in vec3 p, in vec3 n)
{
	float sca = 3., occ = 0.;
    for( int i = 0; i<5; i++ ){

        float hr = float(i + 1)*.15/5.;        
        float d = map(p + n*hr);
        occ += (hr - d)*sca;
        sca *= .7;
    }

    return clamp(1. - occ, 0., 1.);  


}

// Compact, self-contained version of IQ's 3D value noise function. I have a transparent noise
// example that explains it, if you require it.
float n3D(in vec3 p){

	const vec3 s = vec3(7, 157, 113);
	vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(3. - 2.*p); //p *= p*p*(p*(p * 6. - 15.) + 10.);
    h = mix(fract(sin(h)*43758.5453), fract(sin(h + s.x)*43758.5453), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); // Range: [0, 1].
}

// Very basic pseudo environment mapping... and by that, I mean it's fake. :) However, it 
// does give the impression that the surface is reflecting the surrounds in some way.
//
// More sophisticated environment mapping:
// UI easy to integrate - XT95    
// https://www.shadertoy.com/view/ldKSDm
vec3 envMap(vec3 p){

    p *= 6.;
    p.y += iTime;

    float n3D2 = n3D(p*2.);

    // A bit of fBm.
    float c = n3D(p)*.57 + n3D2*.28 + n3D(p*4.)*.15;
    c = smoothstep(.45, 1., c); // Putting in some dark space.

    p = vec3(c*c*c*c, c*c, c); // Bluish tinge.

    return mix(p.zxy, p, n3D2*.34 + .665).yxz; // Mixing in a bit of purple.

}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    // Screen coordinates.
	vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

	// Camera Setup.
	vec3 ro = vec3(0, iTime*1.*.5, -1.5); // Camera position, doubling as the ray origin.
	vec3 lk = ro + vec3(0, .1, .25);//vec3(0, -.25, iTime);  // "Look At" position.

    // Light positioning. One is just in front of the camera, and the other is in front of that.
 	vec3 lp = ro + vec3(1.5, 3, -1.5);// Put it a bit in front of the camera.


    // Using the above to produce the unit ray-direction vector.
    float FOV = 1.; // FOV - Field of view.
    vec3 fwd = normalize(lk - ro);
    vec3 rgt = normalize(vec3(fwd.z, 0., -fwd.x )); 
    // "right" and "forward" are perpendicular, due to the dot product being zero. Therefore, I'm 
    // assuming no normalization is necessary? The only reason I ask is that lots of people do 
    // normalize, so perhaps I'm overlooking something?
    vec3 up = cross(fwd, rgt); 

    // rd - Ray direction.
    vec3 rd = normalize(uv.x*rgt + uv.y*up + fwd/FOV);

    // Swiveling the camera about the XY-plane.
	//rd.xy *= rot2( sin(iTime/8. - cos(iTime/12.))/2. );



    // Raymarch to the scene.
    float t = trace(ro, rd);

    // Save the block ID and object ID.
    vec3 svGID = gID;

    // Object ID.
    float svObjID = objID;

    // Saved local triangle face coordinates and vertices.
    vec2 svP = gP;
    vec2[3] svV = gV;


    // Initiate the scene color to black.
	vec3 col = vec3(0);

	// The ray has effectively hit the surface, so light it up.
	if(t < FAR){


    	// Surface position and surface normal.
	    vec3 sp = ro + rd*t;
	    //vec3 sn = getNormal(sp, edge, crv, ef, t);
        vec3 sn = getNormal(sp, t);


        // Obtaining the texel color. 
	    vec3 texCol;   

        // The extruded grid.
        if(svObjID<.5){

            // Coloring the individual blocks with the saved ID.

            // Tri-planar texture coloring.
            vec3 tx = tex3D(iChannel0, sp/1., sn);
            // Solid coloring for each block.
            vec3 tx2 = getTex(svGID.xy);

            // Triangle color.
            texCol = mix(tx, tx2, 1.5);


            //texCol = .55 + .45*cos(6.2831*hash21(svGID.xy)/2. + vec3(0, 1, 2));

            // Black dots at the incircle position on the triangle face.
            //texCol = mix(texCol, vec3(0), 
            //				1. - smoothstep(0., .005, length(sp.xy - svGID.xy) - .0086));


            // Rendering a dark ring around the face edges.
            vec3 inC = inCentRad(svV[0], svV[1], svV[2]);
            float tri = sdTriR(svP, svV[0], svV[1], svV[2], inC);
            float tri2 = tri;
            tri = max(abs(tri) - .001, abs(sp.z + svGID.z*2.));
            texCol = mix(texCol, vec3(0), 1. - smoothstep(0., .005, tri));
            //texCol = mix(texCol, vec3(0), 1. - smoothstep(0., .005, tri + .005));

            // Light lines from the incircle center to the vertices for emphasis.
            const float ew = .001;
            float ln = 1e5;
            ln = min(ln, lBox(svP, inC.xy, svV[0], ew));
            ln = min(ln, lBox(svP, inC.xy, svV[1], ew));
            ln = min(ln, lBox(svP, inC.xy, svV[2], ew));
            texCol = mix(texCol, min(texCol*1.66, 1.), (1. - smoothstep(0., .005, ln)));

            // Other markings.
            //float pat = abs(fract(tri2*50.) - .5)*2. - .25;
            //texCol = mix(texCol, vec3(0), (1. - smoothstep(0., .005, max(tri2, pat)))*.7);
            //texCol = mix(texCol, vec3(0), 1. - smoothstep(0., .005, tri2 + inC.z - .01));
            //texCol = mix(texCol, vec3(0), 1. - smoothstep(0., .005, abs(tri2 + .015) - .001));


        }
        else {

            // The dark floor in the background. Hiddent behind the pylons, but
            // you still need it.
            texCol = vec3(0);
        }


    	// Light direction vector.
	    vec3 ld = lp - sp;

        // Distance from respective light to the surface point.
	    float lDist = max(length(ld), .001);

    	// Normalize the light direction vector.
	    ld /= lDist;



        // Shadows and ambient self shadowing.
    	float sh = softShadow(sp, lp, sn, 16.);
    	float ao = calcAO(sp, sn); // Ambient occlusion.
        sh = min(sh + ao*.25, 1.);

	    // Light attenuation, based on the distances above.
	    float atten = 1.25/(1. + lDist*.05);


    	// Diffuse lighting.
	    float diff = max( dot(sn, ld), 0.);
        diff = pow(diff, 2.)*1.35; // Ramping up the diffuse.

    	// Specular lighting.
	    float spec = pow(max(dot(reflect(ld, sn), rd ), 0.), 16.); 

	    // Fresnel term. Good for giving a surface a bit of a reflective glow.
        float fre = pow(clamp(1. - abs(dot(sn, rd))*.5, 0., 1.), 2.);

        // Combining the above terms to procude the final color.
        col = texCol*(diff + ao*.35 + vec3(.2, .4, 1)*fre + vec3(1, .4, .2)*spec*4.);

        vec3 cTex = envMap(reflect(rd, sn));//texture(iChannel2, reflect(rd, sn)).xyz; cTex *= cTex;

        col += col*cTex*5.;

        // Shading.
        col *= ao*sh*atten;



	}

/*    
    // Just the 2D rendering.
    uv = fragCoord/iResolution.y;
    vec4 d = blocks(vec3(uv + iTime/32., 0.));
    vec3 inC = inCentRad(gV[0],  gV[1],  gV[2]); 
    vec3 oCol = smoothstep(0., .5, getTex(d.yz));
    float triD = sdTriR(gP, gV[0],  gV[1],  gV[2], inC); 
    float sf = 1./iResolution.y;
    col = mix(vec3(.1), vec3(0), 1. - smoothstep(0., sf, triD));
    col = mix(col, oCol, 1. - smoothstep(0., sf, triD + .005));
*/        

    // Rought gamma correction.
	fragColor = vec4(sqrt(max(col, 0.)), 1);

}

