// set AA 2 if you have beafy pc
#define AA 2
#define OPTIMIZE 1

// not worth it, too different look
#define SIMPLERAO 0
#define ORTHOANIM 0

// #define SDBOX( p, r ) (  length(max(abs(p)-r,0.)))

float hash( in float n )
{
    return fract(sin(n)*43758.5453);
}

float sdBox( in vec3 p, in vec3 r )
{
    vec3 d = abs(p)-r;
    return length(max(d,0.));
}
float displacement(in vec3 p, in float force){
	return (sin(force*p.z*1.0)/2.) / force;
}

vec4 map( in vec3 p, float time )
{
    vec3 boxSize = vec3(0.03, 0.2, 0.4);
    vec3 start = vec3(0.32,-0.1,0.0);
    vec3 dif = vec3(0.08,0.0,-0.015);
    float d = 0.1;

    p -= start;

    #if OPTIMIZE
    // bounding box
    float bbox = sdBox( p+vec3(0.495,-0.09,0.), vec3(0.54, 0.2, 0.65) );
    if (bbox < 0.05){
    #endif

    float t = iTime*1.75;
    float an1 = smoothstep(0.0,1.0,(sin(t+10.26)*.5+.6));
    an1 *= an1;
    float an2 = smoothstep(0.0,1.0,(sin(t+0.6)*.5+.6));
    an2 *= an2;
    float an3 = smoothstep(0.0,1.0,(sin(t+42.745)*0.5+0.6));
    an3 *= an3;
    float an4 = smoothstep(0.0,1.0,(sin(t+0.345)*0.5+0.6));
    an4 *= an4;
    float an5 = smoothstep(0.0,1.0,(sin(t+142.745)*0.6+0.6));
    an5 *= an5;
    float an6 = smoothstep(0.0,1.0,(sin(t+2.135)*0.6+0.6));
    an6 *= an6;
    float an7 = smoothstep(0.0,1.0,(sin(t+3.956)*0.7+0.6));
    an7 *= an7;
    float an8 = smoothstep(0.0,1.0,(sin(t+1.3)*0.7+0.6));
    an8 *= an8;

	#if 0
    p.z = p.z*(0.8*smoothstep(0.0,1.0,(sin(t/4.)*2.6+.0)));
	#else
	p.z = p.z*0.8;
	#endif
    vec3 q = p;


    for(float i=0.01; i<13.; i+=1.){
        q=p+dif*i;

        #if OPTIMIZE
        float bbox = sdBox( q +vec3(0.,-0.1,0.), vec3(boxSize.x, boxSize.y*1.0, boxSize.z) );
        //d = min(d, bbox);
        if (bbox >= 0.064){
            continue;
        }
        #endif

        float h = hash(i*i/3.);
        float h2 = hash(h*i/3.);
        float c2 = mod(i-0.01, 2.);
        float c3 = mod(i-0.01, 3.);
        float c4 = mod(i-0.01, 4.);
        float c5 = mod(i-0.01, 5.);
        float c6 = mod(i-0.01, 6.);
        vec3 qd = q+vec3(0.,0.,0.1)*h;

        if (i<1.){
            q.y += displacement(q, 40.)*0.8;
            float dis = displacement(qd+vec3(0.,0.,1.), 30.)*1.0 ;
            q.y += dis+dis*an1;
            q.y += 		displacement(qd+vec3(0.,0.,0.2), 60.)*1.0 * an3;
		} else {
            q.y += (displacement(qd+vec3(0.,0.,1.2*h2), 20.)*0.9) ;
            q.y += (displacement(qd+vec3(0.,0.,1.1*h),  20.)*an4*1.0) * c2 ;
            q.y += (displacement(qd+vec3(0.,0.,1.9*h2), 20.)*an5*0.5) * c3;
            q.y += (displacement(qd+vec3(0.,0.,1.5*h),  30.)*an6*0.7) * c4;
            q.y += (displacement(qd+vec3(0.,0.,6.2*h),  30.)*an7*0.4) * c5;
            q.y += (displacement(qd+vec3(0.,0.,2.2*h),  40.)*an8*0.3) * c6;
    	}
        q.y += smoothstep(0.9,1.1,abs(q.z+dif.z)/boxSize.z)/10.; // edges
        float d2 = sdBox( q, boxSize);
        d = min(d, d2);

        // if hit box in front - skip all next boxes
        #if OPTIMIZE
        if (d2<0.0135)
            break;
        #endif
    }

    #if OPTIMIZE
    }
    #endif

    //d = min(bbox,d);
    return vec4( d, p );
}

#define ZERO min(iFrame,0)

vec3 calcNormal( in vec3 pos, in float time )
{
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.0005*e,time).x;
    }
    return normalize(n);
}

float calcAO( in vec3 pos, in vec3 nor, in float time )
{
	float occ = 0.0;
    float sca = 1.0;
    #if SIMPLERAO
    int   a = 3;
    float b = 1.;
    #else
    int   a = 5;
    float b = 4.;
    #endif

    for( int i=ZERO; i<a; i++ )
    {
        float h = 0.01 + 0.12*float(i)/b;
        float d = map( pos+h*nor, time ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

vec4 intersect( in vec3 ro, in vec3 rd, in float time )
{
    vec4 res = vec4(-1.0);

    float t = 1.9;
    float tmax = 3.1;
    for( int i=0; i<128 && t<tmax; i++ )
    {
        vec4 h = map(ro+t*rd,time)/2.;			//hack
        if( h.x<0.001 ) { res=vec4(t,h.yzw); break; }
        t += h.x;
    }

    return res;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        float d = 0.5*sin(fragCoord.x*147.0)*sin(fragCoord.y*131.0);
        float time = iTime;
        #else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        float time = iTime;
        #endif


	    // camera	
        float an = 1.0;// 6.2831*time/40.0;
        vec3 ta = vec3( 0.0, 0.1, 0.0 )*1.9;
    	vec3 ro = vec3(1., 1.2, 2.);// ray origin (camera pos)
    	//ro = vec3(0., 1.2, 2.);// ray origin (camera pos)
        //vec3 ro = ta + vec3( 0.5*cos(an), 0.2, 0.5*sin(an) );

        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta, 0. );

        // ray direction
        float fl = 4.0;
        vec3 rd = ca * normalize( vec3(p,fl) );

    #if !ORTHOANIM
    float f = 0.56;
	#else
	float f = iMouse.x/iResolution.x;
    #endif

    vec3 rd_orth = setCamera(ro, ta, 0.)*normalize(vec3(0., 0., 1.0));// ray direction via orthographic projection
    rd = mix(rd, rd_orth, f);

    #if ORTHOANIM
    float orthRectSize = .0 + 3.0*iMouse.y/iResolution.y;
    vec3 ro_orth = ro + vec3(p * orthRectSize, 0.);// ray origin 
    ro = mix(ro, ro_orth, f);
    #endif


        // background
        vec3 bcol = vec3(0.01, 0.7, 1.0);
        vec3 tcol = vec3(0.71, 0.35, 0.87)-0.25;
        vec3 col = mix(bcol, tcol, p.y*.5+0.5 /*1.0+rd.y*/);//vec3(1.0+rd.y)*0.03;

        // raymarch geometry
        vec4 tuvw = intersect( ro, rd, time );

        if( tuvw.x>0.0 )
        {
            // shading/lighting	
            vec3 pos = ro + tuvw.x*rd;
            vec3 nor = calcNormal(pos, time);
            vec3 l = vec3(2.0,1.0,-1.);
            float ndl = dot(nor,l)*.4+.6;
            vec3 amb = col*.5;
            float rim = (dot(rd,nor)*.5+.5);
            rim = clamp(0.0,1.0,(pow(rim,1.)));
            float invRim = 1.0-rim;
            invRim*=invRim*invRim*invRim;

            float y = tuvw.y;
            y = y*y*8.5;
            y += 2.0;
            y /= 3.0;
            tcol = vec3(0.823, 0.686, 0.992)*0.8;
			col = mix(bcol, tcol, y);

            col = col*ndl+amb;
            col *= col + sin(tuvw.yyy*3000.)*col*0.9*invRim * nor.y + rim;

    #if SIMPLERAO
            float ao = calcAO( tuvw.yzw, nor, time )*1.5;
            ao += 3.0*ao*(1.-nor.y); // top
            float side = nor.x*(1.-ao)*.8;  // side
            ao += side;
            ao *= ao;
            col = mix(col,col*ao/.9,0.6);
    #else
            float ao = calcAO( tuvw.yzw, nor, time )*1.2;
            ao += 2.0*ao*(1.-nor.y); // top
            ao += nor.x*(1.-ao)*.2;  // side
            col = mix(col,col*ao/.9,0.6);
    #endif

            // color correction
            col *= 0.5;
            col *= col + nor.y*0.4;
            //col = vec3(ao);
        }

	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // gamma        
    tot = pow(tot,vec3(0.45) );

    // cheap dithering to remove banding
    tot += sin(fragCoord.x*114.0)*sin(fragCoord.y*211.1)/512.0;

    // show banding
    #if 0
    tot = floor(tot*255.)/255.;
    tot = (abs(dFdy(tot))+abs(dFdx(tot)))*200.0;
    #endif

    fragColor = vec4( tot, 1.0 );
}

// Edit of Photoshop Blend Modes by ridhojeftha https://www.shadertoy.com/view/4tSGWV
// Why not photoshop blend audio?

//Darken
vec3 darken (vec3 target, vec3 blend){

 return min (target, blend);   
}

//Multiply
vec3 multiply (vec3 target, vec3 blend){
    return target*blend;
}

//Color Burn
vec3 colorBurn (vec3 target, vec3 blend){
    return 1.0 - (1.0 - target)/ blend;
}

//Linear Burn
vec3 linearBurn (vec3 target, vec3 blend){
    return target + blend - 1.0;
}

//Lighten
vec3 lighten (vec3 target, vec3 blend){
    return max (target, blend);
}

//Screen
vec3 screen (vec3 target, vec3 blend){
    return 1.0 - (1.0 - target) * (1.0 - blend);
}

//Color Dodge
vec3 colorDodge (vec3 target, vec3 blend){
    return target / (1.0 - blend);
}

//Linear Dodge
vec3 linearDodge (vec3 target, vec3 blend){
    return target + blend;
}

//Overlay
vec3 overlay (vec3 target, vec3 blend){
    vec3 temp;
    temp.x = (target.x > 0.5) ? (1.0-(1.0-2.0*(target.x-0.5))*(1.0-blend.x)) : (2.0*target.x)*blend.x;
    temp.y = (target.y > 0.5) ? (1.0-(1.0-2.0*(target.y-0.5))*(1.0-blend.y)) : (2.0*target.y)*blend.y;
    temp.z = (target.z > 0.5) ? (1.0-(1.0-2.0*(target.z-0.5))*(1.0-blend.z)) : (2.0*target.z)*blend.z;
    return temp;
}

//Soft Light
vec3 softLight (vec3 target, vec3 blend){
 vec3 temp;
    temp.x = (blend.x > 0.5) ? (1.0-(1.0-target.x)*(1.0-(blend.x-0.5))) : (target.x * (blend.x + 0.5));
    temp.y = (blend.y > 0.5) ? (1.0-(1.0-target.y)*(1.0-(blend.y-0.5))) : (target.y * (blend.y + 0.5));
    temp.z = (blend.z > 0.5) ? (1.0-(1.0-target.z)*(1.0-(blend.z-0.5))) : (target.z * (blend.z + 0.5));
    return temp;   
}

//Hard Light
vec3 hardLight (vec3 target, vec3 blend){
    vec3 temp;
    temp.x = (blend.x > 0.5) ? (1.0-(1.0-target.x)*(1.0-2.0*(blend.x-0.5))) : (target.x * (2.0*blend.x));
    temp.y = (blend.y > 0.5) ? (1.0-(1.0-target.y)*(1.0-2.0*(blend.y-0.5))) : (target.y * (2.0*blend.y));
    temp.z = (blend.z > 0.5) ? (1.0-(1.0-target.z)*(1.0-2.0*(blend.z-0.5))) : (target.z * (2.0*blend.z));
    return temp;
}

//Vivid Light
vec3 vividLight (vec3 target, vec3 blend){
     vec3 temp;
    temp.x = (blend.x > 0.5) ? (1.0-(1.0-target.x)/(2.0*(blend.x-0.5))) : (target.x / (1.0-2.0*blend.x));
    temp.y = (blend.y > 0.5) ? (1.0-(1.0-target.y)/(2.0*(blend.y-0.5))) : (target.y / (1.0-2.0*blend.y));
    temp.z = (blend.z > 0.5) ? (1.0-(1.0-target.z)/(2.0*(blend.z-0.5))) : (target.z / (1.0-2.0*blend.z));
    return temp;
}

//Linear Light
vec3 linearLight (vec3 target, vec3 blend){
    vec3 temp;
    temp.x = (blend.x > 0.5) ? (target.x)+(2.0*(blend.x-0.5)) : (target.x +(2.0*blend.x-1.0));
    temp.y = (blend.y > 0.5) ? (target.y)+(2.0*(blend.y-0.5)) : (target.y +(2.0*blend.y-1.0));
    temp.z = (blend.z > 0.5) ? (target.z)+(2.0*(blend.z-0.5)) : (target.z +(2.0*blend.z-1.0));
    return temp;
}

//Pin Light
vec3 pinLight (vec3 target, vec3 blend){
     vec3 temp;
    temp.x = (blend.x > 0.5) ? (max (target.x, 2.0*(blend.x-0.5))) : (min(target.x, 2.0*blend.x));
    temp.y = (blend.y > 0.5) ? (max (target.y, 2.0*(blend.y-0.5))) : (min(target.y, 2.0*blend.y));
    temp.z = (blend.z > 0.5) ? (max (target.z, 2.0*(blend.z-0.5))) : (min(target.z, 2.0*blend.z));
    return temp;
}

//Difference
vec3 difference (vec3 target, vec3 blend){
    return abs (target - blend);

}
//Exclusion
vec3 exclusion (vec3 target, vec3 blend){
    return 0.5 - 2.0*(target-0.5)*(blend-0.5);
}

//Subtract (thanks to Brandondorf9999)
vec3 subtract (vec3 target, vec3 blend){
    return target - blend;
}

vec3 modulo (vec3 target, vec3 blend){
 return mod(target,blend);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float scale = 0.04 / ceil(mod (iTime/2.0, 6.0));
    vec3 upperTexture = vec3(sin(iTime+fragCoord.x*scale));
    vec3 lowerTexture = vec3(
        (sin(iTime+fragCoord.x*2.5025*scale)*0.75)
        +(sin(iTime+fragCoord.x*7.501*scale)*0.25));

    int time = int (mod (iTime/2.0, 18.0));

    vec3 finalImage = vec3(0.0);
    if (time==0) finalImage =  darken ( upperTexture,  lowerTexture	);
    else if (time==1) finalImage = multiply ( upperTexture,  lowerTexture);
    else if (time==2) finalImage =colorBurn ( upperTexture,  lowerTexture);
    else if (time==3) finalImage =linearBurn ( upperTexture,  lowerTexture);
    else if (time==4) finalImage =lighten ( upperTexture,  lowerTexture);
    else if (time==5) finalImage =screen ( upperTexture,  lowerTexture);
    else if (time==6) finalImage =colorDodge ( upperTexture,  lowerTexture);
    else if (time==7) finalImage =linearDodge (upperTexture,  lowerTexture);
    else if (time==8) finalImage =overlay ( upperTexture,  lowerTexture);
    else if (time==9) finalImage =softLight ( upperTexture,  lowerTexture);
    else if (time==10) finalImage =hardLight ( upperTexture,  lowerTexture);
    else if (time==11) finalImage =vividLight ( upperTexture,  lowerTexture);
    else if (time==12) finalImage =linearLight ( upperTexture,  lowerTexture);
    else if (time==13) finalImage =pinLight ( upperTexture,  lowerTexture);
    else if (time==14) finalImage =difference ( upperTexture,  lowerTexture);
    else if (time==15) finalImage =exclusion (upperTexture, lowerTexture);
    else if (time==16) finalImage = subtract(upperTexture, lowerTexture);
    else if (time== 17) finalImage = modulo(upperTexture, lowerTexture);

    //set the color
    finalImage.g = (finalImage.g*finalImage.g);
    finalImage.b = 1.0 - (finalImage.g*finalImage.b);
    fragColor = vec4(floor(finalImage*8.0)/8.0, 1.0);
}

