//Overlay
float overlay (float target, float blend){
    return (target > 0.5) ? (1.0-(1.0-2.0*(target-0.5))*(1.0-blend)) : (2.0*target)*blend;
}

//Soft Light
float softLight (float target, float blend){
    return (blend > 0.5) ? (1.0-(1.0-target)*(1.0-(blend-0.5))) : (target * (blend + 0.5));  
}

//Hard Light
float hardLight (float target, float blend){
    return (blend > 0.5) ? (1.0-(1.0-target)*(1.0-2.0*(blend-0.5))) : (target * (2.0*blend));
}

//Vivid Light
float vividLight (float target, float blend){
    return (blend > 0.5) ? (1.0-(1.0-target)/(2.0*(blend-0.5))) : (target / (1.0-2.0*blend));
}

//Linear Light
float linearLight (float target, float blend){
    return (blend > 0.5) ? (target)+(2.0*(blend-0.5)) : (target +(2.0*blend-1.0));
}

//Pin Light
float pinLight (float target, float blend){
    return (blend > 0.5) ? (max (target, 2.0*(blend-0.5))) : (min(target, 2.0*blend));
}

//Difference
float difference (float target, float blend){
    return abs (target - blend);

}
//Exclusion
float exclusion (float target, float blend){
    return 0.5 - 2.0*(target-0.5)*(blend-0.5);
}

//Subtract (thanks to Brandondorf9999)
float subtract (float target, float blend){
    return target - blend;
}

//Divide (thanks to Brandondorf9999)
float divide (float target, float blend){
 return target/blend;   
}

float modulo (float target, float blend){
 return mod(target,blend);   
}

vec2 mainSound( in int samp, float time )
{
    // A 440 Hz wave that attenuates quickly overt time
    float osc1 = sin(6.2831*420.0*time);
    float osc2 = sin(6.2831*420.0*2.5025*time) * 0.75;
    osc2 += sin(6.2831*220.0*7.601*time) * 0.25;
    float finalImage;
    int func = int (mod (time/2.0, 18.0));
    //func = 1;
    if (func==0) finalImage =  darken ( osc1,  osc2	);
    else if (func==1) finalImage = multiply ( osc1,  osc2);
    else if (func==2) finalImage =colorBurn ( osc1,  osc2)*0.25;
    else if (func==3) finalImage =linearBurn ( osc1,  osc2);
    else if (func==4) finalImage =lighten ( osc1,  osc2);
    else if (func==5) finalImage =screen ( osc1,  osc2);
    else if (func==6) finalImage =colorDodge ( osc1,  osc2);
    else if (func==7) finalImage =linearDodge (osc1,  osc2);
    else if (func==8) finalImage =overlay ( osc1,  osc2);
    else if (func==9) finalImage =softLight ( osc1,  osc2);
    else if (func==10) finalImage =hardLight ( osc1,  osc2);
    else if (func==11) finalImage =vividLight ( osc1,  osc2);
    else if (func==12) finalImage =linearLight ( osc1,  osc2);
    else if (func==13) finalImage =pinLight ( osc1,  osc2);
    else if (func==14) finalImage =difference ( osc1,  osc2);
    else if (func==15) finalImage =exclusion (osc1, osc2);
    else if (func==16) finalImage =subtract (osc1, osc2);
    else if (func==17) finalImage =modulo (osc1, osc2);

    return vec2( (floor(finalImage*8.0)/8.0) * 0.25 );
}

