//to World
vec3 toWorld(vec3 dir, vec3 normOut)
{
    //todo: do this properly using quaternions
    vec3 normDir = vec3(0,0,1);

    float epsilon = 1.0 - 1e-5;
    if(epsilon < dot(normDir, normOut))
        return dir;

    if(dot(normDir, normOut) < -epsilon)
        return -dir;

    //make a pair of bases that are orthogonal to the output normal
    vec3 a = cross(normDir, normOut);
    a = normalize(a);

    vec3 b = cross(a, normOut);
    b = normalize(b);

    mat3 M = mat3(a, b, normOut);

    return normalize(M * dir);
}

//random point in lat/long coords (naiive implementation)
vec3 randMercator(out float pdf)
{
    float xi1 = rnd();
    float xi2 = rnd();

    float psi = xi1 * 3.141592;
    float theta = xi2 * 2.0 * 3.141592;

    float x = sin(psi) * cos(theta);
    float y = sin(psi) * sin(theta);
    float z = cos(psi);

    pdf = sin(psi);

    return vec3(x, y, z);
}

//random point in sphere
vec3 randSphere()
{
    float xi1 = rnd() * 2.0 - 1.0;
    float xi2 = rnd();

    float theta = xi2 * 2.0 * 3.141592;
    float sinp = sqrt(1.0 - xi1*xi1);

    float x = sinp * cos(theta);
    float y = sinp * sin(theta);
    float z = xi1;

    return vec3(x, y, z);
}

vec3 randLambert(vec3 normal)
{
    float xi1 = rnd();
    float xi2 = rnd();

    float theta = xi2 * 2.0 * 3.141592;
    float cosp = sqrt(xi1);
    float sinp = sqrt(1.0 - cosp*cosp);

    float x = sinp * cos(theta);
    float y = sinp * sin(theta);
    float z = cosp;

    return toWorld(vec3(x, y, z), normal);
}

//general case of randLambert
vec3 randPhongSpec(vec3 normal, vec3 rIn, float power)
{
    vec3 refl = reflect(rIn, normal);

    float xi1 = rnd();
    float xi2 = rnd();

    float theta = xi2 * 2.0 * 3.141592;
    float cosp = pow(xi1, 1.0/power);
    float sinp = sqrt(1.0 - cosp*cosp);

    float x = sinp * cos(theta);
    float y = sinp * sin(theta);
    float z = cosp;

    return toWorld(vec3(x, y, z), refl);
}

