void tvSphere(Ray rIn, vec3 normIn, out tvHit hit, inout float rayDist, int id, float sRad, vec3 sPos)
{
    //*
    //use the closer solution if ray is outside sphere, farther if inside
    vec3 sDelta = sPos - rIn.src; // delta
    float sDelta2 = dot(sDelta, sDelta);

    float sRayClosest = dot(sDelta, rIn.dir); // x
    float sRad2 = sRad * sRad;

    //check if ray is outside
    bool above = sRad2 <= sDelta2; // true if outside
    bool facing = 0.0 <= sRayClosest;

    //catch if ray is on surface, and facing surface
    const float epsilon = 1e-4; // 1e-4 is really high for an epsilon
    if(abs(sRad2 - sDelta2) < epsilon)
    {
        if(dot(normIn, rIn.dir) < 0.0) // if ray points away from the surface it came from
            return;
        //adjust which side the ray lies on
        above = dot(normIn, sDelta) < 0.0;
        //facing = !above; // redundant?
    }

    //catch if ray faces away, and is outside
    if(above && !facing)
        return;

    //ray can miss if r^2 < s^2
    //find s
    float sRayClosest2 = sRayClosest * sRayClosest;
    float s2 = sDelta2 - sRayClosest2;

    //catch if r^2 < s^2, ray misses sphere
    if(sRad2 < s2) // check for outside?
        return;

    //ray should always hit
    //find offset
    float offset = sqrt(sRad2 - s2);
    if(above) // use closer solution
        offset = -offset;

    float rDist = sRayClosest + offset;
    if(rDist < rayDist && 0.0 <= rDist)
    {
        hit.rayDist = rayDist = rDist;
        hit.ray.src = rIn.src + rIn.dir * rDist;
        hit.normal = normalize(hit.ray.src - sPos);
        if(!above)
            hit.normal = -hit.normal;

        hit.id = id;
    }

    //*/
    /*
    vec3 sOffset = sPos - rIn.src; // delta
    //ray points toward sphere
    if(0.0 < dot(sOffset, rIn.dir))
    {
        float sRayClosest = dot(sOffset, rIn.dir);
        float sRayClosestDist2 = dot(sOffset, sOffset) - sRayClosest*sRayClosest;
        float sInnerOffset2 = sRad * sRad - sRayClosestDist2;
        if(0.0 < sInnerOffset2)
