                hit.normal = normalize(hit.ray.src - sPos);
                //hit.normal /= sqrt(dot(hit.normal, hit.normal));
                hit.id = id;
            }
        }
    }
    //*/
}

void tvPlane(Ray rIn, out tvHit hit, inout float rayDist, int id, vec3 normal, float off)
{
    //test if ray is above surface
    float side = dot(rIn.src, normal);
    bool above = off < side;

    //test if ray points toward surface
    float proj = dot(rIn.dir, normal); //not the adjusted normal
    bool facing = proj < 0.0;

    if(above == facing)
    {
        Ray rDst = rIn;
        rDst.src = rIn.src + rIn.dir * ((off - side) / proj);

        vec3 diff = rDst.src - rIn.src;
        float dist = length(diff); //sqrt(dot(diff, diff));

        if(dist < rayDist)
        {
            hit.rayDist = rayDist = dist;
            hit.ray = rDst;
            hit.normal = normal;
            hit.id = id;

            if(!above)
                hit.normal = -normal;
        }
    }
}

bool pointInTri(Tri tri, vec3 point, out vec2 coords)
{
    //v1 and v2 form a basis space, whose linear combination is vt. vt = a*v1 + b*v2.
    //Since vt is guaranteed to be on the plane between the two, its psuedoinverse is its inverse.
    //moore-penrose psuedoinverse: (MT*M)^-1 * MT = M+

    //move space toward first vertex
    //maybe use a mul?
    mat3 verts = mat3(tri.vertex[0], tri.vertex[1], tri.vertex[2]);
    verts -= mat3(vec3(0.0), tri.vertex[0], tri.vertex[0]);

    mat2x3 space = mat2x3(verts[1], verts[2]); // can this be truncated from tri?
    mat3x2 st = transpose(space);
    mat2x2 inv = st * space;

    //if the inverse's inputs has no determinant, it covers no area, and has no inverse.
    if(determinant(inv) == 0.0)
        return false;

    mat3x2 mpi = inverse(inv) * st;
    vec2 mpipt = mpi * (point - verts[0]); // move into basis space

    //if the point does not lie within the unit triangle, it misses.
    if(mpipt.x < 0.0)
        return false;
    if(mpipt.y < 0.0)
        return false;
    if(1.0 < dot(mpipt, vec2(1,1)))
       return false;

    coords = mpipt;
    return true;
}

