float brdfLambert(tvHit Hit, Ray rOut)
{
    float diffuse = dot(Hit.normal, rOut.dir) / 3.141592;
    diffuse = clamp(diffuse, 0.0, 1.0);
    return diffuse;
}

float brdfPhongSpecular(tvHit Hit, Ray rIn, Ray rOut, float power)
{
    vec3 refl = reflect(rIn.dir, Hit.normal);

    float specular = dot(rOut.dir, refl);
    specular = clamp(specular, 0.0, 1.0);

    specular = pow(specular, power);
    specular *= (power + 2.0) / (2.0 * 3.141592);
  	return specular;
}

vec3 brdfPhong(tvHit Hit, Ray rIn, Ray rOut, Material Mat)
{
    vec3 albedo = Mat.albedo;
    vec3 reflectivity = Mat.reflectivity;
    float power = Mat.power;

    float diffuse = brdfLambert(Hit, rOut);
    float specular = brdfPhongSpecular(Hit, rIn, rOut, power);

    vec3 brdf = (1.0 - reflectivity) * albedo*diffuse + reflectivity * specular;

    return brdf;
}

float bsdfUniform(tvHit Hit, Ray rOut)
{
    return 1.0 / (4.0 * 3.141592);
}

float bsdfGeometric(tvHit Hit, Ray rOut)
{
    //Cardioid!
    //uses ray.dir instead of normal
    float cosp = -dot(Hit.ray.dir, rOut.dir);

    //this can produce NaNs
    //cosp out of bounds?
    //if(sinp != sinp)
    //    return 0.0;
    cosp = clamp(cosp, -1.0, 1.0);

    float sinp = sin(acos(cosp)/2.0);

    return sinp / (8.0 * 3.141592 / 3.0);

    //geometric sampling is acting strange
    //return bsdfUniform(Hit, rOut);

    float normalizer = 1.0 / (4.0 * 3.141592);
}

float bsdfRayleigh(tvHit Hit, Ray rOut)
{
    //Spherical harmonics?
    float cosp = -dot(Hit.ray.dir, rOut.dir);

    float bsdf = 1.0 + cosp*cosp;

    return bsdf / (16.0 * 3.141592 / 3.0);
}

float bvtfHomogenous(float fDist, float fpdf) // bidirectional volume transmission function
{
    float absorbtion = Materials[1].albedo.x;
    if(absorbtion == 0.0) // no fog
        return 1.0;

    fpdf += 1e-31;

    float ret = absorbtion*exp(-absorbtion * fDist) / fpdf;

    if(ret != ret)
        return 0.0;
    return ret;
}

vec3 sampleMercator(tvHit Hit, out float pdf)
{
  	pdf = 1.0;
    vec3 rOut = randMercator(pdf);

    if(dot(rOut, Hit.normal) < 0.0)
        rOut = reflect(rOut, Hit.normal);

    pdf = 1.0 / pdf; // numerator pdf when multiplied

    //should these be packed into the sampler?
    pdf /= (2.0 * 3.141592) * (3.141592 / 2.0); // sample space pdf: theta, psi

    return rOut;
}

vec3 sampleSphere(tvHit Hit, out float pdf)
{
  	pdf = 1.0;
    vec3 rOut = randSphere();

    if(dot(rOut, Hit.normal) < 0.0)
        rOut = reflect(rOut, Hit.normal);

    //should these be packed into the sampler?
    //float pdf = 1.0; // no numerator pdf
    pdf /= 2.0 * 3.141592; // 4pi r^2 / 2, half a sphere's area (clamped)

    return rOut;
}
float pdfSphere(tvHit Hit, Ray rOut)
{
    return 1.0 / (2.0 * 3.141592);
}


vec3 sampleLambert(tvHit Hit, out float pdf)
{
  	//pdf = 1.0; // no pdf at all, due to importance sampling
    //this is absolutely wrong, the pdf is equal to the brdf so they cancel
    //however, when the pdf is zero it should return 0 juuuust in case haha

    //if(0.0 < dot(Hit.normal, Hit.ray.dir))
    //    Hit.normal *= -1.0;

    //Hit.normal *= -1.0;

    Ray ray;
    ray.dir = randLambert(Hit.normal);

    pdf = brdfLambert(Hit, ray);

    return ray.dir;
}

float pdfLambert(tvHit Hit, Ray rOut)
{
    return brdfLambert(Hit, rOut);
}

vec3 samplePhongSpec(tvHit Hit, out float pdf, Ray rIn, float power)
{
    Ray ray;

    ray.dir = randPhongSpec(Hit.normal, rIn.dir, power);
    pdf = brdfPhongSpecular(Hit, rIn, ray, power);

    return ray.dir;
}

float pdfPhongSpec(tvHit Hit, Ray rIn, Ray rOut, float power)
{
