    {
        ray.dir = sampleLambert(Hit, pdf);
    	//pdf *= 1.0 - refl;
    }
    else // specular
    {
        ray.dir = samplePhongSpec(Hit, pdf, rIn, power);
    	//pdf *= refl;
    }

