vec3 samplePhong(tvHit Hit, out float pdf, Ray rIn, Material mat)
{
    return samplePhongOld(Hit, pdf, rIn, mat.reflectivity, mat.power);
    float reflectivity = 0.25; // hardcoded
    Ray ray;

    //can this be done better by treating it as an MIS?
    float pdfLambert = 1.0;
    float pdfPhongSpec = 1.0;

    vec3 samLambert = sampleLambert(Hit, pdfLambert);
    vec3 samPhongSp = samplePhongSpec(Hit, pdfPhongSpec, rIn, mat.power);

    float misWeight = (1.0 - reflectivity)*pdfLambert + reflectivity*pdfPhongSpec;
    float wLambert = pdfLambert / misWeight + 1e-10;
    float wPhongSpec = pdfPhongSpec / misWeight + 1e-10;

    if(wLambert < rnd()) // diffuse
    {
        pdf = pdfLambert * wLambert;
        ray.dir = samLambert;
    }
    else // specular
    {
        pdf = pdfPhongSpec * wPhongSpec;
        ray.dir = samPhongSp;
    }

    return ray.dir;
}

float pdfPhong(tvHit Hit, Ray rIn, Ray rOut, Material mat)
{
    float diffuse = pdfLambert(Hit, rOut);
    return diffuse;

    //if(diffuse == 0.0)
    //    return 0.0;
    float specular = pdfPhongSpec(Hit, rIn, rOut, mat.power);
    float refl = dot(mat.reflectivity, vec3(1,1,1)) / 3.0;
    return refl*specular + (1.0-refl)*diffuse;
}

vec3 sampleLightSingular(tvHit Hit, out float pdf, out vec3 samPos, vec3 sPos, float sRad)
{
    //selects a random point on the light to raytrace towards
    vec3 rPos = randSphere();
    vec3 rNorm = rPos;

    //finds the vector from the ray source to this sample
    rPos *= sRad;
    rPos += sPos;

    samPos = rPos; // useful for fog sampling

    rPos -= Hit.ray.src;

    //pdf is the probability area projected toward from point
    float r2 = dot(rPos, rPos);
    vec3 rOut = rPos / sqrt(r2);

    float area = 2.0 * 3.141592 * sRad * sRad; // hemisphere? area
    area *= abs(dot(rNorm, rOut)); // projected area
    pdf = r2 / area; // 1/area, in case r2 is near zero it works better here

    /*
    //repeat for the sample on the other side of the sphere, to find the complete pdf
    rNorm = -reflect(rNorm, rOut); // other side
    rPos = rNorm * sRad;
    rPos += sPos;
    rPos -= Hit.ray.src;
    r2 = dot(rPos, rPos);
    area = 4.0 * 3.141592 * sRad * sRad;
    area *= abs(dot(rNorm, rOut));
    pdf += r2 / area;
    */

    if(pdf != pdf) // if r2 and the dot are both 0, pdf becomes NaN.
        pdf = 0.0; // 0/0 == 0 haha

    return rOut;
}

float pdfLightSingular(tvHit Hit, Ray rOut, vec3 sPos, float sRad)
{
    vec3 delta = sPos - rOut.src;
    vec3 ndelta = normalize(delta);
    float delta2 = dot(delta, delta);
    float r2 = sRad * sRad;
    float proj = dot(ndelta, rOut.dir);

    if(proj < 0.0) // sphere is below ray
        return 0.0;

    if(delta2 < r2) // ray is within sphere
        return 0.0; // harder to code

    if(proj * proj <= (1.0 - (r2 / delta2))) // if the ray misses the sphere
        return 0.0;

    //find intersection
    float mdelta = sqrt(delta2);
    float mmid = dot(delta, rOut.dir); // proj * mdelta // length of line that makes a right angle to the sphere
    float mmid2 = mmid * mmid;
    float mdev2 = r2 + mmid2 - delta2;
    if(mdev2 < 0.0)
        return 0.0;

    //mdev is the little length difference between mid and dist
    float mdev = sqrt(mdev2);

    float dist1 = mmid - mdev; // is this a complex root?
    float dist2 = mmid + mdev; // a sqrt's involved, and there's two conjugates

    vec3 sHit1 = dist1 * rOut.dir + rOut.src; // oh shit
    vec3 sHit2 = dist2 * rOut.dir + rOut.src;

    vec3 n1 = normalize(sHit1 - sPos);
    vec3 n2 = normalize(sHit2 - sPos);

    //find projected areas
    float pdf = 0.0;
	float SA = 4.0 * 3.141592 * r2;

	float A1 = SA * abs(dot(n1, rOut.dir));
	float A2 = SA * abs(dot(n2, rOut.dir));

	// area facing the ray, over the distance ^2
	float pdf1 = (dist1 * dist1) / A1;
	float pdf2 = (dist2 * dist2) / A2;

    pdf = pdf1 + pdf2;

    if(pdf != pdf) // if r2 and the dot are both 0, pdf becomes NaN.
        pdf = 0.0; // 0/0 == 0 haha

    return pdf;
}

vec3 sampleTri(tvHit Hit, out float pdf, out vec3 samPos, Tri tri)
{
    vec2 uv = randTri();

    //build basis space
    mat3 space = mat3(tri.vertex[0], tri.vertex[1], tri.vertex[2]);
    space -= mat3(vec3(0.0), tri.vertex[0], tri.vertex[0]);

    //lerp into real space
    vec3 rPos = space * vec3(1.0, uv);

    samPos = rPos; // useful for fog sampling

    rPos -= Hit.ray.src;

    //pdf is the probability area projected toward from point
    float r2 = dot(rPos, rPos);
    vec3 rOut = rPos / sqrt(r2);

    // there's gotta be an easier way to find this
    float area = length(cross(space[1], space[2])) / 2.0; 

    area *= abs(dot(tri.plane.normal, rOut)); // projected area
    pdf = r2 / area; // 1/area, in case r2 is near zero it works better here

    return rOut;
}

float pdfTri(tvHit Hit, Ray rOut, Tri tri)
{
    //find if rOut hits the tri
    tvHit hit;
    hit.ray = rOut;
    hit.normal = vec3(0,0,0);
    hit.id = 0; // id of the sky
    float rayDist = 1.0e30;

    tvTri(rOut, hit, rayDist, 1, Tris[0]); // id doesn't really matter
    if(hit.id == 0)
        return 0.0; // miss

    //build basis space
    mat3 space = mat3(tri.vertex[0], tri.vertex[1], tri.vertex[2]);
    space -= mat3(vec3(0.0), tri.vertex[0], tri.vertex[0]);

    //pdf is the probability area projected toward from point
    float r2 = rayDist * rayDist;

    // there's gotta be an easier way to find this
    float area = length(cross(space[1], space[2])) / 2.0; 

    area *= abs(dot(tri.plane.normal, rOut.dir)); // projected area
    float pdf = r2 / area; // 1/area, in case r2 is near zero it works better here

    return pdf;
}

vec3 sampleLight(tvHit Hit, out float pdf, out vec3 samPos)
{
    vec3 Out;
    vec3 spos = vec3(0);

    //sample uniformly
    //float pick = rnd();
    //if(pick < 1.0 / 3.0)
    //    Out = sampleLightSingular(Hit, pdf, Spheres[0].position, Spheres[0].radius);
    //else if(pick < 2.0 / 3.0)
    //    Out = sampleLightSingular(Hit, pdf, Spheres[3].position, Spheres[3].radius);
    //else
    //    Out = sampleTri(Hit, pdf, Tris[0]);
    //pdf /= 3.0;

    //sample via a sample space
    float samples[4];
    float sample_sum = 0.0;

    int sn = 10; // 3
    int mn = sn + 4;

    //surface area
    samples[0] = 4.0 * 3.141592 * Spheres[0].radius * Spheres[0].radius;
    samples[1] = 4.0 * 3.141592 * Spheres[3].radius * Spheres[3].radius;
    samples[2] = 4.0 * 3.141592 * Spheres[sn].radius * Spheres[sn].radius;
    samples[3] = length(cross(Tris[0].vertex[1]-Tris[0].vertex[0], Tris[0].vertex[2]-Tris[0].vertex[1]));

    //luminous flux (intensity * area)
    sample_sum += samples[0] *= dot(vec3(1), Materials[4].emission);
    sample_sum += samples[1] *= dot(vec3(1), Materials[7].emission);
    sample_sum += samples[2] *= dot(vec3(1), Materials[mn].emission);
    sample_sum += samples[3] *= dot(vec3(1), Materials[15].emission);

    float pick = rnd();
    float samplespace = 0.0;
    int i=0;

    for(i=0; i<4; ++i)
    {
        samplespace += samples[i] / sample_sum;
        if(pick < samplespace)
            break;
    }

    if(i==0)
        Out = sampleLightSingular(Hit, pdf, samPos, Spheres[0].position, Spheres[0].radius);
    else if(i==1)
        Out = sampleLightSingular(Hit, pdf, samPos, Spheres[3].position, Spheres[3].radius);
    else if(i==2)
        Out = sampleLightSingular(Hit, pdf, samPos, Spheres[sn].position, Spheres[sn].radius);
    else
        Out = sampleTri(Hit, pdf, samPos, Tris[0]);

    pdf *= samples[i] / sample_sum;

    Ray rOut = Hit.ray;
    rOut.dir = Out;

    return Out;
}

