float pdfLight(tvHit Hit, Ray rOut) // this should be below sampleLight when its done
{
    float pdf0 = pdfLightSingular(Hit, rOut, Spheres[0].position, Spheres[0].radius);

    //return pdf0;
    //are multiple pdfs weighted averages, like in MIS?

    int sn = 10;
    int mn = sn + 4;

    float pdf3 = pdfLightSingular(Hit, rOut, Spheres[3].position, Spheres[3].radius);

    float pdfn = pdfLightSingular(Hit, rOut, Spheres[sn].position, Spheres[sn].radius);

    //return (pdf0 + pdf3) / 2.0;

    float pdfT = pdfTri(Hit, rOut, Tris[0]);

    //sample uniformly
    //return (pdf0 + pdf3 + pdfT) / 3.0;

    //sample via a sample space
    float samples[4];
    float sample_sum = 0.0;

    //surface area
    samples[0] = 4.0 * 3.141592 * Spheres[0].radius * Spheres[0].radius;
    samples[1] = 4.0 * 3.141592 * Spheres[3].radius * Spheres[3].radius;
    samples[2] = 4.0 * 3.141592 * Spheres[sn].radius * Spheres[sn].radius;
    samples[3] = length(cross(Tris[0].vertex[1]-Tris[0].vertex[0], Tris[0].vertex[2]-Tris[0].vertex[1]));

    //luminous flux (intensity * area)
    sample_sum += samples[0] *= dot(vec3(1), Materials[4].emission);
    sample_sum += samples[1] *= dot(vec3(1), Materials[7].emission);
    sample_sum += samples[2] *= dot(vec3(1), Materials[mn].emission);
    sample_sum += samples[3] *= dot(vec3(1), Materials[15].emission);

    float tpdf[4] = float[4](pdf0, pdf3, pdfn, pdfT);
    float pdf = 0.0;
    for(int i=0; i<4; ++i)
