float sampleFogDistance(Ray rIn, out float fpdf)
{
    float absorbtion = Materials[1].albedo.x;
    if(absorbtion == 0.0)
    {
        fpdf = 1.0;
        return 1.0e31;
    }

    float epsilon = rnd();
    float x = -log(epsilon) / absorbtion;

    //find pdf
    //fpdf = absorbtion * exp(-absorbtion * x); // epsilon
    //fpdf = exp(-absorbtion * x); // epsilon
    //fpdf = epsilon; // this absorbtion term vanishes somehow. 
    fpdf = absorbtion * epsilon; // Found it! This is because im using the integral, not the pdf in the bvtf.
    return x;
}

