float sampleFogEquiAngular(Ray rIn, out float fpdf)
{ // egsr2012_volume.pdf
    //pick a point on a light
    tvHit Hit;
    Hit.ray = rIn;

    float absorbtion = Materials[1].albedo.x;
    if(absorbtion == 0.0)
    {
        fpdf = 1.0;
        return 1.0e31;
    }

    float lpdf = 1.0;
    vec3 samPos = vec3(0);
    vec3 lHit = sampleLight(Hit, lpdf, samPos);

    //find angle to sample pos
    //find closest distance to sample
    vec3 delta = samPos - rIn.src;
    float mDelta = length(delta);
    vec3 ndelta = delta / mDelta;
    float x = dot(delta, rIn.dir);
    float theta = acos(x / mDelta);
    float D = mDelta * sin(theta);
    float a = -(3.141592 / 2.0 - theta);
    float b = 3.141592 / 2.0; // all fograys should go on forever

    //pick a point
    float xi = rnd();
    float t = xi*(b-a) + a;
    float dist = D * tan(t); // move ray onto line

    delta = rIn.src + dist*rIn.dir;

    //find pdf
    fpdf = D / (dot(delta, delta) * (b-a)); // this doesn't work?
    //fpdf = (b-a) / (dot(delta, delta) * D); // but this one does?
    //fpdf *= lpdf;

    return dist;

}

