        vec3 c_hsv = rgb2hsv(c);
        float adj = 0.8;
        c_hsv.r += adj; if(1.0 < c_hsv.r) c_hsv.r -= 1.0;
        if(c_hsv.r < 0.33) c_hsv.r += 0.33; // blue screen
