vec3 raytrace_fundamental(Ray rIn)
{
    tvHit Hit = traverse(rIn, rIn.dir, 0);

    Material Mat = matTexture(Hit);
    vec3 Out = Mat.emission;
    if(Hit.id == 0)
        return Out;

    Ray rOut = Hit.ray;
    float pdf = 1.0;
    vec3 brdf = vec3(0);

    if(Hit.id == 1)// fog
    {
        rOut.dir = sampleScatterUniform(Hit, pdf);
        brdf = vec3(1,1,0) * bsdfGeometric(Hit, rOut);
        brdf+= vec3(0,0,1) * bsdfRayleigh(Hit, rOut);
    }
    else
    {
        rOut.dir = sampleSphere(Hit, pdf);
        brdf = brdfPhong(Hit, rIn, rOut, Mat);
    }

    vec3 rtrace = raytrace2(rOut, Hit.normal, Hit.id);

    pdf += 1e-10; // corrects for a division by zero error

    return Out + rtrace * brdf / pdf; 
}

vec3 raytrace_fundamental_fog(Ray rIn)
{
    tvHit Hit = traverse(rIn, rIn.dir, 0);

    //fog, using tvFog in its current implementation, is a hardcoded importance sample for distance sampling.
    //This is why the following code has the if statement, its a binary operation.
    float fDist = Hit.rayDist;
    float fpdf = 1.0;

    //fDist = sampleFogDistance(rIn, fpdf);
    fDist = sampleFogEquiAngular(rIn, fpdf);
    if(fDist < Hit.rayDist)
    {
        Hit.rayDist = fDist;
        Hit.ray = rIn;
        Hit.ray.src = rIn.src + rIn.dir * fDist;
        Hit.normal = rIn.dir; // hit.normal isn't used in BSDFs, only in and out directions.
        Hit.id = 1;
    }

    float transmission = bvtfHomogenous(fDist, fpdf);

    Ray rOut = Hit.ray;
    float pdf = 1.0;
    vec3 brdf = vec3(0);

    Material Mat = matTexture(Hit);
    vec3 Out = Mat.emission;

    if(Hit.id == 0)
        return Out * transmission;

    if(Hit.id == 1)// fog
    {
        //fpdf = 1.0;
        rOut.dir = sampleScatterUniform(Hit, pdf);
        brdf = vec3(1,1,0) * bsdfGeometric(Hit, rOut);
        brdf+= vec3(0,0,1) * bsdfRayleigh(Hit, rOut);
    }
    else
    {
        //fpdf = 1.0;
        rOut.dir = sampleSphere(Hit, pdf);
        brdf = brdfPhong(Hit, rIn, rOut, Mat);
    }

    vec3 rtrace = raytrace2(rOut, Hit.normal, Hit.id);

    //pdf *= fpdf;
    pdf += 1e-10; // corrects for a division by zero error

    Out += rtrace * brdf / pdf;
    Out *= transmission;

    return Out; 
}


