        //brdf = vec3(1) * brdfPhongSpecular(Hit, rIn, rOut, Mat.power);
        //brdf *= Materials[Hit.id].reflectivity;
        //brdf = vec3(1) * brdfLambert(Hit, rOut);
        //brdf *= 1.0 - Materials[Hit.id].reflectivity;

        brdf = brdfPhong(Hit, rIn, rOut, Mat);

    }

    vec3 rtrace = raytrace2(rOut, Hit.normal, Hit.id);

    pdf += 1e-10; // corrects for a division by zero error

    // correct for pdf weirdness
    if(pdf < 2e-10)
    {
        pdf = 1.0e31;
    }

    Out += rtrace * brdf / pdf;
    return Out * transmission;
}

const int sa_BRDF = 4;
const int sa_BRDF_Spec = 4;
const int sa_Light = 4;
const int sa_Sphere = 4;

vec3 raytrace_multiple_importance(Ray rIn)
{
    tvHit Hit = traverse(rIn, rIn.dir, 0);

    Material Mat = matTexture(Hit);
    vec3 Out = Mat.emission;
    if(Hit.id == 0)
        return Out;

    Ray rOut = Hit.ray;
    float pdf = 1.0;

    //MIS algo:
    //collect samples from different sources
    //for each dissimilar method, collect pdfs from each to find weighted pdf
    //use wpdf instead for sum

    Ray rOutBRDF[sa_BRDF];
    float pdfBRDF[sa_BRDF];
    vec3 brdfBRDF[sa_BRDF];
    vec3 rtBRDF[sa_BRDF];

    Ray rOutBRDF_Spec[sa_BRDF_Spec];
    float pdfBRDF_Spec[sa_BRDF_Spec];
    vec3 brdfBRDF_Spec[sa_BRDF_Spec];
    vec3 rtBRDF_Spec[sa_BRDF_Spec];

    Ray rOutLight[sa_Light];
    float pdf_Light[sa_Light];
    vec3 brdfLight[sa_Light];
    vec3 rtLight[sa_Light];

    // get samples for BRDF
    for(int i=0; i<sa_BRDF; ++i)
    {
        rOutBRDF[i] = Hit.ray;
        pdfBRDF[i] = 1.0;

        //rOutBRDF[i].dir = samplePhong(Hit, pdfBRDF[i], rIn, Mat);
        rOutBRDF[i].dir = sampleLambert(Hit, pdfBRDF[i]);
        brdfBRDF[i] = brdfPhong(Hit, rIn, rOutBRDF[i], Mat);
        rtBRDF[i] = raytrace2(rOutBRDF[i], Hit.normal, Hit.id);
    }

    // get samples for BRDF Specular
    for(int i=0; i<sa_BRDF_Spec; ++i)
    {
        rOutBRDF_Spec[i] = Hit.ray;
        pdfBRDF_Spec[i] = 1.0;

        rOutBRDF_Spec[i].dir = samplePhongSpec(Hit, pdfBRDF_Spec[i], rIn, Mat.power);
        brdfBRDF_Spec[i] = brdfPhong(Hit, rIn, rOutBRDF_Spec[i], Mat);
        rtBRDF_Spec[i] = raytrace2(rOutBRDF_Spec[i], Hit.normal, Hit.id);
    }

    // get samples for Light
    for(int i=0; i<sa_Light; ++i)
    {
        rOutLight[i] = Hit.ray;
        pdf_Light[i] = 1.0;

        rOutLight[i].dir = sampleLight(Hit, pdf_Light[i]);
        brdfLight[i] = brdfPhong(Hit, rIn, rOutLight[i], Mat);
        rtLight[i] = raytrace2(rOutLight[i], Hit.normal, Hit.id);
    }

    //accumulate MIS pass
    vec3 MIS = vec3(0,0,0);
    float sum_samples = float(sa_BRDF + sa_Light);

    //accumulate over BRDF
    for(int i=0; i<sa_BRDF; ++i)
    {
        //find wpdf
        float wpdf = 0.0;

        //this pdf was already precomputed
        wpdf += float(sa_BRDF) * pdfBRDF[i];

        //compute other methods
        wpdf += float(sa_BRDF_Spec) * pdfPhongSpec(Hit, rIn, rOutBRDF[i], Mat.power);
        wpdf += float(sa_Light) * pdfLight(Hit, rOutBRDF[i]); // this right here is the problem
        //wpdf += float(sa_Light) / (2.0 * 3.141592);

        wpdf += 1e-10; // corrects for a division by zero error

        // correct for pdf weirdness
        if(wpdf < 2e-10)
        {
            wpdf = 1.0e31;
        }

        //sample * brdf / weighted pdf
        MIS += rtBRDF[i] * brdfBRDF[i] / wpdf;
    }

    //accumulate over BRDF Specular
    for(int i=0; i<sa_BRDF_Spec; ++i)
    {
        //find wpdf
        float wpdf = 0.0;

        //this pdf was already precomputed
        wpdf += float(sa_BRDF_Spec) * pdfBRDF_Spec[i];

        //compute other methods
        wpdf += float(sa_BRDF) * pdfLambert(Hit, rOutBRDF_Spec[i]);
        wpdf += float(sa_Light) * pdfLight(Hit, rOutBRDF_Spec[i]);

        wpdf += 1e-10; // corrects for a division by zero error

        // correct for pdf weirdness
        if(wpdf < 2e-10)
        {
            wpdf = 1.0e31;
        }

        //sample * brdf / weighted pdf
        MIS += rtBRDF_Spec[i] * brdfBRDF_Spec[i] / wpdf;
    }


    //accumulate over Light
    for(int i=0; i<sa_Light; ++i)
    {
        //find wpdf
        float wpdf = 0.0;

        //this pdf was already precomputed
        wpdf += float(sa_Light) * pdf_Light[i];

        //compute other methods
        wpdf += float(sa_BRDF) * pdfLambert(Hit, rOutLight[i]);
        wpdf += float(sa_BRDF_Spec) * pdfPhongSpec(Hit, rIn, rOutLight[i], Mat.power);

        wpdf += 1e-10; // corrects for a division by zero error

        // correct for pdf weirdness
        if(wpdf < 2e-10)
        {
            wpdf = 1.0e31;
        }

        //sample * brdf / weighted pdf
        MIS += rtLight[i] * brdfLight[i] / wpdf;
    }


    return Out + MIS; 
}

vec3 sample_MIS(tvHit Hit, Material Mat, out float pdf, int mode)
{
    Ray rOut = Hit.ray;
    pdf = 1.0;

    //MIS one sample algo:
    //same as MIS, but you randomly select the method, according to method space

    //build method space
    const int methodCount = 4;
    const float f_mC = float(methodCount);
    float c_Sphere = 1.0 / 4.0;
    float c_Lambert = 1.0 / 4.0;
    float c_Specular = 1.0 / 4.0;
    float c_Light = 1.0 / 4.0;

    float refl = dot(vec3(1), Mat.reflectivity) / 3.0;

    //bias toward more smooth samples, but still stick to 0 or 1 in edge cases
    //refl = 2.0*refl - 1.0;
    //refl *= refl*refl;
    //refl = (refl + 1.0) / 2.0;

    c_Lambert = (1.0 - refl) * (2.0 / f_mC);
    c_Specular = refl * (2.0 / f_mC);

    //c_Sphere =   0.5 / 3.0;
    //c_Lambert =  0.5 / 3.0;
    c_Sphere =   0.0 / 3.0;
    c_Lambert =  1.0 / 3.0;
    c_Specular = 1.0 / 3.0;
    c_Light =    1.0 / 3.0;

    float methodSpace[methodCount];
        methodSpace[0] = c_Sphere; methodSpace[1] = c_Lambert;
        methodSpace[2] = c_Specular; methodSpace[3] = c_Light;

    //pick a method from methodspace
    int c = 0;
    float pick = rnd();
    float methodSum = 0.0;

    if(mode < 0)
    {
        for(c=0; c<methodCount; ++c)
        {
            methodSum += methodSpace[c];
            if(methodSum > pick)
                break;
        }
    }
    else
    {
        c = mode;
        for(int i=0; i<methodCount; ++i)
            methodSpace[c] = 1.0 / float(methodCount);
    }

    //find a sample from the chosen method
    if(c == 0)
        rOut.dir = sampleSphere(Hit, pdf);
    if(c == 1)
        rOut.dir = sampleLambert(Hit, pdf);
    else if(c == 2)
        rOut.dir = samplePhongSpec(Hit, pdf, Hit.ray, Mat.power);
    else //if(c == 3)
        rOut.dir = sampleLight(Hit, pdf);


    //Seperating the resulting samples, but still using weighted PDFs are useful for denoising, 
    //    while retaining MIS's variance reduction effects. Probably.
    //if(rnd() < 0.5)
    //rOut.dir = sampleLambert(Hit, pdf);
    //else
    //rOut.dir = sampleLight(Hit, pdf);
    //rOut.dir = samplePhongSpec(Hit, pdf, Hit.ray, Materials[Hit.id].power);

    //fill wpdf using other methods
    //sum(c * pdf(x)) / c

    //this pdf was already precomputed
    pdf *= methodSpace[c]; 

    for(int i=0; i<methodCount-1; ++i)
    {
        int j = (i+c+1) % methodCount; // skips the precomputed method

        if(j == 0)
            pdf += methodSpace[j] * pdfSphere(Hit, rOut);
        else if(j == 1)
            pdf += methodSpace[j] * pdfLambert(Hit, rOut);
        else if(j == 2)
            pdf += methodSpace[j] * pdfPhongSpec(Hit, Hit.ray, rOut, Mat.power);
        else //if(j == 3)
            pdf += methodSpace[j] * pdfLight(Hit, rOut);
    }

    //end of line
    return rOut.dir;
}

vec3 sample_MIS(tvHit Hit, Material Mat, out float pdf)
{
    return sample_MIS(Hit, Mat, pdf, -1);
}

vec3 sample_fog_MIS(tvHit Hit, out float pdf)
{
    Ray rOut = Hit.ray;
    pdf = 1.0;

    //MIS one sample algo:
    //same as MIS, but you randomly select the method, according to method space

    //build method space
    float c_Uniform = 1.0 / 3.0;
    float c_Geometric = 1.0 / 3.0;
    float c_Light = 1.0 / 3.0;

    float methodSpace[3]; methodSpace[0] = c_Uniform; methodSpace[1] = c_Geometric; methodSpace[2] = c_Light;

    //pick a method from methodspace
    int c = 0;
    float pick = rnd();
    float methodSum = 0.0;

    for(c=0; c<3; ++c)
    {
        methodSum += methodSpace[c];
        if(methodSum > pick)
            break;
    }

    //find a sample from the chosen method
    if(c == 0)
        rOut.dir = sampleScatterUniform(Hit, pdf);
    else if(c == 1)
        rOut.dir = sampleScatterGeometric(Hit, pdf);
    else //if(c == 1)
        rOut.dir = sampleLight(Hit, pdf);

    //this pdf was already precomputed
    pdf *= methodSpace[c]; 

    for(int i=0; i<3-1; ++i)
    {
        int j = (i+c+1) % 3; // skips the precomputed method

        if(j == 0)
            pdf += methodSpace[j] * pdfScatterUniform(Hit, rOut);
        else if(j == 1)
            pdf += methodSpace[j] * pdfScatterGeometric(Hit, rOut);
        else //if(j == 2)
            pdf += methodSpace[j] * pdfLight(Hit, rOut); // use a special one for fog?
    }

    //end of line
    return rOut.dir;
}

float sample_fog_depth_MIS(Ray rIn, out float fpdf)
{
    float depth = 1.0e31;
    fpdf = 1.0;

    //MIS one sample algo:
    //same as MIS, but you randomly select the method, according to method space

    //build method space
    float c_Depth = 1.0 / 2.0;
    float c_EquiAngular = 1.0 / 2.0;

    float methodSpace[3]; methodSpace[0] = c_Depth; methodSpace[1] = c_EquiAngular;

    //pick a method from methodspace
    int c = 0;
    float pick = rnd();
    float methodSum = 0.0;

    for(c=0; c<2; ++c)
    {
        methodSum += methodSpace[c];
        if(methodSum > pick)
            break;
    }

    //find a sample from the chosen method
    if(c == 0)
        depth = sampleFogDistance(rIn, fpdf);
    else // if(c == 1)
        depth = sampleFogEquiAngular(rIn, fpdf);

    //this pdf was already precomputed
    fpdf *= methodSpace[c]; 

    for(int i=0; i<2-1; ++i)
    {
        int j = (i+c+1) % 2; // skips the precomputed method

        if(j == 0)
            fpdf += methodSpace[j] * pdfFogDistance(depth);
        else //if(j == 1)
            fpdf += methodSpace[j] * pdfFogEquiAngular(depth);
    }

    //end of line
    return depth;
}

vec3 raytrace_MIS_sample(Ray rIn)
{
    tvHit Hit = traverse(rIn, rIn.dir, 0);

    float fDist = Hit.rayDist;
    float fpdf = 1.0;

    //fDist = sampleFogDistance(rIn, fpdf);
    //fDist = sampleFogEquiAngular(rIn, fpdf);
    fDist = sample_fog_depth_MIS(rIn, fpdf);
    if(fDist < Hit.rayDist)
    {
        float t = bvtfHomogenous(fDist, fpdf);
        if(rnd() < t)
        {
            Hit.rayDist = fDist;
            Hit.ray = rIn;
            Hit.ray.src = rIn.src + rIn.dir * fDist;
            Hit.normal = rIn.dir; // hit.normal isn't used in BSDFs, only in and out directions.
            Hit.id = 1;
        }
    }

    float transmission = bvtfHomogenous(fDist, fpdf);

    Material Mat = matTexture(Hit);
    vec3 Out = Mat.emission;
    if(Hit.id == 0)
        return Out * transmission;

    Ray rOut = Hit.ray;
    float pdf = 1.0;
    vec3 brdf = vec3(0);

    vec3 accum = vec3(0);
    for(int i = 0; i<4; ++i) // deterministic mode, move some samples to specific methods
    {

    if(Hit.id == 1)// fog
    {
        //rOut.dir = sampleScatterUniform(Hit, pdf);
        //rOut.dir = sampleScatterGeometric(Hit, pdf);
        rOut.dir = sample_fog_MIS(Hit, pdf);
        brdf = vec3(1,1,0) * bsdfGeometric(Hit, rOut);
        brdf+= vec3(0,0,1) * bsdfRayleigh(Hit, rOut);
        //pdf *= 1.0-exp(-Hit.rayDist); // fog experiment
    }
    else
    {
        //rOut.dir = sampleSphere(Hit, pdf);
        //rOut.dir = sample_MIS(Hit, Mat, pdf);
        rOut.dir = sample_MIS(Hit, Mat, pdf, int(float(i)/1.0));
        brdf = brdfPhong(Hit, rIn, rOut, Mat);
        //pdf *= exp(-Hit.rayDist);
    }

    vec3 rtrace = raytrace2(rOut, Hit.normal, Hit.id);

    pdf += 1e-10; // corrects for a division by zero error

    // correct for pdf weirdness
    if(pdf < 2e-10)
    {
        pdf = 1.0e31;
    }

    vec3 res = Out; // emission
    res += rtrace * brdf / pdf;
    res *= transmission;

    accum += res;

    //accum += rtrace * brdf / pdf;

    }
    Out = accum / 4.0;
    //Out += accum / 4.0;
    //Out += rtrace * brdf / pdf;

    return Out;
}

vec3 Refract(vec3 rIn, vec3 normal, float eta)
{
    vec3 rOut = refract(rIn, normal, eta);
    if(rOut == vec3(0))
        return reflect(rIn, normal);
    return rOut;
}

vec3 raytracePath(Ray rIn)
{
	//raytracing with bounces, using a for loop instead of recusion
    //const int maxBounces = 4;
    const int maxBounces = 4;
    int bounces = 0;

    vec3 emissive[maxBounces + 1];
    vec3 brdf[maxBounces + 1];

    tvHit Hit;
