    for(bounces=0; bounces < maxBounces + 1; ++bounces)
    {
        Hit = traverse(ray, Hit.normal, Hit.id);

        emissive[bounces] = vec3(0,0,0);

        Material Mat = matTexture(Hit);
        emissive[bounces] = Mat.emission;
        if(Hit.id == 0)
            break;

        //if(Hit.id == 3)
        //    emissive[bounces] = vec3(0,1,0) / (0.25*0.25);

        Ray rold = ray;
        ray = Hit.ray;
        float pdf = 1.0;
        /*
        bool shiny = 0.5 < rnd();
        float albedo = 1.0;

        if(Hit.id == 1 && !shiny)
        	if(sin(Hit.ray.src.x * 3.141592) * sin(Hit.ray.src.y * 3.141592) < 0.0)
                albedo = 0.0;

        //if(Hit.id == 1 || shiny)//Hit.id == 2 || Hit.id == 3)
       	if(false && shiny && (Hit.id == 2 || Hit.id == 3))
        {
        	pdf = 1.0 / (2.0 * 3.141592);
            ray.dir = reflect(rold.dir, Hit.normal);
        }
        else
        */
        //if(bounces + 1 == maxBounces)
       	//	ray.dir = sampleLight(Hit, pdf);
        //else

        if(Hit.id == 1)// fog
        {
            //rOut.dir = sampleScatterUniform(Hit, pdf);
            //brdf = vec3(1,1,1) * bsdfUniform(Hit, rOut);

            //ray.dir = sampleScatterGeometric(Hit, pdf);
            ray.dir = sample_fog_MIS(Hit, pdf);
            //brdf[bounces] = vec3(1,1,1) * bsdfGeometric(Hit, ray);
            //brdf[bounces] = vec3(1,1,1) * bsdfRayleigh(Hit, ray);
            brdf[bounces] = vec3(1,1,0) * bsdfGeometric(Hit, ray);
            brdf[bounces]+= vec3(0,0,1) * bsdfRayleigh(Hit, ray);
        }
        else if(4 <= Hit.id && Hit.id <= 14) // triangle
        {
            pdf = 1.0;
            //ray.dir = reflect(ray.dir, Hit.normal);
            ray.dir = Refract(ray.dir, Hit.normal, ior);
            ior = 1.0 / ior;
            //ray.dir = reflect(ray.dir, Hit.normal);
            Hit.normal = -Hit.normal;

            brdf[bounces] = vec3(1);//brdfPhong(Hit, rold, ray, Mat);


        }
        else
        {
            //ray.dir = sampleLambert(Hit, pdf);
        	//ray.dir = samplePhong(Hit, pdf, rIn, Materials[Hit.id]);
        	//ray.dir = sampleLight(Hit, pdf);
        	//ray.dir = sampleMercator(Hit, pdf);
        	//ray.dir = sampleSphere(Hit, pdf);
            ray.dir = sample_MIS(Hit, Mat, pdf);

        	//brdf[bounces] = vec3(1,1,1) * brdfLambert(Hit, ray);
        	brdf[bounces] = brdfPhong(Hit, rold, ray, Mat);
        }

        if(pdf != pdf)
            pdf = 1e-30;

        if(brdf[bounces] != brdf[bounces])
            return vec3(1,0,0);

        if(1e-8 < pdf)
            brdf[bounces] /= pdf;
        else
            brdf[bounces] = vec3(0,0,0);

        if(brdf[bounces] == vec3(0,0,0))
            break;

        //brdf[bounces] = albedo;

    }

    //bounces--;
    vec3 col = emissive[bounces];
    bounces--;

    for(; 0<=bounces; --bounces)
        col = emissive[bounces] + brdf[bounces] * col;

    return col;
}

