void initScene(in float rTime, in vec2 uv)
{

    //rTime += (rnd() - 0.5) / 60.0; //const motion blur
    rTime += (rnd() - 0.5) * iTimeDelta; // full frame adaptive motion blur
    //rTime += (rnd() - 0.5) * iTimeDelta / 2.0; // half frame adaptive motion blur // this is how cameras work!

    //setup materials
    //sky
    Materials[0].emission = vec3(0); // sky and fog only emit

    //fog
    Materials[1].emission = vec3(0);

    //I'm not a fan of this formulation, I should redo it at some point
    float f_n = 1000.0; //particle density
    float f_r = 0.0005; //particle radius, 0.5mm
    float f_c_vol = 1.0; //control volume, 1 cubic meter

    float f_crs_c_vol = pow(f_c_vol, 2.0 / 3.0); // v^(2/3)
    float f_p_area = 3.141592 * f_r*f_r;
    float f_denom = 1.0 - f_p_area / f_crs_c_vol;
    float f_absorbtion = -log(f_denom) * f_n * f_crs_c_vol;

    //f_absorbtion *= 1000.0;
    f_absorbtion *= 100.0;
    //f_absorbtion *= (0.5 + 0.5*sin(iTime));
    f_absorbtion = 0.0;

    //f_absorbtion *= 2000.0;

    //f_absorbtion *= 10.0;

    Materials[1].albedo = vec3(f_absorbtion); // repurpose

    ////////////
    // planes //
    ////////////

    //plane 0
    Materials[2].emission     = vec3(0,0,0);
    Materials[2].albedo       = vec3(1,1,1);
    Materials[2].reflectivity = vec3(1,1,1) * 0.25;// * 4.0 * 0.99;
    Materials[2].power        =               1000.0; // 1000.0;
    //Materials[2].power        =               pow(10.0, sin(iTime / 5.0) + 3.0); // 1000.0;
    //Materials[2].power        =               10000000.0; // 1000.0;
    //Materials[2].power        =               10.0; // 1000.0;
    //Materials[2].power        =               10000.0;
    //Materials[2].power = 100.0;

    //Materials[2].power = 1000.0;

    for(int i=2+1; i<MatNum; ++i)
        Materials[i] = Materials[2];

    //other plane
    Materials[3] = Materials[2];

    //Materials[3].emission = vec3(1,1,1) * 10000.0;

    //setup planes
    Planes[0].normal = vec3(0,0,1);
    Planes[0].offset = dot(Planes[0].normal, vec3(0,0,-1.0));
    Planes[1].normal = vec3(0,0,1);
    Planes[1].offset = dot(Planes[1].normal, vec3(0,0, 1.75)); // fog -8.0+1.75

    /////////////
    // spheres //
    /////////////

    //large sphere
    Materials[4] = Materials[2];
    Materials[4].emission     = vec3(1,1,1) * 1.0;// / 100.0;
    //Materials[4].power = 100000.0;

    //first sphere
    Materials[5] = Materials[4];
