    Spheres[1].radius = 0.25;
    Spheres[1].position = vec3(1.0,3.0,-1.0 + Spheres[1].radius);

    //other spheres
    float aTime = rTime + 4.1;

    for(int i=0; i<9; i++)
    {
        Spheres[2+i] = Spheres[1];
        Materials[4+2+i] = Materials[5];
        Spheres[2+i].position += vec3(0,i+1,0);
        Spheres[2+i].position += vec3(2.0 * 0.3*float(i+1)*cos(aTime + float(i) / 2.0), 0.0, 0.0);
    }

    Materials[7].emission = vec3(0,1,0) * 100.0;// / 1000.0;
    //Materials[14].emission = vec3(1,1,1) * 10000.0;// / 1000.0;
    //Spheres[3].position += vec3(2.0 * cos(iTime), 0.0, 0.0);

    float fTime = 0.0*rTime * 0.1 - 0.1;// + 3.141592;

    //Materials[14].emission = vec3(1,1,1) * 1000.0;//  10000.0;

    //Spheres[10].position.xy *= 2.0;
    //Materials[14].emission *= 4.0 * 10.0;

    //Spheres[10].radius /= 5.0;
    //Materials[14].emission *= 25.0;
    //Spheres[10].position.z *= -1.0;

    mat2x2 fogrotate = mat2x2(vec2(cos(fTime), sin(fTime)), vec2(-sin(fTime), cos(fTime)));
