    //////////
    // tris //
    //////////

    //setup tris
    Materials[15] = Materials[2];
    Materials[15].emission = vec3(0,0,1) * 10.0;// / 10.0;
    //Materials[15].emission = vec3(1) * 100.0; // caustic scene

    //setup tris
    float pTime = rTime;// + 4.1;
    float speed = 1.0 * 0.25; //0.2;
    float off = 0.33;

    pTime = off+speed*pTime;

    Tris[0].vertex[0] = vec3(3,4,1.0  + sin(pTime));
    Tris[0].vertex[1] = vec3(1,5,1.0 + sin(pTime + 2.0*3.141592 / 3.0));
    Tris[0].vertex[2] = vec3(4,7,1.0  + sin(pTime + 4.0*3.141592 / 3.0));
    Tris[0].plane.normal = normalize(cross(Tris[0].vertex[1]-Tris[0].vertex[0], Tris[0].vertex[2]-Tris[0].vertex[0]));
    Tris[0].plane.offset = dot(Tris[0].plane.normal, Tris[0].vertex[0]);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //seed = fract(iTime) + iResolution.y * uv.x + uv.y;
    //seed = iResolution.y * fragCoord.x / iResolution.x + fragCoord.y / iResolution.y;

    seed = hashrandom(iTime + iResolution.y * uv.x + uv.y);

    float rTime = iTime;// + 14.61;// + 2.0;
    //float rTime = iTime * 0.0 + 23.14;// + 2.0;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(rnd(), rnd(), rnd());

    initScene(rTime, uv);

    //render

    float x = fragCoord.x / iResolution.x;
    float y = fragCoord.y / iResolution.y;

    x = x * 2.0 - 1.0;
    y = y * 2.0 - 1.0;
    y *= iResolution.y / iResolution.x;

    Ray ray;
    col = vec3(0,0,0);
    const int samples = 4; // *4 mis subsamples
    for(int smp=0; smp<samples; ++smp)
    {
        seed += 1.0;
        float dx = 2.0 * rnd() - 1.0;
        float dy = 2.0 * rnd() - 1.0;

        dx /= iResolution.x;
        dy /= iResolution.x;

        //dx = 0.0;
        //dy = 0.0;

        ray.src = vec3(0,0,0);
        ray.dir = vec3(x + dx,1,y + dy);
        float cTime = rTime / 5.0;
        float cCos = cos(cTime);
        float cSin = sin(cTime);
        mat4x4 t1 = mat4x4(vec4(1,0,0,0),vec4(0,1,0,0),vec4(0,0,1,0),vec4(0,5,0,1));
        mat4x4 t2 = mat4x4(vec4(1,0,0,0),vec4(0,1,0,0),vec4(0,0,1,0),vec4(0,-5,0,1));
        mat4x4 rotate = mat4x4(vec4(cCos, cSin, 0,0), vec4(-cSin, cCos, 0,0), vec4(0,0,1,0), vec4(0,0,0,1));
        rotate = t1 * rotate * t2;
        ray.src = (rotate * vec4(ray.src, 1.0)).xyz;
        ray.dir = (rotate * vec4(ray.dir, 0.0)).xyz;

        ray.dir = normalize(ray.dir);
        //ray.dir /= sqrt(dot(ray.dir, ray.dir));

        vec3 clr = raytrace(ray, fragCoord / iResolution.xy);
        col += clr / float(samples);
    }

    //convert color space from lsrgb to srgb
    //good enough
    //col = sqrt(col);

    // Output to screen

    vec3 colacc = col;

    bool progressiveRendering = false;
    vec4 tprev = texture(iChannel0, fragCoord/iResolution.xy);
    int iFrameStart = int(tprev.w);
    if(0.5 < iMouse.z)
    {
        iFrameStart = iFrame;
     	colacc = col;   
    }
    if(progressiveRendering)
    {
        float spp1 = float(iFrame - iFrameStart);
        float spp2 = float(iFrame - iFrameStart + 1);
        vec3 colprev = tprev.xyz;
        colacc = (col / spp2) + (colprev * (spp1/spp2));
    }

    fragColor = vec4(colacc,float(iFrameStart)); // store start frame in alpha channel
}

