//color space transforms
float lsrgb2srgb(float lsrgb)
{
    if(lsrgb < 0.0031308)
        return lsrgb * 12.92;
    float a = 1.055;
    return a * pow(lsrgb, 1.0/2.4) - (a - 1.0);
}

vec3 lsrgb2srgb(vec3 lsrgb)
{
    return vec3(lsrgb2srgb(lsrgb.x),lsrgb2srgb(lsrgb.y),lsrgb2srgb(lsrgb.z));
}

float srgb2lsrgb(float srgb)
{
    if(srgb < 0.04045)
        return srgb / 12.92;
    float a = 1.055;
    return pow(1.0 + (srgb - 1.0) / a, 2.4);
}

vec3 srgb2lsrgb(vec3 srgb)
{
    return vec3(srgb2lsrgb(srgb.x),srgb2lsrgb(srgb.y),srgb2lsrgb(srgb.z));
}

//https://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
vec3 rgb2hsv(vec3 rgb)
{
	vec4 k = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(rgb.bg, k.wz), vec4(rgb.gb, k.xy), step(rgb.b, rgb.g));
    vec4 q = mix(vec4(p.xyw, rgb.r), vec4(rgb.r, p.yzx), step(p.x, rgb.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;

    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 hsv)
{
	vec4 k = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(hsv.xxx + k.xyz) * 6.0 - k.www);
    return hsv.z * mix(k.xxx, clamp(p - k.xxx, 0.0, 1.0), hsv.y);    
}

