// License CC0: Pulse team logo in neonwave
//  Created a team logo for my team at work in neon wave style. Thougnt it turned out ok so sharing it.
#define PI          3.141592654
#define TAU         (2.0*PI)
#define RESOLUTION  iResolution
#define TIME        iTime

#define SCA(a) vec2(sin(a), cos(a))

const vec2 sca0 = SCA(0.0);

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

float emin(float a, float b, float k) {
  float res = exp2( -k*a ) + exp2( -k*b );
  return -log2( res )/k;
}

float emax(float a, float b, float k) {
  return -emin(-a, -b, k);
}

float planex(vec2 p, float w) {
  return abs(p.y) - w;
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

float box(vec2 p, vec2 b) {
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float horseshoe(vec2 p, vec2 c, float r, vec2 w) {
  p.x = abs(p.x);
  float l = length(p);
  p = mat2(-c.x, c.y, c.y, c.x)*p;
  p = vec2((p.y>0.0)?p.x:l*sign(-c.x),(p.x>0.0)?p.y:l);
  p = vec2(p.x,abs(p.y-r))-w;
  return length(max(p,0.0)) + min(0.0,max(p.x,p.y));
}

float letterp(vec2 p) {
  float b = box(p - vec2(-0.45, -0.25), vec2(0.1, 0.75));
  float c = max(circle(p, 0.5), -circle(p, 0.3));
  return min(b, c);
}

float letteru(vec2 p) {
  return horseshoe(p - vec2(0.0, 0.125), sca0, 0.5, vec2(0.375, 0.1));
}

float letterl(vec2 p) {
  return box(p, vec2(0.125, 0.75));
}

float letters(vec2 p) {
  rot(p, -PI/6.0);
  rot(p, -PI/2.0);
  float u = horseshoe(p - vec2(-0.25*3.0/4.0, -0.125/2.0), sca0, 0.375, vec2(0.2, 0.1)) - 0.0;
  rot(p, PI);
  float l = horseshoe(p - vec2(-0.25*3.0/4.0, -0.125/2.0), sca0, 0.375, vec2(0.2, 0.1));
  return min(u,l);
}

float lettere(vec2 p) {
  return min(box(p, vec2(0.4, 0.1)), max(circle(p, 0.5), -circle(p, 0.3)));
}

float pulse(vec2 p) {
  p.x += 1.95;
  const float op = +0.10;
  const float ou = +1.25;
  const float ol = +2.10;
  const float os = +2.80;
  const float oe = +3.85;
  float dp = letterp(p - vec2(op, 0.0));
  float du = letteru(p - vec2(ou, 0.0));
  float dl = letterl(p - vec2(ol, 0.0));
  float ds = letters(p - vec2(os, 0.0));
  float de = lettere(p - vec2(oe, 0.0));
  float oo = 0.1;
  float dx = abs(p.y) - oo;
  dx = abs(dx) - oo*0.5;
  float d = 1000000.0;
  d = min(d, dp);
  d = min(d, du);
  d = min(d, dl);
  d = min(d, ds);
  d = min(d, de);
  d = max(d, -dx);
  return d;
}

float sun(vec2 p) {
  const float ch = 0.0125;
  vec2 sp = p;
  vec2 cp = p;
  mod1(cp.y, ch*6.0);
  float d0 = circle(sp, 0.5);
  float d1 = planex(cp, ch);
  float d2 = p.y+ch*3.0;
  float d = d0;
  d = pmax(d, -max(d1, d2), ch*2.0);
  return d;
}

float df(vec2 p) {
  const vec2 off = vec2(0.0, -10.0+0.5);
  const vec2 coff = vec2(0);
  const float si = 5.0;
  const float sc = 25.0;
  float di = pulse((p - off)/si)*si;
  float ds = sun(p/sc)*sc;
  float d = ds;
  d = emax(d, -(di - 0.5), 2.0+sin(0.5*TIME+p.x));
  d = min(d, di);
  return d;
}

vec3 postProcess(vec3 col, vec2 q)  {
  col = clamp(col,0.0,1.0);
  col=col*0.6+0.4*col*col*(3.0-2.0*col);
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

vec3 sound(vec2 q, vec3 baseCol) {
  // the sound texture is 512x2
  int tx = int(q.x*512.0);
  int ty = int(q.y*512.0);

  // first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
  const int dfft = 7;
  float fft  = 0.0; 
  for (int i = -dfft; i <= dfft; ++i) {
    fft += sqrt(texelFetch(iChannel0, ivec2(ty+i,0), 0).x); 
  }
  fft /= float(dfft)*2.0 + 1.0;

  // second row is the sound wave, one texel is one mono sample
  float wave = 0.0;
  const int dwave = 5;
  for (int i = -dwave; i <= dwave; ++i) {
    wave += texelFetch(iChannel0, ivec2(tx+i,1), 0).x; 
  }
  wave /= float(dwave)*2.0 + 1.0;

  vec3 col = vec3(0.0);

  float xamp = sqrt(1.0-2.0*abs(q.x - 0.5));
  col += 1.0*mix(vec3(0.0), baseCol, fft*fft);
  float xoff = abs(0.125*(xamp*wave - 0.5) + 0.185 - q.y);
  col += 1.25*xamp*vec3(1.0, 0.9, 1.0)*exp(-30.0*xoff);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  float dc = min(abs(p.x), abs(p.y));
  p.x *= RESOLUTION.x/RESOLUTION.y;

  float aa = 2.0/RESOLUTION.y;

  vec3 sunCol = mix(vec3(1.0, 1.0, 0.0), vec3(1.0, 0.0, 1.0), clamp((0.85 - p.y)*0.75, 0.0, 1.0));
  vec3 glareCol = sqrt(sunCol);
  float ss = smoothstep(-1.05, 0.0, p.y);
  vec3 glow = mix(vec3(1.0, 0.7, 0.6).zyx, glareCol, ss);

  float s = 15.0;
  float d = df(p*s)/s;
  float db = abs(d) - 0.0025;

  vec3 col = vec3(1.0, 0.0, 1.0)*0.125;
  vec3 corona = 0.65*glow*exp(-2.5*d)*ss;
  col += sound(q, corona);
  col += corona;
  col = mix(col, sunCol*ss, smoothstep(-aa, aa, -d));
  col = mix(col, glow*1.55, smoothstep(-aa, aa, -db));
//  col = mix(col, vec3(1.0), smoothstep(-aa, aa, -dc));

  col += 1.0-smoothstep(0.0, 2.0, TIME);
  col = postProcess(col, q);


  fragColor = vec4(col, 1.0);
}

