    { bnoise = fract( texture( iChannel0, fragCoord/1024.).x + T ); } //animated bluenoise pattern}
    else
    { bnoise = 0.5; }

    //3d camera
	vec2 p = (2.0*fragCoord.xy-iResolution.xy) / iResolution.y;
	vec3 rd = normalize( vec3(p,-2.0) );
    vec3 ro = vec3(0.0, 0.0, 5.0 );

    //running animation
    float runtransition = smoothstep( 0., 1., sin(T*0.3));
    float runadvancement = -cos(T*0.3) *1.2;
    float runpos = abs(sin(T * 9.0)) * runtransition * 0.25;
    float runrot = sin(T * 9.0 * 2. + 0.5) * runtransition * runtransition * 0.15 - 0.6;
    float sway = cos(T * 9.0 + 0.5) * runtransition * runtransition * runtransition * 0.1;

    //flashlight setup
    vec3 conepos = vec3(-0.3, runpos, runadvancement * 2.0 + 1.0);
    vec3 conedirection;
    conedirection = mix( normalize( vec3(sin(T),cos(T*0.6),-cos(T*0.3)+0.4) + vec3(cos(T*2.6),sin(T*2.2),-sin(T*2.3))*0.6 ), vec3(0., runrot, 0.), vec3(runtransition) );
    float conelength = 2.0;
    float coneangle = 1.1;

    //draw eyes
    float eyes = draweye(ro, rd, conepos + vec3(0.25,0.75,-0.5) ) + draweye(ro, rd, conepos + vec3(0.4,0.75,-0.5) ) ;
    eyes *= round(fract(T*0.45)+0.4);

    //add sway motion
    conepos += mix( vec3(0.), vec3(sway, 0., 0.), vec3(runtransition) );
    conedirection += vec3(sway, 0., 0.);

    //draw flashlight/volume light
    float source = drawsource(ro, rd, conepos);
    float cone = drawcone(ro, rd, conepos, conedirection, conelength, coneangle, bnoise);

    //composite/color
    fragColor = vec4(aces( vec3(source + cone) * vec3(0.7,0.9,1.) + vec3(eyes) * vec3(1.,0.,0.)   ) ,1.);
}

