//Changes zoom level
#define FocalDistance 2.0 

//Classic sphere hit fuction that returns white on hit or the background otherwise

vec4 SphereHit(const in vec3 ro, const in vec3 rd)
{
	float R = 0.1;
    float b = 2.0 * dot(rd, ro);
    float c = dot(ro.xyz, ro)- R*R;
    float d = b*b - 4.0*c;

	if(d >= 0.0)
        return vec4(1.0);
    else 
        return texture(iChannel1,normalize(rd));
}


//==== Transformtion Matrices ===

mat3 RotateY(float theta)
{
    float Cos = cos(theta);
    float Sin = sin(theta);
	return mat3(Cos,0.0,Sin,
                0.0,1.0,0.0,
                -Sin,0.0,Cos);
}

mat3 RotateX(float theta)
{
    float Cos = cos(theta);
    float Sin = sin(theta);
	return mat3(1.0,0.0,0.0,
                0.0,Cos,-Sin,
                0.0,Sin,Cos);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy-0.5;
	uv.x*= iResolution.x/iResolution.y;
    //Get data from buffer
    vec4 transform = texelFetch(iChannel0,ivec2(0,0),0);     
    //Calculate the origin with transform data, here the z value is used to set the distance
    vec3 ro = RotateY(transform.x) * RotateX(-transform.y) * vec3(0.0,0.0,-transform.z);
    //usual look at calculation
 	vec3 lp = vec3(0.0);
    vec3 forward = normalize(lp-ro);
    vec3 GlobalUp = vec3(0.0,1.0,0.0);
    vec3 right = normalize(cross(forward,GlobalUp));
    vec3 localUp = cross(right,forward);    
    vec3 rd = (ro+forward*FocalDistance) + uv.x * right + uv.y * localUp - ro;    
    fragColor = SphereHit(ro,normalize(rd));
}

