#define PI 3.1415926535

float hash11(float p)
{
    vec2 p2 = fract(vec2(p * 5.3983, p * 5.4427));
    p2 += dot(p2.yx, p2.xy + vec2(21.5351, 14.3137));
    return fract(p2.x * p2.y * 95.4337);
}

float hash12(vec2 p)
{
    p=fract(p*vec2(5.3983, 5.4427));
    p+=dot(p.yx, p.xy + vec2(21.5351, 14.3137));
    return fract(p.x * p.y * 95.4337);
}

float smin(float a, float b, float k)
{
	float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}


float box(vec3 p, vec3 b)
{
    vec3 d=abs(p)-b;
    return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); 
}

float box2D(vec3 p, vec2 b)
{
    vec2 d = abs(p.xy) - b;
    return min(max(d.x, d.y), 0.0) + length(max(d,0.0));
}


float vline(vec3 p, float h, float r)
{
    p.y-=clamp(p.y, 0.0, h);
    return length(p)-r;
}

vec3 tri(vec3 p)
{
    return abs(fract(p) - .5);
}

float triNoise(vec3 p)
{
    return dot(tri(p + .5 * tri(p.yzx * 1.3 + vec3(.3, .7, .1) + 0.25 * tri(p.zyx * 2.6))), vec3(0.3333));
}

float noise(vec3 x)
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f * f * (3.0 - 2.0 * f);

    vec2 uv = (p.xy + vec2(37.0, 17.0)*p.z)+f.xy;
    vec2 rg = textureLod(iChannel0, (uv  + 0.5)/256., .0).yx;
    return mix(rg.x, rg.y, f.z);
}

float sphere(vec3 p)
{
    return length(p) - 1.;
}

int mateID = 0;
float glow0 = 0., glow1 = 0.;
bool doGlow = false;

float lights(vec3 p, float d)
{
    vec3 q = p;
    q.x = abs(q.x) - 1.15;
    q.z = mod(q.z, 2.) - 1.;
    float d2 = vline(q.xzy, 0.6, 0.16);

    if(d2 < d)
    {
    	mateID = 4;
    	d = d2;
        if(doGlow) glow0 += 0.2 / (0.15 + abs(d));
    }

    q.x = p.x;
    d2 = vline(q.xzy - vec3(0., 0., 1.), 0.4, 0.16);
    if(d2 < d)
    {
        mateID = 4;
        d = d2;
        if(doGlow) glow1 += 0.2 / (0.15 + abs(d));
    }

    return d;
}

float tunnel(vec3 p)
{
    mateID = 0;
    float d0 = 1.- length(p.xy) + triNoise(p) * 0.2;
    float d1 = abs(p.y + 0.65);

    if(d1 < d0)
    {
        mateID = 1;
        d0 = d1;
    }

    vec3 q = p * 6.;

    vec2 f = fract(q.xz);
    vec2 l = floor(q.xz);

    //vec2 off = smoothstep(-1.,-0.9, f) - smoothstep(0.9, 1., f);
    //d1 -= (off.x + off.y) * 0.01;

    q.xz = mod(q.xz, vec2(1., 1.1)) - vec2(.5, .55);
    float d2 = box(q + vec3(0., 3.97, 0.), vec3(0.2, 0.1, 0.45)*.98) / 6. - .01*noise(p*25.);

    if(d2 < d0)
    {
        mateID = 1;
        d0 = d2;
    }

    q = p * 6.;
    q.xz = mod(q.xz + vec2(0.5, 0.5), vec2(1., 1.1)) - vec2(.5, .55);

    d2 = box(q + vec3(0., 3.97, 0.), vec3(0.2, 0.1, 0.45)*.98) / 6. - .02*noise(p*15.);;

    if(d2 < d0)
    {
        mateID = 1;
        d0 = d2;
    }

    q = p;
    q.x = abs(q.x) - .8;
    d2 = box2D(q + vec3(0., .7, 0.), vec2(0.175 , 0.1));

    if(d2 < d0)
    {
        mateID = 2;
        d0 = d2;
    }


   	d0 = lights(p, d0);

    return d0;
}

float map(vec3 p)
{
    float d0 = tunnel(p);
    return d0;
}

float mapSimple(vec3 p)
{
    float d0 = 1.- length(p.xy) + triNoise(p) * 0.2;
    d0 = lights(p, d0);

    return d0;
}

vec3 normal(vec3 p, float t)
{
    vec2 e = vec2(0.001 * t , 0.);

    return normalize(vec3(map(p + e.xyy) - map(p - e.xyy),
                          map(p + e.yxy) - map(p - e.yxy),
                          map(p + e.yyx) - map(p - e.yyx)));
}


float ao(vec3 p, vec3 n)
{
    float r = 0.0, w = 1.0, d;
    for(float i = 1.0; i <5.0 + 1.1; i++)
    {
        d = i / 5.;
        r += w * (d - map(p + n * d));
        w *= 0.5;
    }
    return 1.0 - clamp(r, 0., 1.);
}

float noise(vec2 x)
{
    vec2 p=floor(x);
    vec2 f=fract(x);
    f=f*f*(3.0-2.0*f);
    float n=p.x + p.y*57.0;
    return mix(mix(hash11(n+0.0), hash11(n+1.0),f.x),
               mix(hash11(n+57.0), hash11(n+58.0),f.x),f.y);
}
const mat2 m=mat2(0.8,0.6,-0.6,0.8);


float fbm(vec2 p)
{
    float f=0.0;
    f+=.5*noise(p); p=m*p*2.;
    f+=.25*noise(p); p=m*p*2.;
    f+=.125*noise(p); p=m*p*2.;
    f+=.0625*noise(p); p=m*p*2.;
    f+=.03125*noise(p);
    return f/0.984375;
}

vec4 texcube(sampler2D sam, vec3 p, vec3 n)
{
    vec4 p1=texture(sam, p.xy);
    vec4 p2=texture(sam, p.xz);
    vec4 p3=texture(sam, p.yz);
    return p1*abs(n.z)+p2*abs(n.y)+p3*abs(n.x);
}

float bump(vec3 p, vec3 n)
{
    return dot(texcube(iChannel0, 0.25*p, n).xyz, vec3(0.299, 0.587, 0.114)); 
}

vec3 bump_mapping(vec3 p, vec3 n, float weight)
{
    vec2 e = vec2(2./iResolution.y, 0); 
    vec3 g=vec3(bump(p-e.xyy, n)-bump(p+e.xyy, n),
                bump(p-e.yxy, n)-bump(p+e.yxy, n),
                bump(p-e.yyx, n)-bump(p+e.yyx, n))/(e.x*2.);  
    g=(g-n*dot(g,n));
    return normalize(n+g*weight);
}

vec3 reflection(vec3 p, vec3 n, vec3 rd, vec3 ro)
{
    rd = reflect(rd, n);

    float t = 0.01, d = 0.;

    p += 0.001 * n;

    float glow = 0.;

    doGlow = true;
    for(int i = 0; i < 32; ++i)
    {
        d = mapSimple(p + t * rd);

        if(d < 0.005 || t > 20.)
        	break;

        t += d;
    }
    doGlow = false;

    vec3 radi = vec3(1.);
    if(t < 20.)
    {
        p = p + t * rd;
        n = normal(p, t);

        vec3 PL0 = ro + vec3(0, 0.5, 2.);
        vec3 w0 = normalize(PL0 - p);
        vec3 L0 = vec3(1.);

        float dif = max(0., dot(n, w0));
        float bac = max(0., dot(n, -w0));
        float amb = 0.5 + 0.5 * n.y;
        float spe = 0.;
        float att = length(PL0 - p);
        att = 1. / (att + att * att);


    	vec3 mate = vec3(.7);

        radi = (4. * dif + .5 * bac + .5 * amb) * mate * L0 * att + spe * L0 * .25;


        if(mateID == 0)
        {
            float rho = length(p.xy);
            float phi = asin(p.y / rho);

            vec3 cy = vec3(rho, phi, p.z) * 6.;

            vec2 f = fract(cy.zy) * 2. - 1.;
            vec2 l = floor(cy.zy);

            float rnd = hash12(l);

            if(rnd > 0.7) radi = mix(radi, vec3(2.), 1. - smoothstep(0.4, .5, length(f)));
        }
    }

  	radi += glow0 * 0.06 * vec3(1.,.5,.2);
    radi += glow1 * 0.02 * vec3(.0,.5,1.);
    return radi;
}

float ggx(float roughness, vec3 rd, vec3 W, vec3 n)
{
    vec3 H = normalize(W - rd);
    float NoH = max(0., dot(n, H));
    vec3 NxH = cross(n, H);
    float a = roughness * roughness;
    float b = NoH * a;
    float c = a / ((1. - NoH * NoH) + b * b);
    float d = c * c;
    return d;
}

float schlickFresnel(vec3 rd, vec3 l)
{
    vec3 h = normalize(-rd + l);
    float EdotH = max(0., dot(-rd, h));
    return pow(max(0.0, 1.0 - EdotH), 2.0);
}

vec3 shading(vec3 p, vec3 n, vec3 ro, vec3 rd)
{
    vec3 PL0 = ro + vec3(0, 0.5, 2.);
    vec3 w0 = normalize(PL0 - p);
    vec3 L0 = vec3(1.);


    float reflective = .0;
    float roughness = 1.;

    bool hasRefl = false;

    vec3 mate = vec3(.1);

    float water = 0.;
    float fade = 1.;

    // road, a little wet by default
    if(mateID == 1)
    {
        mate = vec3(.2);
        n=bump_mapping(p * (5. + 0.001 * iResolution.x), n, 0.02);

        // water area
        water = smoothstep(0.0, 0.7, fbm(p.xz * 5.));

        // very wet area
        float k = smoothstep(0.1,0.9, fbm(p.zx * 10.));

        // fade the lighting for water
        fade = mix(fade, 0.3, 1. - smoothstep(0.3, 0.6, water));

        roughness = 0.8;
        hasRefl = true;

        if(water < 0.6)
        {
            reflective = .5;
            roughness = .8;
        }
        else if(water > 0.85)
        {
            roughness = mix(roughness, 0.1,  1. - smoothstep(0.0,.5,k));
            reflective = .3;
        }
    }

    float occ = ao(p, n);

    float tmp = map(p);
    float dif = max(0., dot(n, w0));
    float bac = max(0., dot(n, -w0));
    float amb = 0.5 + 0.5 * n.y;
    float spe = ggx(roughness, rd, w0, n) * max(0., dot(n, w0));
    float att = length(PL0 - p);
    att = 1. / (att + att * att * 4.);
    float fre = schlickFresnel(rd, w0);


    vec3 diffuse = (4. * dif + .5 * bac + .5 * amb + fre*fre*fre*5.) * (mate - mate * reflective) * L0 * att;
    vec3 specular = spe * L0 * .25;

    vec3 radi = (diffuse + specular) * fade;

    // dirty
    radi = mix(radi * vec3(smoothstep(0.1, .8, pow(fbm(p.xz * 5.), 1.))), radi, smoothstep(-1.,0.0, p.y));

    radi *= occ;    

    // dots on the wall
    if(mateID == 0)
    {
        float rho = length(p.xy);
        float phi = asin(p.y / rho);

        vec3 cy = vec3(rho, phi, p.z) * 6.;

        vec2 f = fract(cy.zy) * 2. - 1.;
        vec2 l = floor(cy.zy);

        float rnd = hash12(l);

        if(rnd > 0.7) radi = mix(radi, vec3(2.), 1. - smoothstep(0.4, .5, length(f)));
    }

    // smooth the normal for water area
    if(mateID == 1)
    {        
        n = mix(n, normalize(vec3(0.,1.,0)*.98 + n*0.02), 1. - smoothstep(0.5, 0.8, water));
    }
    else if(mateID == 4)
        radi = vec3(1.);

    // mix with reflection
    if(hasRefl)
    {
      	radi = mix(radi,  reflection(p, n, rd, ro) * reflective, fre);
    }

    return radi * 0.32;
}

float depth = 0.;

vec3 scene(vec3 ro, vec3 rd, vec2 uv)
{
    vec3 col = vec3(0.);
    float rnd = 0.9 + 0.1 * hash12(uv.xy);

    float t = 0.01, d = 1e10;

    doGlow = true;
    for(int i = 0; i < 128; ++i)
    {
        d = abs(map(ro + t * rd)) * rnd;
        if(d < 0.001 + 0.0002 * t || t > 50.)
            break;

        t += d;
    }
    doGlow = false;


    if(t < 50.)
    {
        vec3 p = ro + t * rd;
        vec3 n = normal(p, t);
        col = shading(p, n, ro, rd);
    }

    depth = t;

    col += glow0 * 0.03 * vec3(1.,.5,.2);
    col += glow1 * 0.02 * vec3(.0,.5,1.);
    col=mix(col, vec3(0.), 1.-exp(-.04*t*t));

    return col;
}


mat3 cam(vec3 ro, vec3 ta)
{
    vec3 f = normalize(ta - ro);
    vec3 r = normalize(cross(vec3(0, 1, 0), f));
    vec3 u = normalize(cross(f, r));

    return mat3(r, u, f);
}

vec3 tonemap(vec3 x) 
{
    const float a=2.51, b=0.03, c=2.43, d=0.59, e=0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv * 2. - 1.;
    p.x *= iResolution.x / iResolution.y;


    float fov = 1.5;

    float time = iTime * 1.5;

    vec3 ro = vec3(0., -.25, iTime);
    vec3 ta = ro + vec3(0., 0., 1.);

    // debugging camera
    float x_rot=-iMouse.x/iResolution.x*PI*2.0;
    float y_rot=iMouse.y/iResolution.y*3.14*0.5 + PI/2.0;
    if(iMouse.z>0.||iMouse.w>0.)
        ro=vec3(0.,0,-2)+vec3(cos(y_rot)*cos(x_rot),cos(y_rot)*cos(x_rot),cos(y_rot)*sin(x_rot))*1.;

    vec3 rd = normalize(cam(ro, ta) * vec3(p.x, p.y, fov));

    vec3 col = scene(ro, rd, p);
    fragColor = vec4(col, depth);
}

