vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

float remap(float value, float low1, float high1, float low2, float high2)
{
    return clamp (low2 + (value - low1) * (high2 - low2) / (high1 - low1), low2, high2);
}

float hatch(vec2 uv, vec2 scrUV, float inpTime)
{
    float inp = (noise ((floor (scrUV) + vec2(inpTime)) * 0.3333) + 1.0) * 0.5;
    float inp2 = (noise (( floor (scrUV * 0.1) + vec2(inpTime * 0.5 + 150.0)) * 0.3333) + 1.0) * 0.5;
    inp2 = remap (inp2, 0.3, 1.0, 0.0, 1.0);
    inp = mix (inp, 0.5, pow (inp2 * 18.0, 0.6));

    if ( inp > 0.9 )
    {
        if (abs (uv.x - uv.y) < 0.1)
        {
            if ( length(uv - vec2 (0.5)) < 0.707 * (inp - 0.9) * 10.0 ) return 1.0;
            else return 0.0;
        }
        else return 0.0;
    }
    else if ( inp < 0.1 )
    {
        if (abs ((1.0 - uv.x) - uv.y) < 0.1)
        {
            if ( length(uv - vec2 (0.5)) < 0.707 * (1.0 - inp * 10.0) ) return 1.0;
            else return 0.0;
        }
        else return 0.0;
    }
    else
        return 0.0;
}

float hatch2(vec2 uv, vec2 scrUV)
{
    return hatch (uv, scrUV, iTime + 200.0) + 
		   hatch (uv + vec2(0.5, -0.5), scrUV, iTime + 300.0) +
		   hatch (uv + vec2(-0.5, 0.5), scrUV, iTime + 400.0) +
		   hatch (uv + vec2(-0.5, -0.5), scrUV, iTime + 500.0) +
		   hatch (uv + vec2(0.5, 0.5), scrUV, iTime + 600.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float whr = iResolution.x/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy * vec2 (whr, 1.0) * 40.0;

    float noiseVal = clamp (hatch2(fract (uv), uv), 0.0, 1.0);
    float colorVal = (noise(uv / 75.0) + 1.0) * 0.5;
    vec3 outCol = mix (vec3 (1.0, 0.5, 0.5), vec3 (1.0, 0.5, 1.0), colorVal) * noiseVal;

    fragColor = vec4(outCol,1.0);
}

