// All the distance functions from:https://iquilezles.org/articles/distfunctions
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 128
#define MAX_DIST 128.
#define SURF_DIST .0001
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define Box(p,s) max(max(abs(p).x-s.x,abs(p).y-s.y),abs(p).z-s.z)
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

#define MATERIAL_DIFFUSE0 0
#define MATERIAL_DIFFUSE1 1
#define MATERIAL_REFLECT 2

// by Dave_Hoskins
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 combine(vec2 val1, vec2 val2){
    vec2 res1 = val1;
    vec2 res2 = val2;
    return (val1.x < val2.x)?res1:res2;
}

// by iq https://iquilezles.org/articles/distfunctions
float smin(float a, float b, float k) {
    float h = clamp(.5 + .5*(b - a)/k, .0, 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

// by iq https://iquilezles.org/articles/distfunctions
float smax( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h);
}

float udRoundBox(vec3 p, vec3 b, float r) {
    return length(max(abs(p)-b, .0)) -r;
}

// by reinder https://www.shadertoy.com/view/4tdcWS
// This one is super fast.
float sdCapsule(vec3 p, vec3 a, vec3 b, const float r0, const float r1) {
    vec3 d = b -a;
    float h = length(d);
    d = normalize(d);
    float t=clamp(dot(p-a, d), 0., h);
    return distance(a+t*d, p) -mix(r0, r1, t/h);
}

float dHand(vec3 p){
    vec3 prevP = p;

    const float k = 1.1;
    float c = cos(k*p.x);
    float s = sin(k*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(0.0);    
    q.y = p.y;
    q.xz=m*p.xz;

    float d = udRoundBox(q-vec3(0.01,0.05,0.0),vec3(0.1,0.05,0.001),0.04);
    d = smin(d,udRoundBox(p-vec3(0.005,-0.02,0.01),vec3(0.09,0.02,0.0001),0.035),0.03);
    d = smin(d,udRoundBox(p-vec3(0.0,-0.1,0.0),vec3(0.065,0.02,0.0001),0.035),0.03);

    // palm
    float d2 = sdCapsule(q,vec3(0.13,0.05,0.0425),vec3(-0.03,0.09,0.045),0.005,0.001);
    d = smax(d2,d,0.01);
    d2 = sdCapsule(q,vec3(0.03,0.03,0.05),vec3(-0.13,0.06,0.0425),0.001,0.005);
    d = smax(d2,d,0.01);
    d2 = sdCapsule(q,vec3(0.00,-0.1,0.045),vec3(-0.04,0.01,0.06),0.005,0.001);
    d = smax(d2,d,0.01);

    p = prevP;

    // pinky
    d = smin(d,sdCapsule(p,vec3(0.13,0.15,0.0),vec3(0.15,0.18,0.0),0.035,0.03),0.03);
    d = smin(d,sdCapsule(p,vec3(0.15,0.18,0.0),vec3(0.165,0.22,0.01),0.03,0.027),0.005);
    d = smin(d,sdCapsule(p,vec3(0.165,0.22,0.01),vec3(0.175,0.26,0.03),0.028,0.025),0.005);

    // ring
    d = smin(d,sdCapsule(p,vec3(0.06,0.15,0.0),vec3(0.07,0.21,0.0),0.035,0.03),0.01);
    d = smin(d,sdCapsule(p,vec3(0.07,0.21,0.0),vec3(0.085,0.27,0.01),0.03,0.027),0.005);
    d = smin(d,sdCapsule(p,vec3(0.085,0.27,0.01),vec3(0.095,0.33,0.03),0.028,0.025),0.005);

    // middle
    d = smin(d,sdCapsule(p,vec3(-0.015,0.15,0.0),vec3(-0.015,0.22,-0.01),0.035,0.03),0.02);
    d = smin(d,sdCapsule(p,vec3(-0.015,0.22,-0.01),vec3(-0.015,0.29,0.00),0.03,0.027),0.005);
    d = smin(d,sdCapsule(p,vec3(-0.015,0.29,0.00),vec3(-0.015,0.36,0.02),0.028,0.025),0.005);

    // index
    d = smin(d,sdCapsule(p,vec3(-0.09,0.15,0.0),vec3(-0.105,0.21,0.0),0.035,0.03),0.03);
    d = smin(d,sdCapsule(p,vec3(-0.105,0.21,0.0),vec3(-0.12,0.26,0.01),0.03,0.027),0.005);
    d = smin(d,sdCapsule(p,vec3(-0.12,0.26,0.01),vec3(-0.13,0.32,0.03),0.028,0.025),0.005);   

    // thumb
    d = smin(d,sdCapsule(p,vec3(-0.08,-0.03,0.0),vec3(-0.13,0.02,0.0),0.035,0.037),0.035);
    d = smin(d,sdCapsule(p,vec3(-0.13,0.02,0.0),vec3(-0.17,0.09,0.01),0.037,0.032),0.005);
    d = smin(d,sdCapsule(p,vec3(-0.17,0.09,0.01),vec3(-0.19,0.15,0.02),0.032,0.03),0.005);

    // knuckles
    d = smin(d,sdCapsule(p,vec3(0.12,0.12,-0.027),vec3(0.08,0.0,-0.02),0.01,0.005),0.03);
    d = smin(d,sdCapsule(p,vec3(0.05,0.13,-0.03),vec3(0.03,0.0,-0.02),0.01,0.005),0.03);
    d = smin(d,sdCapsule(p,vec3(-0.015,0.13,-0.03),vec3(-0.015,0.0,-0.02),0.01,0.005),0.03);
    d = smin(d,sdCapsule(p,vec3(-0.09,0.12,-0.03),vec3(-0.07,0.0,-0.02),0.01,0.005),0.03);
    return d;
}

vec2 GetDist(vec3 p) {
    vec3 prevP = p;
    p.x = abs(p.x);
    p.x-=0.3;
    float d = dHand(p);

    p = prevP;
    p.y+=0.15;
    float d2 = Box(p,vec3(1.2,0.01,1.2));

    vec2 res = vec2(d, MATERIAL_DIFFUSE0);
    vec2 res2 = vec2(d2, MATERIAL_DIFFUSE1);

    vec2 model = combine(res,res2);
    return model;
}

vec2 RayMarch(vec3 ro, vec3 rd, float side) {
    vec2 dO = vec2(0.0);
    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 diffuseMaterial(vec3 n, vec3 col) {
    vec3 lightDir = normalize(vec3(0.8,0.4,0.2));
    float diff = dot(n,normalize(lightDir))*0.5+0.5;
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);

    vec3 res = col*(diff);
    res += col*bounceDiff*0.5;    

    return res;
}

vec3 reflectMaterial(vec3 p, vec3 rd, vec3 n) {
    vec3 r = reflect(rd,n);

    vec3 col = r;

    float k = r.y*r.y;
    col = mix(col,vec3(1.5),k);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 0, -3.5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(10.0));
    ro.xz *= Rot(-iTime*.5+1.0);

    #endif

    vec3 rd = R(uv, ro, vec3(0,0.2,0), 3.0);
    vec2 d = RayMarch(ro, rd, 1.);
    vec3 col = vec3(0.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);

        int mat = int(d.y);
        if(mat == MATERIAL_DIFFUSE0){
            float h = hash12(p.xz*5.0)*0.05;
            col = diffuseMaterial(n,vec3(0.95)+h);
        } else if(mat == MATERIAL_DIFFUSE1) {
            p.x = abs(p.x);
            p.x-=0.3;
            float fakeShadow = length(p.xz)-0.3;
            col = diffuseMaterial(n,mix(vec3(0.9),vec3(0.2),S(fakeShadow,-0.5)));
        } else if(mat == MATERIAL_REFLECT) {
            col = reflectMaterial(p,rd,n);
        }

    }

    // gamma correction
    col = pow( col, vec3(0.6545) );

    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

