#define MIRROR
#define PERIOD 			4.0  // <- Angle_dia = pi * period 
#define INNER_DAMPING   4.0  // <- Damping damping for inner radius damping. > 1 = MADNESS
#define DOUBLE_DAMPING  0.54 // <- Damping of Buffer A
 #define SWAP              // <- Swap inner and outer
// #define MADNESS           // <- Uncomment if you want to see some features
#define RED             3.0  // <- value of .. idk, this is for red
#define GREEN           2.0  // <- for green 
#define BLUE            5.0  // <- for blue
#define COLOR_ADD       .3  // <- increase color intensivity

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    // Constants
    float RADIUS = min(iResolution.x, iResolution.y) / 4.0;
    float inner, outer;

    // Get distance
    vec2 dist    = fragCoord.xy - iResolution.xy / 2.0;
    float s_dist = sqrt(dist.x * dist.x + dist.y * dist.y);

    // Get rotation angle [0.0 - 1.0]
    float angle  = atan(dist.y / dist.x) / (3.14 * PERIOD) + 0.25;
    if(dist.x <= 0.0)
        angle += 0.5;
    #ifdef MIRROR
    	if(dist.x <= 0.0)
            angle = 1.0 - angle;
    #endif

    #ifndef SWAP
    	inner = texture(iChannel0, vec2(angle, 0.25)).x;
    	outer = texture(iChannel0, vec2(angle, 0.75)).x;
    #else
    	inner = texture(iChannel0, vec2(angle, 0.75)).x;
    	outer = texture(iChannel0, vec2(angle, 0.25)).x;
    #endif

    float color = 0.0;
    float radius = 0.0;
    float damping = s_dist / iResolution.x * 2.0;// * INNER_DAMPING;
    float frame_render = texture(iChannel1, uv).x * DOUBLE_DAMPING;

    radius = (RADIUS + 0.1) * (0.9 + outer * damping);

    if(s_dist < radius)
        color = 1.0;
    else
        color = damping * (radius / iResolution.x + 1.0 - damping);

    #ifdef MADNESS
    	radius = (RADIUS * 0.84) * (1.0 + inner * damping);
    #else
    	radius = (RADIUS - 0.1) * (1.0 - inner * (damping * INNER_DAMPING));
    #endif

    if(s_dist < radius)
        color = 0.2 * (1.0 - damping) * (radius / iResolution.x + 1.0 - damping);

    fragColor = vec4(color);

    // Coloring 
    float scalar_color_offset = COLOR_ADD + s_dist / radius * (1.0 - COLOR_ADD);

    fragColor *= vec4(RED, GREEN, BLUE, 1.0) * scalar_color_offset;

    // Bound it down
    fragColor = clamp(vec4(0.0), vec4(1.0), fragColor + frame_render);
}

