// honorable mention to https://www.shadertoy.com/view/tlXBR8 -- has some links and discussion

/* found this somewhere -- it's neat but doesn't render cleanly.
mb from here: https://www.shadertoy.com/view/Ws23zt -- there are some equiv SDFs there too */

float sdTet2(vec3 p, float r) {
  return (max(
    abs(p.x - p.z) + p.y,
    abs(p.x + p.z) - p.y
  ) - r);
}

/* my derivation -- wanted to get something that was easily generalized to 4d. It didn't quite work. */

vec3 sdTetrahedronInner(vec3 p, float r) {
  float ySign = sign(p.y);
  p.xz = abs(ySign < 0. ? p.zx : p.xz);
  p.y = abs(p.y);
  vec3 box = vec3(mix(r/2., r, p.y / r), r, mix(r/2., r, -p.y / r));
  return p - box;
}

float sdTetrahedron(vec3 p, float r, out vec3 dists) {
  dists = sdTetrahedronInner(p, r);
  return length(bestOf(dists)) * sign(bestOf(dists));
}
float sdTetrahedron(vec3 p, float r) {
  vec3 tmp;
  return sdTetrahedron(p, r, tmp);
}

float sdTetrahedronFrame(vec3 p, float r, out vec3 dists) {
  dists = sdTetrahedronInner(p, r);
  return length(best2(dists));
}
float sdTetrahedronFrame(vec3 p, float r) {
  vec3 tmp;
  return sdTetrahedronFrame(p, r, tmp);
}

/* from https://www.shadertoy.com/view/Ms3Sz2 */

// Isohedral Tetrahedron distance function
float sdIsoTet(vec3 p, float h) 
{
    vec3 q = abs(p);

    float y = p.y,
          d1 = q.z-max(0.,y),
          d2 = max(q.x*.5 + y*.5,.0) - min(h, h+y);
    return length(max(vec2(d1,d2),.005)) + min(max(d1,d2), 0.);
}

/* https://www.shadertoy.com/view/MtV3Dy */

float  plane(vec3 p, vec3 origin, vec3 normal){ 
   return dot(p - origin,normal);   
}

float tetrahedronPlanes(vec3 p, float d) { 
  float dn =1.0/sqrt(3.0);

   //The tetrahedran is the intersection of four planes:
    float sd1 = plane(p,vec3(d,d,d) ,vec3(-dn,dn,dn)) ; 
    float sd2 = plane(p,vec3(d,-d,-d) ,vec3(dn,-dn,dn)) ;
 	float sd3 = plane(p,vec3(-d,d,-d) ,vec3(dn,dn,-dn)) ;
 	float sd4 = plane(p,vec3(-d,-d,d) ,vec3(-dn,-dn,-dn)) ;

    //max intersects shapes
    dn = max(max(sd1,sd2),max(sd3,sd4));
  return dn; 
}

