float maxElem(vec4 a){
    return max(a.x, max(a.y, max(a.z,a.w)));
}
//A box in 4D tetrahedral space
float sdBox4( vec4 p, vec4 b )
{
    vec4 d = abs(p) - b;
    return min(maxElem(d),0.0) + length(max(d,0.0));
}

float gTetOffset = 1.0;//using 1 makes this a tet, using 0 makes it an octahedron

//Convert from euclidean to tetrahedral space
vec4 GetTetCoord(vec3 p){
    float offset = gTetOffset;//sqrt(2.0);
    float s1 = 1.0/sqrt(4.0);
    float s2 = 1.0/sqrt(2.0);
    float s3 = 1.0;

    return vec4(offset - p.z*s1 - s2*p.x,
                offset - p.z*s1 + s2*p.x,
                offset + p.z*s1 + s2*p.y,
                offset + p.z*s1 - s2*p.y)*s3;
}

float sdTetrahedronInTetSpace(vec3 p, float size, float roundness){
    vec4 t = GetTetCoord(p); 

    float scaleDown = 1.0/sqrt(4.0);//scale it to match expectation of size--
    float offsetSize = gTetOffset;//offset size to expectation of tet
    size = size*scaleDown + offsetSize ;

  //roundness *= scaleDown;
    return sdBox4(t, vec4(size))-roundness; //rounded tetrahedon, yah!
    //return sdBox4(t, vec4(size,size*.05,size*1.0, size*3.0))-roundness; //can do irregular shaped tets also
  //return maxElem(abs(t)) - size;//max norm tetrahedron as seen in many shadertoys
}

/* @iq posted this in the comments of https://www.shadertoy.com/view/ts3SRs */

float sdTetrahedronIq(vec3 p, float size, float roundness)
{
    const float k = sqrt(2.0);

    p *= size;

    p.xz = abs(p.xz);

    float m = 2.0*p.z - k*p.y - 1.0;

    p = (m>0.0) ? p : vec3(p.z,-p.y,p.x);

    float s1 = clamp(p.x,0.0,1.0);
    float s2 = clamp((p.x-p.y*k-p.z+2.0)/4.0,0.0,1.0);

    m = 2.0*p.z - k*p.y - 1.0;

    float d = sign(m)*sqrt((sign(p.y*k+p.z+1.0)+sign(2.0-3.0*p.x-k*p.y-p.z)<1.0) 
                  ?
                  min( dot2(vec3(s1,-k*0.5,0)-p), 
                       dot2(vec3(s2, k*0.5-k*s2,1.0-s2)-p) )
                  :
                  m*m/6.0 );

    return d / size - roundness;
}

/* https://www.shadertoy.com/view/tdGBzW */

float sdTetrahedronPyramid(vec3 p) {
    p *= 0.5;
    return max(
        // Vertical bound
        abs(p.y) - 0.5, 

        // Horizontal bound
        max(abs(p.x) * 0.866025 + p.z * 0.5, -p.z) - 0.25 * abs(0.5 - p.y)
    ) * 1.9;
}


