precision highp float;

// distributed under CC0

/* here's an example of an SDF I was developing for a tetrahedron
-- note: this was some experimental code where I was trying to get
the distances to play nicely. */

float testSdTet(vec3 p, float r, out vec3 dists, out vec3 col) {
  float zoomOut = 2.;
  // p *= zoomOut;

  float ySign = sign(p.y);
  // p.xz = abs(ySign < 0. ? p.zx : p.xz);
  // p.y = abs(p.y);
  vec3 box = vec3(
    mix(r/2., r, p.y / r / r),
    r,
    mix(r/2., r, -p.y / r / r)
  );
  p.y *= SQRT_3;
  dists = (abs(p) - box);
  return length(bestOf(dists)) * sign(bestOf(dists));

  /* a neat version I found that gave me the idea for using mix + box method */

  /*
  return (max(
    abs(p.x - p.z) + p.y,
    abs(p.x + p.z) - p.y
  ) - r);
  /**/
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float aspect = u_resolution.x / u_resolution.y;
    vec2 uv = scaleUvWithAspect(gl_FragCoord.xy / u_resolution.xy, aspect);
    vec2 mouse = scaleUvWithAspect(u_mouse.xy/u_resolution.xy, aspect);
    float fw = bestOf(fwidth(uv));
    float t = u_time * .8;
    float zMovement = 0.;
    float zResolution = .1;

    float gridSize = 5., fwg = fw * gridSize;
    vec2 id, gv;
    setIDGV(uv, gridSize, id, gv);

    /* comment this to remove animation */
    zMovement = sin(iTime) * .2;

    if (bestOf(mouse.xy) > -.5 + 1. / gridSize) {
        zMovement += mouse.y + mouse.x;
    }

    vec3 col = vec3(0), p;

    // z from -1 to 1
    float gridN = id.x + id.y * gridSize;
    float nGridCells = gridSize * gridSize;
    float z = gridN / (nGridCells - 1.) * 2. - 1. + zMovement;
    float zId = floor((z + zResolution / 2.) * (gridSize + 1.) * 2.);
    p = vec3(gv * 2., z);

    vec3 tmp;
    float d = testSdTet(p, .5, tmp, col);
    // float d = sdTriangleGen(p, .5, tmp);

    if (col == vec3(0)) {
      col.b = step(.0, d) * hh(hh(cos(d * TAU * 5.)));
      col.g = step(.0, -d) * hh(hh(cos(d * TAU * 5.)));
      // mark close to zero points with white --
      col += onlyWithE(d, 1. / nGridCells, 0.);
    }

    // grid lines
    col *= product(1. - step(.48, abs(gv)));
    // mark slice near z==0 with green square
    col.g += (only(zId, 0.) + .5 * onlyWithE(p.z, .3, 0.)) * step(.5 - fwg, bestOf(abs(gv)));
    // mark bottom left with red square (indicating pos reset on mouseover)
    col.r += (only(id.x, 0.) * only(id.y, 0.)) * step(.5 - fwg, bestOf(abs(gv)));


    // black out squares outside core grid
    col *= vec3(
      step(.0, id.x) * step(- gridSize + 1., -id.x)
      * step(.0, id.y) * step(- gridSize + 1., -id.y)
    );

    fragColor = vec4(col,1.0);
}

