#define GET_OTHER_VERSION 1

// RNG from https://de.wikipedia.org/wiki/Xorshift
int rngSeed = 314159265;

int xorshift(in int value) {
    value ^= value << 13;
    value ^= value >> 17;
    value ^= value << 5;
    return value;
}

int nextInt() {
    rngSeed = xorshift(rngSeed);
    return rngSeed;
}

// True only when the given position is within the view port.
bool inView(ivec2 pos) {
    return all(greaterThanEqual(pos, ivec2(0))) && all(lessThan(pos, ivec2(iResolution.xy)));
}

// Flatten the 2d position into a 1d index
int flatten(ivec2 position) {
    return position.x + position.y * int(iResolution.x);
}

// Unflatten the 1d index into a 2d position
ivec2 unflatten(int index) {
    return ivec2(index % int(iResolution.x), index / int(iResolution.x));
}

// Get the other pixel to a specific one with set random vector.
// Needs to be self inverse with set random value, i.e: getOther(getOther(p, r), r) = p
ivec2 getOther(ivec2 current, ivec2 random) {
#if GET_OTHER_VERSION == 0
    ivec2 res = ivec2(iResolution.xy);
    random = random % res;
    random |= 1; 
    if (((current.x ^ current.y) & 1) == 0) {
        return (current + random) % res;
    } else {
        return (current - random + res) % res;
    }
#else
    int res = flatten(ivec2(iResolution.xy));
    random |= 1;
    random %= res;
    int index = flatten(current); 
    if ((index & 1) == 0) {
        return unflatten((index + random.x) % res);
    } else {
        return unflatten((index - random.x + res) % res);
    }
#endif
}

// Compute this rounds random offset vector and return the pixel corresponding to the given and random vector.
ivec2 getOther(ivec2 current) {
    ivec2 random = ivec2(nextInt(), nextInt()) & 0x7FFFFFFF;
    ivec2 other = getOther(current, random);
    if (inView(other) && all(equal( getOther(other, random), current )) ) {
        return other;
    }
    return current;
}

// The color difference between two colors. 
float colorDiff(vec3 a, vec3 b) {
    // TODO try better difference, i.e. CIE or ITU, see https://en.wikipedia.org/wiki/Color_difference

    // Currently use squared euclidean distance of RGB values.
    return dot(a-b, a-b);
}

float colorDiff(vec4 a, vec4 b) {
    return colorDiff(a.rgb, b.rgb);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Init the RNG with a *different* semi random value every pass.
    // Thus no need to store the RNG state after each frame.
    rngSeed = floatBitsToInt(sin(float(iFrame)) + 2.);

    // Compute both the pixel and uv coords for the current pixel
    // and the potential swap partner.
    ivec2 pos = ivec2(fragCoord);
    ivec2 posOther = getOther(pos);

    vec2 uv = vec2(pos) / iResolution.xy;
    vec2 uvOther = vec2(posOther) / iResolution.xy;

    vec4 col;
    if (iFrame <= 20) {
        // Init the temporally persistent buffer with the first image (source).
        // This is done for the first 20 frames, so that initalization upon site load may occur.
        col = texture(iChannel0, uv); 
    } else {
        vec4 target = textureLod(iChannel1, uv, 0.);
        vec4 targetOther = textureLod(iChannel1, uvOther, 0.);

        vec4 current = texelFetch(iChannel2, pos, 0);
        vec4 currentOther = texelFetch(iChannel2, posOther, 0);

        // Compute the parts of the heuristic that may change upon a pixel swap. 
        float currentDiff = colorDiff(current, target) + colorDiff(currentOther, targetOther);
        float swappedDiff = colorDiff(current, targetOther) + colorDiff(currentOther, target);

        // Swap if the heuristic impoves.
        if (swappedDiff < currentDiff) {
            col = currentOther;
        } else {
            col = current;
        }
    }

    // Output to buffer
    fragColor = col;
}

