#define time iTime

vec2 scene(vec3 p)
{
	float po=5.+4.0*(sin(time/30.)+1.);
	vec3 z=p;
	float dr=1.0, r=0.;
	//p.xy*=mat2(cos(time),sin(time),-sin(time),cos(time))*p.xz;
	for(int i=0;i<64;i++)
	{
		p.xz*=mat2(cos(time),sin(time),-sin(time),cos(time)) * p.xz;
		r=length(z);
		if(r>1.4)break;
		float t=atan(z.z/r), ph = atan(z.y,z.x);
		dr=pow(r,po-1.)*po*dr+1.;
		float zr=pow(r,po);
		t*=po;
		ph*=po;
		z=zr*vec3(sin(t)*cos(ph),sin(ph)*cos(t),cos(t));
		z+=p;
	}
	return vec2(0.5*log(r)*r/dr,r * 25.);
}

vec4 rc(vec3 ro,vec3 rd) 
{
	float len=0.;
	for(int i=0;i<255;i++)
	{
		vec3 pos=ro+rd*len;
		vec2 dist=scene(pos);
		if(dist.x<0.001) return vec4(pos,dist.y);
		len+=dist.x;
		if(dist.x>100.) break;
	}
	return vec4(100);
}
void mainImage(out vec4 c, in vec2 f)
{
	vec2 r = iResolution.xy, u=((f)*2.- r)/r.y;
	vec3 ro=vec3(0,0,-3);
	vec3 rd=normalize(vec3(u,2.));
	vec4 bulb=rc(ro,rd);
	float lol=1.-bulb.w+10.;
	float len=length(bulb.xyz-ro);
	c=vec4(bulb.w*0.0052);
	c*=1./(len-1.);
}

// Fork of "XOR Towers in Fog & Rain" by GregRostami. https://shadertoy.com/view/7lsXR2
// Thanks to P_Malin's SmallStars code for the rain.
// Place a "//" in front of each line with a "*" in the comments to turn that feature off. 

void mainImage(out vec4 o, vec2 u)
{
     vec2 R = iResolution.xy;
    ivec4 b = ivec4(o -= o);                   // Initialize b=0
    float t = .1*iTime, B, h, z, i=0.;
     vec4 g;

    u =
        (2. + cos(t)) *                        // * Camera push in/out 
        (u+u-R)/R.y                            //   Center coordinates
        * mat2( cos( vec4(0,33,55,0) - .1*t))  // * Rotate camera
        ;

    z = (h = cos(B = ceil(atan(u.x, u.y) * 2e2))) / dot(u,u);  //Variables for Rain

    for (;i<3.;)
    g += pow(texture(iChannel2,.5*u + .1*sin(t/.1+(i+=.04)+3.*u) ),g-g+8.); // Ground ghosts

    for (; (b.x^b.y^b.z)%99 > b.z-8 ; )        // The XOR function for towers!!
        b = ivec4(u * o.a + 2e2
                + vec2(7,30)*t                 // * Move camera (x,y)
                , o+=.1 );                     // Increment layer

    o =
        o.a < 8.1 ? .1+sin(t/.1)*vec4(b%32 & b.x%9 & b.z%9) :  // * Blinking lights
        o/80. - .02 *                                          // * Distance fog
        vec4(b%3)                                              // * Building colors
        + .2*(o.a > 17. ? vec4( int(.5+sin(float(b.x/2 + b.y + 2*b.z))) & b.x & b.y & int(.5+sin(4.*o.a-3.4)) ) : g-g) // * Windows
        + (o.a > 50. ? g*vec4(.15,.4,1,0) : g-g)               // * Ground ghosts
        + .06*texture( iChannel1, .02*u - .03*t ).r            // * Moving fog
        + .01*max(exp(fract(h * B - z + t+t) * -1e2) / z,0.)   // * Rain
        ;
}

precision mediump float;
uniform sampler2D spriteSheet;
uniform vec2 spriteSheetSize;   // In px
uniform vec2 spriteSize;        // In px
uniform float index;            // Sprite index in sprite sheet (0-...)
uniform vec2 vcoord;
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float w = 1024.0;
    float h = 1024.0;
    // Normalize sprite size (0.0-1.0)
    float dx = 64.0 / w;
    float dy = 64.0 / h;
    // Figure out number of tile cols of sprite sheet
    float cols = w / 64.0;
    // From linear index to row/col pair
    float col = mod(50.0, cols);
    float row = floor(50.0 / cols);
    // Finally to UV texture coordinates
    vec2 uv = vec2(dx * fragCoord.x + col * dx, 1.0 - dy - row * dy + dy * fragCoord.y);
    fragColor = texture(spriteSheet, uv);
}

// Fork of "Volumetric laser tracer" by michael0884. https://shadertoy.com/view/NtXSR4
// 2021-07-22 19:27:50

vec3 encodeSRGB(vec3 linearRGB)
{
    vec3 a = 12.92 * linearRGB;
    vec3 b = 1.055 * pow(linearRGB, vec3(1.0 / 2.)) - 0.055;
    vec3 c = step(vec3(0.0031308), linearRGB);
    return mix(a, b, c);
}

vec3 tone(vec3 c)
{
    c = XYZtosRGB(c);
    return tanh(encodeSRGB(c));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 acc = texture(iChannel0, fragCoord/iResolution.xy);
    fragColor = vec4(tone(0.1*acc.xyz/acc.w), 1.0);
}

