precision highp float;
/*

4D VERSION + EXPLANATION: <https://www.shadertoy.com/view/NtBXDW>

Do 2d raymarching over 1 axis with secondary ray march for projections.

The result is shown on the LHS -- a 1d image of the ray marched YZ plane
with the XY plane projected on it.

Note: some bits of shapes are missed (in this case they should all appear
solid), but SURF_DIST is set quite low here.

The yellow bits mean MAX_STEPS is reached without reaching MAX_DIST or
SURF_DIST.

set HIGH_RESOURCES to 1 to see more shapes, but FYI it runs hot.

*/

#define MAX_STEPS 70
#define MAX_DIST 25.
#define SURF_DIST 0.001

#define HIGH_RESOURCES 0


// i used 'sld' for "signed line-dist" b/c I was trying to do the projection
// via the distance between a point an a line (our ray direction is a normal
// this line), but couldn't get it to work. I don't think it can work, but if
// it could then it might speed things up a lot.

vec4 sldBox(in vec3 p, in vec3 b) {
  vec3 d = abs(p) - b;  // point on the line to corner
  float l = length(max(d, 0.0)) + min(max(d.z, max(d.x, d.y)), 0.0);
  return vec4(l, d);
}

vec4 sldBoxEdges(vec3 p, in vec3 s) {
  vec3 d = abs(p) - s;
  float l = length(best2(d));
  return vec4(l, d);
}

vec4 minWith(vec4 a, vec4 b) {
  return (min(0., (a.x - b.x)) * a + min(0., (b.x - a.x)) * b) * -1. / abs(b.x - a.x);
}

float mapWDists(vec3 p, out vec3 dists) {
  mat3 r1 = rot3XY(u_time, 3), r2 = rot3XY(u_time * .48597, 1);
  // r1 = rot3XY(u_time * .0, 3), r2 = rot3XY(u_time * .763 * .0, 2);
  vec4 boxEdges = sldBoxEdges((p - vec3(0.4, 1.0 , 0)) * r2 * r2 * r1, vec3(.3)) - .01;
  vec4 box = sldBox(p * r2 * r1, vec3(.3)) - .01;
  vec4 tri = sdTriangle2((p - vec3(-.4, -.9, 0)) * r2 * r1, .3) - .01;
  vec4 tri2 = sdTriangle1((p - vec3(.4, -1.4, 0)) * r2 * r1, .3) - .01;
  vec4 tet;
  tet.x = sdTetrahedron((p - vec3(.4, 1.9, 0)) * r2 * r1, .3, tet.yzw) - .01;

  vec4 tri3;
  tri3.x = sdTriangleGen((p - vec3(.5, 1.9, 0)) * r2 * r1, .3, tri3.yzw) - .01;

  vec4 m = minWith(boxEdges, vec4(999.));
  m = minWith(m, tri2);

  if (HIGH_RESOURCES == 1) {
    m = minWith(m, box);
    m = minWith(m, tri);
  }

  dists = m.yzw;
  return m.x;
}

float map(vec3 p) {
  vec3 temp;
  return mapWDists(p, temp);
}

vec4 rayMarch(inout vec3 p, in vec3 rd, out vec3 dists) {
  float dS = 99., d = 0., minDS = dS, steps = 0.;
  for (int i = 0; i < MAX_STEPS; i++) {
    steps += 1.;
    dS = mapWDists(p, dists);
    minDS = min(minDS, abs(dS));
    d += dS;
    p = p + rd * dS;
    if ((0. <= dS && dS < SURF_DIST) || d > MAX_DIST) break;
  }
  return vec4(d, dS, minDS, steps);
}

vec2 e = vec2(.0001, 0);

vec4 rayMarch1d(inout vec3 p, in vec3 rd, in vec3 rd2, out vec3 dists) {
  float dS = 99., dSx = 99., dSy, dSz, d = 0., minDS = dS, steps = 0.;
  vec3 dists2;
  for (int i = 0; i < MAX_STEPS; i++) {
    steps += 1.;
    dS = 99.;
    dS = mapWDists(p, dists);
    minDS = min(minDS, abs(dS));
    if (dS > SURF_DIST) {
      vec3 p2 = p - rd2 * 2.;
      vec4 innerRmd = rayMarch(p2, rd2, dists2);
      // steps += innerRmd.w;
      if (innerRmd.x < MAX_DIST && innerRmd.y < SURF_DIST) {
        p = p2;
        return vec4(length(vec2(innerRmd.x, d)), innerRmd.yz, steps);
      } else {
        dS = min(dS, min(innerRmd.y, 99.));
        minDS = min(minDS, abs(innerRmd.z));
      }
    }
    // backported chBasis + slow_thru from 4d version
    mat3 chBasis = mat3(rd.x, rd.y, rd.z, 0, 0, 0, rd2.x, rd2.y, rd2.z);
    vec3 components = normalize(dists * chBasis);
    d += dS;
    float dsFactor = maxOf(vec4(components.xz, -components.xz));
    dsFactor *= min(1., max(minOf(abs(p)) + .08, .01));
    p = p + rd * dS * dsFactor;
    if ((0. <= dS && dS < SURF_DIST) || d > MAX_DIST) break;
  }
  return vec4(d, dS, minDS, steps);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  // split screen first
  vec2 uv = gl_FragCoord.xy / u_resolution.xy;
  float aspect = u_resolution.x / u_resolution.y;
  float id = sign(uv.x - .5);
  uv.x = (fract(uv.x * 2.) - .5) * aspect;
  uv.y -= .5;
  vec2 mouse = (u_mouse.xy - 0.5 * u_resolution.xy) / u_resolution.y;
  float t = u_time *3.;
  vec3 col = BLACK;

  float zoom = 3.0;
  uv *= zoom * vec2(1.0, 2.0);
  float maxU = zoom / 2. * aspect;
  vec2 fw = fwidth(uv);

  // starting with this at -2 (like you might with the z axis) results in issues rendering b/c the ray doesn't pass close enough to the objects to get the sideways projection right.
  float xStart = hh(id) * uv.x;  // 0 for left, 1 for right
  float yStart = uv.y + .5;
  vec3 ro = vec3(xStart, yStart, -2);
  vec3 rd = vec3(0, 0, 1);
  vec3 xAxis = vec3(1, 0, 0);
  mat3 r = rot3XY(t / 3., 1);
  ro *= r;
  rd *= r;
  xAxis *= r;

  vec3 p = ro, dists;
  vec4 rmd;
  if (id < 0.) {
    rmd = rayMarch1d(p, rd, xAxis, dists);
  } else {
    rmd = rayMarch(p, rd, dists);
  }

  float d = rmd.x, dS = rmd.y, minDS = rmd.z, steps = rmd.w;

  if (d > MAX_DIST) {
    col = vec3(.95, .1, .7);
  } else if (steps >= float(MAX_STEPS)) {
    col = vec3(.8, .9, .1);
  } else {
    col.r += max(1. / (1. + d), 0.);
    col.b = clamp(p.x, 0., 1.);
  }

  //col.g += max(1. / (1. + minDS), 0.);
  //col.g += minDS / 2.;
  //col.b += 1. / (1. + minDS);
  //col += S(SURF_DIST * 2., SURF_DIST, minDS);

  /* lines marking origin */
  col.b += S(fw.x * 2., .0, abs(uv.x));
  col.b += S(fw.y * 2., .0, abs(uv.y));
  float around = .25;

  col *= S((-maxU + fw.x * 3.) * id, (-maxU + fw.x * 5.) * id, uv.x);

  fragColor = vec4(col, 1.0);
}

