    p.z = min(p.z,sz*21.);
    p.xz = mod(p.xz+rep*.5, rep)-rep*.5;


    float lamp = max(max(length(p)-sz, p.y), -(length(p)-sz*.99));
    acc = _min(acc, vec2(lamp, 0.));

    float offlamp = 0.04;
    p.y += offlamp;

    float lampcord = max(max(length(p.xz)-0.001, p.y), -(p.y+sz-offlamp));
    acc = _min(acc, vec2(lampcord, 1.));

    float lampbulb = length(p)-.025;
    acc = _min(acc, vec2(lampbulb, (p.y < .005 ? 2. : 3.)));

    float wallrep = .25;
    float wallsz = wallrep*.25;
    op.x = mod(op.x+wallrep*.5, wallrep)-wallrep*.5;
    acc = _min(acc, vec2(_cube(op-vec3(0.,0.,5.), vec3(wallsz,30.,wallsz)),5.));
    acc = _min(acc, vec2(-op2.z+5.5, 6.));

    op3.yz *= r2d(-.25);
    float tv = sdRoundBox(op3-vec3(0.,.5,0.), vec3(.5,.4,.25), .1);
    tv = max(tv, - sdRoundBox(op3-vec3(0.,.5,-0.65), vec3(.4,.3,.25), .1));
    acc = _min(acc, vec2(tv, 7.));

    return acc;
}
vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.01,0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}
vec3 trace(vec3 ro ,vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.01)
            return vec3(res.x, distance(p, ro), res.y);
        p += rd * res.x*mix(.25,1.,pow(sat(distance(p,ro)/15.),15.)); 
    }
    return vec3(-1.);
}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0.);

    vec3 ro = vec3(0.+sin(iTime*.25),1.,.5);
    vec3 ta = vec3(0.,-0.,5.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(uv, rd);
    vec3 res = trace(ro, rd, 256);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = n *.5+.5;
        float refl = 1.;
        if (res.z == 2.)
            col = vec3(1.000,0.710,0.302)*2.;
        else if (res.z == 3.)
            col = vec3(0.302,0.157,0.122)*.5;
        else if (p.y < -.001)
        {
             refl = .1;
             col = mix(vec3(0.302,0.157,0.122)*.5*sat(-dot(rd, n)), vec3(1.000,0.710,0.302),sat(dot(vec3(0.,1.,0.), n)));
        }
        else if (res.z == 5.)
        {
            vec3 h = normalize(rd+vec3(-.5, 1.,-1.));
            col = sat(p.y)*vec3(0.302,0.157,0.122)*.35+vec3(1.000,0.710,0.302)*(1.-pow(sat(dot(n, h)), .1))*.25;
        }
        else if (res.z == 7.)
        {
            col = pow(sat(-p.y+1.),4.)*vec3(0.302,0.255,0.157)*.75+vec3(0.718,0.463,0.220)*(1.-pow(sat(dot(n, rd)), .5))*.25;
            refl = .25;

            if (p.y > .24 && p.y < .9 && p.x > -.4 && p.x < .4)
            {
                vec2 tuv = p.xy*vec2(1.,-2.)*.8-vec2(-0.5,-1.5);
                float stppx = 0.005;
                tuv = vec2(ivec2(tuv/stppx))*stppx;
                float offchroma = 0.005;
                col.r = texture(iChannel1, tuv).x;
                col.g = texture(iChannel1, tuv+vec2(offchroma,0.)).y;
                col.b = texture(iChannel1, tuv+vec2(offchroma,0.)*2.).z;
                refl = .1;
                float flicker = sat(sin(iTime*5.)*sin(iTime*3.)*10.)*sat(sin(iTime)*10.);
                vec3 dark = mix(vec3(.1), vec3(1.000,0.494,0.161)*2.5, (1.-pow(sat(length(col)),.05)));
                col = mix(col, dark, flicker);
            }

        }
        else
         col = vec3(0.302,0.157,0.122)*.25;
        col += col*texture(iChannel0, reflect(rd, n)).xyz*refl;
    }

    return col;
}
vec3 cheapAA(vec2 uv)
{
    vec3 col = rdr(uv);
    vec2 off = vec2(1., -1.)/(iResolution.x*2.);
    // Not so cheap antialiasing
    {
        //col = vec3(1.,0.,0.);
        vec3 acc = col;
        acc += rdr(uv+off.xx);
        acc += rdr(uv+off.xy);
        acc += rdr(uv+off.yy);
        acc += rdr(uv+off.yx);
        col = acc/5.;

    }
    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;

    vec3 col = cheapAA(uv);

    fragColor = vec4(col,1.0);
}

