#define sat(a) clamp(a, 0.,1.)
#define PI 3.14159265
#define TAU (2.*PI)
mat2 r2d(float a) { float c= cos(a), s = sin(a); return mat2(c, -s, s, c); }
// Stolen on stackoverflow somewhere
float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }
vec3 getCam(vec2 uv, vec3 rd)
{
    vec3 r = normalize(cross(rd, vec3(0.,1.,0.)));
    vec3 u = normalize(cross(rd, r));
    return normalize(rd+r*uv.x+u*uv.y);
}

vec2 _min(vec2 a, vec2 b)
{
    if (a.x < b.x)
        return a;
    return b;
}
float _cube(vec3 p, vec3 s)
{
    vec3 l = abs(p)-s;
    return max(max(l.x,l.y), l.z);
}

// Thanks IQ :)
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}
float line(vec3 p, vec2 s, float curve)
{
    p.yz *= r2d(p.y*curve);
    vec3 p2 = p;
    p2.y = abs(p2.y)-s.y;
  return min(max(length(p.xz)-s.x, abs(p.y)-s.y), length(p2)-s.x);
//  return min(length(rep.xz)-s,length(rep-vec2(0.,s.y))-s.x);
}

// Thanks IQ
float smin( float a, float b, float k )
{
    a = pow( a, k ); b = pow( b, k );
    return pow( (a*b)/(a+b), 1.0/k );
}
float _sqr(vec2 p, vec2 s)
{
  vec2 l = abs(p)-s;
  return max(l.x,l.y);
}


float line(vec2 p, vec2 s)
{
  vec2 rep = p;
  rep.y= abs(p.y);
  return min(_sqr(p, s),length(rep-vec2(0.,s.y))-s.x);
}

